INSERT INTO questions (batch_id, id, curriculum_area, lecturer, topic, clusters, cognitive_level, question_type, distractor_strategy, difficulty, source_file, source_file_page_number, topics, question_prompt, choices, explanation, related_questions) VALUES (0, 'GUIDAZZOLI_introduzione_very_easy_002', 'MOD. 3 - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft', 'GUIDAZZOLI', 'Ottica HOME vs BUSINESS', '["Infrastruttura client", "IT aziendale"]', 'remember', 'concept', 'obviously_wrong', 'very_easy', 'materiale/GUIDAZZOLI - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft/1 -Introduzione.ppt', 5, '["ottica HOME", "ottica BUSINESS", "infrastruttura client", "IT aziendale"]', 'Secondo il corso, cosa deve fare un tecnico IT per lavorare in ambito professionale?', '[{"text": "Installare esclusivamente sistemi operativi Linux su tutti i computer", "is_correct": false}, {"text": "Imparare a programmare in linguaggio Assembly per i microprocessori", "is_correct": false}, {"text": "Passare dall''ottica HOME all''ottica BUSINESS nella gestione dei sistemi", "is_correct": true}]', '{"right_answer": {"selected_answer": "Passare dall''ottica HOME all''ottica BUSINESS nella gestione dei sistemi", "rationale": "Il materiale sottolinea che bisogna passare dall''ottica HOME all''ottica BUSINESS: un tecnico IT aziendale deve gestire i sistemi con criteri professionali diversi da quelli domestici.", "original_reference": {"path": "materiale/GUIDAZZOLI - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft/1 -Introduzione.ppt", "page": 5}}, "wrong_answers": [{"answer_text": "Imparare a programmare in linguaggio Assembly per i microprocessori", "interpretation": "Confonde il ruolo del tecnico di sistema con quello dello sviluppatore a basso livello", "why_wrong": "Il corso non tratta programmazione Assembly. Il focus e'' sulla gestione dell''infrastruttura client in ambiente aziendale."}, {"answer_text": "Installare esclusivamente sistemi operativi Linux su tutti i computer", "interpretation": "Confonde il modulo Microsoft con un modulo su sistemi open-source", "why_wrong": "Il modulo riguarda tecnologie Microsoft, non Linux. Il passaggio richiesto e'' dall''ottica HOME a quella BUSINESS."}]}', '["GUIDAZZOLI_introduzione_very_easy_001"]');
INSERT INTO questions (batch_id, id, curriculum_area, lecturer, topic, clusters, cognitive_level, question_type, distractor_strategy, difficulty, source_file, source_file_page_number, topics, question_prompt, choices, explanation, related_questions) VALUES (0, 'ANZALONE_2b_ft_bc_very_easy_003', 'MOD. 5 - Cloud privato e cloud pubblico al servizio della business continuity e disaster recovery', 'ANZALONE', 'vSphere Fault Tolerance - Tecnologia vLockstep', '["Infrastruttura VMware vSphere", "Virtualizzazione e Hypervisor"]', 'remember', 'definition', 'obviously_wrong', 'very_easy', 'materiale/ANZALONE - Cloud privato e cloud pubblico al servizio della business continuity e disaster recovery/2b_ft_bc.pdf', 36, '["vSphere FT", "vLockstep", "primary VM", "secondary VM"]', 'In vSphere Fault Tolerance, come si chiama la tecnologia che mantiene sincronizzate primary e secondary VM?', '[{"text": "SMTP Relay", "is_correct": false}, {"text": "DNS Round Robin", "is_correct": false}, {"text": "Bluetooth Pairing", "is_correct": false}, {"text": "vLockstep Technology", "is_correct": true}]', '{"right_answer": {"selected_answer": "vLockstep Technology", "rationale": "VMware FT utilizza la tecnologia vLockstep per mantenere la primary VM e la secondary VM in perfetta sincronizzazione, processando gli stessi dati, modificando le stesse locazioni di memoria e scrivendo gli stessi dati su disco.", "original_reference": {"path": "materiale/ANZALONE - Cloud privato e cloud pubblico al servizio della business continuity e disaster recovery/2b_ft_bc.pdf", "page": 36}}, "wrong_answers": [{"answer_text": "Bluetooth Pairing", "interpretation": "Tecnologia wireless per dispositivi personali", "why_wrong": "Il Bluetooth Pairing è una tecnologia wireless per il collegamento di dispositivi personali, completamente estranea alla virtualizzazione."}, {"answer_text": "DNS Round Robin", "interpretation": "Meccanismo di distribuzione DNS", "why_wrong": "Il DNS Round Robin è un metodo di distribuzione del carico a livello DNS, non ha alcuna relazione con la sincronizzazione delle VM in Fault Tolerance."}, {"answer_text": "SMTP Relay", "interpretation": "Protocollo di posta elettronica", "why_wrong": "SMTP Relay è un concetto legato all''invio di posta elettronica, completamente estraneo alla Fault Tolerance."}]}', '["ANZALONE_2b_ft_bc_hard_001"]');
INSERT INTO questions (batch_id, id, curriculum_area, lecturer, topic, clusters, cognitive_level, question_type, distractor_strategy, difficulty, source_file, source_file_page_number, topics, question_prompt, choices, explanation, related_questions) VALUES (0, 'BERNABEI_06_GroupPolicy_easy_001', 'MOD. 3 - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft', 'BERNABEI', 'Scope delle GPO', '["Group Policy", "Scope"]', 'understand', 'concept', 'partial_knowledge', 'easy', 'materiale/BERNABEI - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft/Slide/06.Implementare le Group Policy.pptx', 8, '["GPO link", "site", "dominio", "OU", "SDOU"]', 'A quali container di Active Directory e possibile linkare una GPO?', '[{"text": "Solo alle Organizational Unit", "is_correct": false}, {"text": "A qualsiasi oggetto di Active Directory, inclusi utenti e gruppi", "is_correct": false}, {"text": "Site, Dominio, Organizational Unit (SDOU)", "is_correct": true}]', '{"right_answer": {"selected_answer": "Site, Dominio, Organizational Unit (SDOU)", "rationale": "La slide 8 indica che una GPO puo essere collegata ad un sito, a un dominio, o a una OU (SDOU), definendo cosi lo scope massimo della GPO.", "original_reference": {"path": "materiale/BERNABEI - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft/Slide/06.Implementare le Group Policy.pptx", "page": 8}}, "wrong_answers": [{"answer_text": "Solo alle Organizational Unit", "interpretation": "Scope limitato alle OU", "why_wrong": "Le GPO possono essere linkate non solo alle OU, ma anche ai siti e ai domini. L''acronimo SDOU indica tutti e tre i livelli possibili."}, {"answer_text": "A qualsiasi oggetto di Active Directory, inclusi utenti e gruppi", "interpretation": "Link GPO a singoli oggetti", "why_wrong": "Le GPO non possono essere linkate direttamente a singoli utenti o gruppi. Si linkano solo a siti, domini e OU."}]}', '["BERNABEI_06_GroupPolicy_very_easy_001", 