INSERT INTO questions (batch_id, id, curriculum_area, lecturer, topic, clusters, cognitive_level, question_type, distractor_strategy, difficulty, source_file, source_file_page_number, topics, question_prompt, choices, explanation, related_questions) VALUES (4, 'BONONI_NAT_medium_003', 'MOD. 2 - Sicurezza delle reti e dei sistemi', 'BONONI', 'Controversie sul NAT', '["NAT", "controversie", "end-to-end argument"]', 'understand', 'conceptual', 'conceptual_inversion', 'medium', 'materiale/BONONI - Sicurezza delle reti e dei sistemi/NAT-IPv4.pdf', 5, '["NAT", "end-to-end argument", "layer 3", "IPv6", "controversie"]', 'Quale critica al NAT riguarda il principio architetturale end-to-end delle reti?', '[{"text": "Il NAT rafforza l''end-to-end argument eliminando la necessita'' di protocolli intermedi", "is_correct": false}, {"text": "Il NAT e'' conforme all''end-to-end argument perche'' opera solo a layer 2", "is_correct": false}, {"text": "Il NAT viola l''end-to-end argument perche'' modifica i pacchetti in transito nel percorso di rete", "is_correct": true}, {"text": "Il NAT viola l''end-to-end argument perche'' impedisce l''uso di protocolli cifrati come TLS", "is_correct": false}]', '{"right_answer": {"selected_answer": "Il NAT viola l''end-to-end argument perche'' modifica i pacchetti in transito nel percorso di rete", "rationale": "Il materiale elenca tra le controversie del NAT: ''violates end-to-end argument''. L''end-to-end argument sostiene che le funzionalita'' applicative devono risiedere agli estremi della comunicazione, non nei nodi intermedi. Il NAT, modificando indirizzi e porte nei router intermedi, viola questo principio.", "original_reference": {"path": "materiale/BONONI - Sicurezza delle reti e dei sistemi/NAT-IPv4.pdf", "page": 5}}, "wrong_answers": [{"answer_text": "Il NAT viola l''end-to-end argument perche'' impedisce l''uso di protocolli cifrati come TLS", "interpretation": "Associa erroneamente la violazione end-to-end con la cifratura", "why_wrong": "Il NAT non impedisce TLS. La violazione riguarda la modifica degli header IP/porta in transito, non l''impossibilita'' di cifrare il payload."}, {"answer_text": "Il NAT rafforza l''end-to-end argument eliminando la necessita'' di protocolli intermedi", "interpretation": "Inverte la relazione tra NAT e principio end-to-end", "why_wrong": "Il NAT non rafforza il principio end-to-end, lo viola. Il NAT aggiunge elaborazione in un nodo intermedio (il router), che e'' esattamente cio'' che l''end-to-end argument sconsiglia."}, {"answer_text": "Il NAT e'' conforme all''end-to-end argument perche'' opera solo a layer 2", "interpretation": "Sbaglia il livello a cui opera il NAT", "why_wrong": "Il NAT opera a livello 3 (IP) e 4 (porte TCP/UDP), non a layer 2. Inoltre, una delle critiche e'' proprio che i router dovrebbero operare solo fino al layer 3, ma il NAT tocca anche il layer 4."}]}', '[]');
INSERT INTO questions (batch_id, id, curriculum_area, lecturer, topic, clusters, cognitive_level, question_type, distractor_strategy, difficulty, source_file, source_file_page_number, topics, question_prompt, choices, explanation, related_questions) VALUES (4, 'FORONI_2_Fondamenti_easy_004', 'MOD. 4 - Implementazione e verifica della sicurezza delle reti e dei sistemi', 'FORONI', 'Sicurezza fisica - componenti', '["sicurezza fisica", "edificio", "anti-incendio", "controllo accesso", "BC", "DR"]', 'remember', 'factual', 'category_confusion', 'easy', 'materiale/FORONI/Lezioni - Implementazione e verifica della sicurezza delle reti e dei sistemi/2.Fondamenti Cybersecurity .pdf', 11, '["sicurezza fisica", "edificio", "anti-incendio", "anti-allagamento", "controllo accesso", "business continuity", "disaster recovery"]', 'Quali sono i componenti della sicurezza fisica elencati nel materiale?', '[{"text": "Antivirus, antimalware, antispam e sandbox", "is_correct": false}, {"text": "Firewall, IDS, IPS e proxy server", "is_correct": false}, {"text": "Sicurezza dell''edificio, sistemi anti-incendio e anti-allagamento, controllo accesso delle persone, sistemi di Business Continuity e Disaster Recovery", "is_correct": true}]', '{"right_answer": {"selected_answer": "Sicurezza dell''edificio, sistemi anti-incendio e anti-allagamento, controllo accesso delle persone, sistemi di Business Continuity e Disaster Recovery", "rationale": "Il materiale elenca come componenti della sicurezza fisica: sicurezza dell''edificio, sistemi anti-incendio e anti-allagamento, controllo accesso delle persone all''edificio, e sistemi di Business Continuity e Disaster Recovery.", "original_reference": {"path": "materiale/FORONI/Lezioni - Implementazione e verifica della sicurezza delle reti e dei sistemi/2.Fondamenti Cybersecurity .pdf", "page": 11}}, "wrong_answers": [{"answer_text": "Firewall, IDS, IPS e proxy server", "interpretation": "Confonde la sicurezza fisica con la sicurezza perimetrale di rete", "why_wrong": "Firewall, IDS, IPS e proxy sono strumenti di sicurezza perimetrale (logica), non di sicurezza fisica."}, {"answer_text": "Antivirus, antimalware, antispam e sandbox", "interpretation": "Confonde la sicurezza fisica con la sicurezza degli endpoint", "why_wrong": "Antivirus, antimalware, antispam e sandbox sono strumenti di sicurezza degli endpoint, non componenti della sicurezza fisica."}]}', '["FORONI_2_Fondamenti_easy_005"]');
INSERT INTO questions (batch_id, id, curriculum_area, lecturer, topic, clusters, cognitive_level, question_type, distractor_strategy, difficulty, source_file, source_file_page_number, topics, question_prompt, choices, explanation, related_questions) VALUES (4, 'BONONI_intro_reti2025B_easy_001', 'MOD. 2 - Sicurezza delle reti e dei sistemi', 'BONONI', 'Protocollo IP - servizio connectionless', '["Protocollo IP"]', 'understand', 'conceptual', 'common_confusion', 'easy', 'materiale/BONONI - Sicurezza delle reti e dei sistemi/LBononi_RdC_intro_reti2025Bnew.pdf', 4, '["IP", "connectionless", "livello rete"]', 'Il protocollo IP fornisce un servizio di tipo connectionless. Cosa significa?', '[{"text": "I pacchetti vengono spediti senza stabilire una connessione preventiva e possono arrivare in ordine diverso o andare perduti", "is_correct": true}, {"text": "I pacchetti sono sempre consegnati nell''ordine corretto con garanzia di ricezione", "is_correct": false}, {"text": "Il protocollo IP non utilizza indirizzi per identificare mittente e destinatario", "is_correct": false}, {"text": "I pacchetti possono essere spediti solo all''interno della stessa rete locale", "is_correct": false}]', '{"right_answer": {"selected_answer": "I pacchetti vengono spediti senza stabilire una connessione preventiva e possono arrivare in ordine diverso o andare perduti", "rationale": "Il servizio connectionless del protocollo IP significa che i pacchetti vengono instradati individualmente dal mittente al destinatario senza una connessione punto a punto preventiva. I pacchetti possono seguire strade diverse, arrivare disordinati o andare perduti.", "original_reference": {"path": "materiale/BONONI - Sicurezza delle reti e dei sistemi/LBononi_RdC_intro_reti2025Bnew.pdf", "page": 4}}, "wrong_answers": [{"answer_text": "I pacchetti sono sempre consegnati nell''ordine corretto con garanzia di ricezione", "interpretation": "Descrive un servizio connection-oriented affidabile come TCP", "why_wrong": "Questo descrive il servizio fornito da TCP al livello trasporto, non il servizio connectionless di IP a livello rete."}, {"answer_text": "Il protocollo IP non utilizza indirizzi per identificare mittente e destinatario", "interpretation": "Nega l''indirizzamento IP", "why_wrong": "Il protocollo IP definisce un sistema di indirizzamento globale e gerarchico. Connectionless non significa ''senza indirizzi''."}, {"answer_text": "I pacchetti possono essere spediti solo all''interno della stessa rete locale", "interpretation": "Confonde IP con protocolli di livello locale", "why_wrong": "Il protocollo IP e'' progettato proprio per la comunicazione tra reti diverse (Internet), non solo locali."}]}', '[]');
INSERT INTO questions (batch_id, id, curriculum_area, lecturer, topic, clusters, cognitive_level, question_type, distractor_strategy, difficulty, source_file, source_file_page_number, topics, question_prompt, choices, explanation, related_questions) VALUES (4, 'GUIDAZZOLI_introduzione_very_easy_001', 'MOD. 3 - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft', 'GUIDAZZOLI', 'Tipologie di licenza Windows', '["Licenze Microsoft", "Infrastruttura client"]', 'remember', 'definition', 'obviously_wrong', 'very_easy', 'materiale/GUIDAZZOLI - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft/1 -Introduzione.ppt', 8, '["licenza OEM", "licenza Retail", "licenza VLP", "licensing Windows"]', 'Cosa significa la sigla OEM nel contesto delle licenze Windows?', '[{"text": "Original Equipment Manufacturer: licenza preinstallata dal produttore hardware", "is_correct": true}, {"text": "Optical External Memory: tipo di memoria esterna per il backup dei dati", "is_correct": false}, {"text": "Open Encryption Module: modulo di crittografia integrato nel sistema operativo", "is_correct": false}, {"text": "Online Enterprise Management: licenza per la gestione remota dei server", "is_correct": false}]', '{"right_answer": {"selected_answer": "Original Equipment Manufacturer: licenza preinstallata dal produttore hardware", "rationale": "OEM sta per Original Equipment Manufacturer. E'' una tipologia di licenza Windows che viene preinstallata dal produttore del computer e venduta insieme all''hardware.", "original_reference": {"path": "materiale/GUIDAZZOLI - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft/1 -Introduzione.ppt", "page": 8}}, "wrong_answers": [{"answer_text": "Online Enterprise Management: licenza per la gestione remota dei server", "interpretation": "Confonde OEM con un acronimo inventato legato alla gestione aziendale", "why_wrong": "OEM non ha nulla a che fare con la gestione remota dei server. Si tratta di una licenza legata all''hardware del produttore."}, {"answer_text": "Open Encryption Module: modulo di crittografia integrato nel sistema operativo", "interpretation": "Confonde OEM con un concetto di sicurezza informatica", "why_wrong": "OEM non e'' un modulo di crittografia. E'' una tipologia di licenza che lega il software all''hardware su cui viene venduto."}, {"answer_text": "Optical External Memory: tipo di memoria esterna per il backup dei dati", "interpretation": "Confonde OEM con un componente hardware di storage", "why_wrong": "OEM non indica un dispositivo di memoria. E'' l''acronimo di Original Equipment Manufacturer, riferito alla licenza software."}]}', '["GUIDAZZOLI_introduzione_very_easy_002", "GUIDAZZOLI_introduzione_easy_001"]');
INSERT INTO questions (batch_id, id, curriculum_area, lecturer, topic, clusters, cognitive_level, question_type, distractor_strategy, difficulty, source_file, source_file_page_number, topics, question_prompt, choices, explanation, related_questions) VALUES (4, 'GUIDAZZOLI_file_access_easy_003', 'MOD. 3 - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft', 'GUIDAZZOLI', 'Protocolli di autenticazione Windows', '["Autenticazione", "Kerberos", "NTLM"]', 'remember', 'definition', 'obviously_wrong', 'easy', 'materiale/GUIDAZZOLI - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft/File Access and Printers.ppt', 5, '["Kerberos", "NTLM", "certificate mapping", "protocolli autenticazione"]', 'Quale protocollo di autenticazione e'' usato da Windows 7 con server Windows 2000 o successivi?', '[{"text": "HTTP (HyperText Transfer Protocol)", "is_correct": false}, {"text": "FTP (File Transfer Protocol)", "is_correct": false}, {"text": "Kerberos versione 5", "is_correct": true}, {"text": "SMTP (Simple Mail Transfer Protocol)", "is_correct": false}]', '{"right_answer": {"selected_answer": "Kerberos versione 5", "rationale": "Il materiale indica: ''Kerberos version 5 protocol - Used by Windows 7 clients and servers running Microsoft Windows Server 2000 or later''.", "original_reference": {"path": "materiale/GUIDAZZOLI - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft/File Access and Printers.ppt", "page": 5}}, "wrong_answers": [{"answer_text": "FTP (File Transfer Protocol)", "interpretation": "Confonde un protocollo di autenticazione con uno di trasferimento file", "why_wrong": "FTP e'' un protocollo per il trasferimento di file, non per l''autenticazione di dominio."}, {"answer_text": "SMTP (Simple Mail Transfer Protocol)", "interpretation": "Confonde un protocollo di autenticazione con uno di posta elettronica", "why_wrong": "SMTP e'' il protocollo per l''invio di email, non per l''autenticazione Windows."}, {"answer_text": "HTTP (HyperText Transfer Protocol)", "interpretation": "Confonde un protocollo di autenticazione con uno per il web", "why_wrong": "HTTP e'' il protocollo per la navigazione web, non per l''autenticazione di dominio Windows."}]}', '["GUIDAZZOLI_file_access_easy_001", "GUIDAZZOLI_file_access_easy_004"]');
INSERT INTO questions (batch_id, id, curriculum_area, lecturer, topic, clusters, cognitive_level, question_type, distractor_strategy, difficulty, source_file, source_file_page_number, topics, question_prompt, choices, explanation, related_questions) VALUES (4, 'ANZALONE_4a_storage_medium_003', 'MOD. 5 - Cloud privato e cloud pubblico al servizio della business continuity e disaster recovery', 'ANZALONE', 'Storage DRS - funzionalita''', '["Storage", "VMware vSphere", "Automazione"]', 'understand', 'concept', 'related_domain', 'medium', 'materiale/ANZALONE - Cloud privato e cloud pubblico al servizio della business continuity e disaster recovery/4a_storage.pdf', 75, '["Storage DRS", "SDRS", "datastore cluster", "load balancing", "initial placement"]', 'Quali sono le tre funzioni principali abilitate da Storage DRS quando attivato su un datastore cluster?', '[{"text": "Monitoraggio della temperatura, gestione dell''alimentazione e controllo delle ventole", "is_correct": false}, {"text": "Backup automatico, replica remota e snapshot schedulati delle VM", "is_correct": false}, {"text": "Crittografia dei dati, compressione dei file e deduplicazione dei blocchi", "is_correct": false}, {"text": "Bilanciamento dello spazio, bilanciamento dell''I/O e posizionamento iniziale dei dischi virtuali", "is_correct": true}]', '{"right_answer": {"selected_answer": "Bilanciamento dello spazio, bilanciamento dell''I/O e posizionamento iniziale dei dischi virtuali", "rationale": "Storage DRS abilita tre funzioni principali: space load balancing (bilanciamento dello spazio tra datastore), I/O load balancing (bilanciamento del carico I/O) e initial placement (posizionamento automatico dei dischi virtuali basato su spazio e carico I/O).", "original_reference": {"path": "materiale/ANZALONE - Cloud privato e cloud pubblico al servizio della business continuity e disaster recovery/4a_storage.pdf", "page": 76}}, "wrong_answers": [{"answer_text": "Crittografia dei dati, compressione dei file e deduplicazione dei blocchi", "interpretation": "Funzionalita'' di data services, non di bilanciamento", "why_wrong": "Crittografia, compressione e deduplicazione sono funzionalita'' di data services dello storage array, non di Storage DRS che si occupa di bilanciamento e posizionamento."}, {"answer_text": "Backup automatico, replica remota e snapshot schedulati delle VM", "interpretation": "Funzionalita'' di protezione dati, non di bilanciamento", "why_wrong": "Backup, replica e snapshot sono funzionalita'' di protezione dati gestite da altri strumenti (es. vSphere Replication, Veeam). Storage DRS si occupa di bilanciamento delle risorse storage."}, {"answer_text": "Monitoraggio della temperatura, gestione dell''alimentazione e controllo delle ventole", "interpretation": "Funzionalita'' hardware di gestione fisica del data center", "why_wrong": "Queste sono funzionalita'' di gestione hardware fisica (IPMI/iLO/iDRAC), non hanno nulla a che fare con Storage DRS che opera a livello di virtualizzazione storage."}]}', '["ANZALONE_4a_storage_medium_001"]');
INSERT INTO questions (batch_id, id, curriculum_area, lecturer, topic, clusters, cognitive_level, question_type, distractor_strategy, difficulty, source_file, source_file_page_number, topics, question_prompt, choices, explanation, related_questions) VALUES (4, 'ANZALONE_2a_introVmware_easy_006', 'MOD. 5 - Cloud privato e cloud pubblico al servizio della business continuity e disaster recovery', 'ANZALONE', 'Balloon driver - memory management', '["Infrastruttura VMware vSphere"]', 'remember', 'definition', 'obviously_wrong', 'easy', 'materiale/ANZALONE - Cloud privato e cloud pubblico al servizio della business continuity e disaster recovery/2a_introVmware.pdf', 74, '["balloon driver", "VMware Tools", "memory reclaim"]', 'Di quale componente software fa parte il balloon driver e quale funzione svolge in VMware?', '[{"text": "È un componente del BIOS che gestisce il raffreddamento del server", "is_correct": false}, {"text": "È un driver per schede grafiche utilizzate dal rendering 3D delle VM", "is_correct": false}, {"text": "Fa parte di vCenter Server e distribuisce storage tra le VM", "is_correct": false}, {"text": "Fa parte dei VMware Tools e recupera memoria dal guest OS per restituirla all''hypervisor", "is_correct": true}]', '{"right_answer": {"selected_answer": "Fa parte dei VMware Tools e recupera memoria dal guest OS per restituirla all''hypervisor", "rationale": "Il balloon driver è parte dei VMware Tools ed è un driver specifico per il sistema operativo guest. Quando l''host ESXi è in pressione di memoria, l''hypervisor segnala al balloon driver di gonfiarsi (inflate), richiedendo memoria al guest OS e restituendola all''hypervisor.", "original_reference": {"path": "materiale/ANZALONE - Cloud privato e cloud pubblico al servizio della business continuity e disaster recovery/2a_introVmware.pdf", "page": 74}}, "wrong_answers": [{"answer_text": "Fa parte di vCenter Server e distribuisce storage tra le VM", "interpretation": "Componente di gestione storage", "why_wrong": "Il balloon driver non è un componente di vCenter e non gestisce lo storage. È un driver installato nel guest OS tramite VMware Tools per il recupero della memoria."}, {"answer_text": "È un componente del BIOS che gestisce il raffreddamento del server", "interpretation": "Gestione termica hardware", "why_wrong": "Il balloon driver non ha nulla a che fare con il BIOS o il raffreddamento. È un driver software per la gestione dinamica della memoria nelle VM."}, {"answer_text": "È un driver per schede grafiche utilizzate dal rendering 3D delle VM", "interpretation": "Driver grafico", "why_wrong": "Il balloon driver non gestisce la grafica. Si occupa esclusivamente del recupero della memoria dal guest OS per restituirla all''hypervisor sotto pressione."}]}', '["ANZALONE_2a_introVmware_hard_003"]');
INSERT INTO questions (batch_id, id, curriculum_area, lecturer, topic, clusters, cognitive_level, question_type, distractor_strategy, difficulty, source_file, source_file_page_number, topics, question_prompt, choices, explanation, related_questions) VALUES (4, 'BERNABEI_01_IntrodAD_medium_003', 'MOD. 3 - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft', 'BERNABEI', 'Replica multimaster e siti', '["Active Directory Domain Services", "Replica e siti"]', 'apply', 'scenario', 'common_misconception', 'medium', 'materiale/BERNABEI - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft/Slide/01.Introduzione ad Active Directory DS.pptx', 32, '["replica", "intrasite", "intersite", "sito", "multimaster"]', 'Un''azienda ha due sedi collegate via WAN. Come differiscono i tempi di replica intrasite e intersite in Active Directory?', '[{"text": "Intrasite ogni 24 ore; intersite in tempo reale tramite connessione VPN dedicata", "is_correct": false}, {"text": "Intrasite avviene in 15-45 secondi; intersite e configurabile, di default ogni 15 minuti", "is_correct": true}, {"text": "Intrasite e intersite avvengono entrambe in tempo reale, senza alcun ritardo configurabile", "is_correct": false}, {"text": "Intrasite e manuale; intersite avviene automaticamente ogni 5 secondi", "is_correct": false}]', '{"right_answer": {"selected_answer": "Intrasite avviene in 15-45 secondi; intersite e configurabile, di default ogni 15 minuti", "rationale": "La slide 32 specifica che le repliche intrasite avvengono molto velocemente (15-45 secondi) mentre quelle intersite possono essere configurate con un default di 15 minuti.", "original_reference": {"path": "materiale/BERNABEI - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft/Slide/01.Introduzione ad Active Directory DS.pptx", "page": 32}}, "wrong_answers": [{"answer_text": "Intrasite e intersite avvengono entrambe in tempo reale, senza alcun ritardo configurabile", "interpretation": "Errata assunzione di replica istantanea ovunque", "why_wrong": "La replica intersite ha ritardi configurabili per ottimizzare l''uso della banda WAN; non e in tempo reale come quella intrasite."}, {"answer_text": "Intrasite ogni 24 ore; intersite in tempo reale tramite connessione VPN dedicata", "interpretation": "Inversione dei tempi di replica", "why_wrong": "E il contrario: la replica intrasite e la piu veloce (15-45 secondi), mentre quella intersite e piu lenta e programmabile."}, {"answer_text": "Intrasite e manuale; intersite avviene automaticamente ogni 5 secondi", "interpretation": "Errata concezione di replica manuale e tempi sbagliati", "why_wrong": "La replica intrasite e automatica e veloce (15-45 sec); non e manuale. La replica intersite non avviene ogni 5 secondi."}]}', '["BERNABEI_01_IntrodAD_hard_002", "BERNABEI_01_IntrodAD_very_easy_003"]');
INSERT INTO questions (batch_id, id, curriculum_area, lecturer, topic, clusters, cognitive_level, question_type, distractor_strategy, difficulty, source_file, source_file_page_number, topics, question_prompt, choices, explanation, related_questions) VALUES (4, 'ANZALONE_1a_virtualIntro_easy_004', 'MOD. 5 - Cloud privato e cloud pubblico al servizio della business continuity e disaster recovery', 'ANZALONE', 'Application virtualization - vantaggi', '["Virtualizzazione e Hypervisor"]', 'understand', 'concept', 'obviously_wrong', 'easy', 'materiale/ANZALONE - Cloud privato e cloud pubblico al servizio della business continuity e disaster recovery/1a_virtualIntro.pdf', 16, '["application virtualization", "vantaggi", "gestione centralizzata", "portabilita''"]', 'Qual e'' uno dei vantaggi principali dell''application virtualization per gli amministratori IT?', '[{"text": "L''applicazione viene installata una sola volta su un server e aggiornata centralmente", "is_correct": true}, {"text": "L''applicazione deve essere installata individualmente su ogni desktop degli utenti", "is_correct": false}, {"text": "L''applicazione puo'' funzionare solo su dispositivi con sistema operativo Windows", "is_correct": false}, {"text": "L''applicazione richiede sempre una connessione Bluetooth per funzionare", "is_correct": false}]', '{"right_answer": {"selected_answer": "L''applicazione viene installata una sola volta su un server e aggiornata centralmente", "rationale": "Il vantaggio principale dell''application virtualization e'' che gli amministratori IT installano l''applicazione una sola volta su un server, facilitando aggiornamenti e patch centralizzati, invece di dover intervenire su ogni singolo desktop.", "original_reference": {"path": "materiale/ANZALONE - Cloud privato e cloud pubblico al servizio della business continuity e disaster recovery/1a_virtualIntro.pdf", "page": 16}}, "wrong_answers": [{"answer_text": "L''applicazione deve essere installata individualmente su ogni desktop degli utenti", "interpretation": "Questa risposta descrive il modello tradizionale senza virtualizzazione", "why_wrong": "E'' esattamente il problema che l''application virtualization risolve: l''installazione individuale su ogni desktop."}, {"answer_text": "L''applicazione puo'' funzionare solo su dispositivi con sistema operativo Windows", "interpretation": "Questa risposta limita la portabilita'' a Windows", "why_wrong": "Al contrario, l''application virtualization permette di accedere alle applicazioni anche da thin client, Android, iOS e dispositivi non-Windows."}, {"answer_text": "L''applicazione richiede sempre una connessione Bluetooth per funzionare", "interpretation": "Questa risposta menziona una tecnologia di connessione irrilevante", "why_wrong": "L''application virtualization richiede una connessione di rete al server, non Bluetooth. Il Bluetooth non e'' coinvolto."}]}', '[]');
INSERT INTO questions (batch_id, id, curriculum_area, lecturer, topic, clusters, cognitive_level, question_type, distractor_strategy, difficulty, source_file, source_file_page_number, topics, question_prompt, choices, explanation, related_questions) VALUES (4, 'BERNABEI_05_AccountComputer_easy_002', 'MOD. 3 - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft', 'BERNABEI', 'Accesso con utente di dominio e locale', '["Account Computer", "Autenticazione"]', 'remember', 'definition', 'plausible_alternative', 'easy', 'materiale/BERNABEI - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft/Slide/05.Gestire gli account computer.pptx', 12, '["logon", "utente dominio", "utente locale", "FQDN"]', 'Quale sintassi consente di effettuare l''accesso con un utente locale su un computer membro di dominio?', '[{"text": "nomedominio\\nomeutente", "is_correct": false}, {"text": "dominio/nomeutente", "is_correct": false}, {"text": ".\\nomeutente", "is_correct": true}, {"text": "nomeutente@FQDNdominio", "is_correct": false}]', '{"right_answer": {"selected_answer": ".\\nomeutente", "rationale": "La slide 12 indica che per l''accesso con utente locale si utilizza la sintassi nomecomputer\\nomeutente oppure .\\nomeutente.", "original_reference": {"path": "materiale/BERNABEI - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft/Slide/05.Gestire gli account computer.pptx", "page": 12}}, "wrong_answers": [{"answer_text": "nomedominio\\nomeutente", "interpretation": "Sintassi locale con nome dominio", "why_wrong": "La sintassi nomedominio\\nomeutente e utilizzata per l''accesso con utente di dominio, non per l''utente locale."}, {"answer_text": "nomeutente@FQDNdominio", "interpretation": "UPN come accesso locale", "why_wrong": "La sintassi nomeutente@FQDNdominio (formato UPN) e utilizzata per l''accesso con utente di dominio, non locale."}, {"answer_text": "dominio/nomeutente", "interpretation": "Slash al posto del backslash", "why_wrong": "La sintassi corretta per il dominio utilizza il backslash (\\), non lo slash (/). Inoltre questa forma si riferirebbe comunque al dominio, non a un accesso locale."}]}', '["BERNABEI_05_AccountComputer_easy_003", "BERNABEI_05_AccountComputer_medium_002"]');
INSERT INTO questions (batch_id, id, curriculum_area, lecturer, topic, clusters, cognitive_level, question_type, distractor_strategy, difficulty, source_file, source_file_page_number, topics, question_prompt, choices, explanation, related_questions) VALUES (4, 'GUIDAZZOLI_devices_drivers_medium_001', 'MOD. 3 - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft', 'GUIDAZZOLI', 'Differenze tra volumi spanned e striped', '["Volumi disco", "Spanned", "Striped", "RAID"]', 'understand', 'comparison', 'common_misconception', 'medium', 'materiale/GUIDAZZOLI - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft/Devices and Drivers.ppt', 10, '["spanned volume", "striped volume", "simple volume", "fault tolerance", "RAID"]', 'Qual e'' la differenza principale tra un volume spanned e un volume striped in Windows?', '[{"text": "Lo striped distribuisce i dati uniformemente su piu'' dischi migliorando le prestazioni I/O", "is_correct": true}, {"text": "Non c''e'' alcuna differenza: sono due nomi per la stessa tecnologia", "is_correct": false}, {"text": "Lo spanned offre fault tolerance mentre lo striped no", "is_correct": false}, {"text": "Il volume spanned puo'' essere usato solo su dischi SSD, lo striped solo su HDD", "is_correct": false}]', '{"right_answer": {"selected_answer": "Lo striped distribuisce i dati uniformemente su piu'' dischi migliorando le prestazioni I/O", "rationale": "Un volume striped (RAID 0) distribuisce i dati in blocchi uniformi su piu'' dischi fisici, migliorando le prestazioni di lettura/scrittura. Un volume spanned riempie un disco prima di passare al successivo.", "original_reference": {"path": "materiale/GUIDAZZOLI - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft/Devices and Drivers.ppt", "page": 10}}, "wrong_answers": [{"answer_text": "Lo spanned offre fault tolerance mentre lo striped no", "interpretation": "Attribuisce erroneamente la fault tolerance allo spanned", "why_wrong": "Nessuno dei due offre fault tolerance. La fault tolerance e'' offerta dal mirror (RAID 1), non dallo spanned ne'' dallo striped."}, {"answer_text": "Il volume spanned puo'' essere usato solo su dischi SSD, lo striped solo su HDD", "interpretation": "Introduce una distinzione basata sul tipo di disco inesistente", "why_wrong": "Entrambi i tipi di volume funzionano indipendentemente dal tipo di disco fisico (SSD o HDD)."}, {"answer_text": "Non c''e'' alcuna differenza: sono due nomi per la stessa tecnologia", "interpretation": "Non riconosce le differenze tecniche tra i due tipi di volume", "why_wrong": "Sono tecnologie diverse: lo spanned concatena lo spazio, lo striped lo distribuisce in parallelo."}]}', '["GUIDAZZOLI_devices_drivers_medium_002"]');
INSERT INTO questions (batch_id, id, curriculum_area, lecturer, topic, clusters, cognitive_level, question_type, distractor_strategy, difficulty, source_file, source_file_page_number, topics, question_prompt, choices, explanation, related_questions) VALUES (4, 'ANZALONE_2b_ft_bc_very_easy_002', 'MOD. 5 - Cloud privato e cloud pubblico al servizio della business continuity e disaster recovery', 'ANZALONE', 'High Availability - Five 9s', '["Business Continuity e Disaster Recovery"]', 'remember', 'definition', 'obviously_wrong', 'very_easy', 'materiale/ANZALONE - Cloud privato e cloud pubblico al servizio della business continuity e disaster recovery/2b_ft_bc.pdf', 7, '["high availability", "five nines", "disponibilità"]', 'In ambito High Availability, a quale percentuale di disponibilità corrisponde lo standard "five 9s"?', '[{"text": "99.999%", "is_correct": true}, {"text": "50.000%", "is_correct": false}, {"text": "99.9%", "is_correct": false}, {"text": "95.000%", "is_correct": false}]', '{"right_answer": {"selected_answer": "99.999%", "rationale": "Lo standard \"five 9s\" (cinque nove) corrisponde a una disponibilità del 99.999%, considerato uno standard molto ambizioso e difficile da raggiungere per un sistema o prodotto.", "original_reference": {"path": "materiale/ANZALONE - Cloud privato e cloud pubblico al servizio della business continuity e disaster recovery/2b_ft_bc.pdf", "page": 7}}, "wrong_answers": [{"answer_text": "95.000%", "interpretation": "Valore arbitrario molto più basso", "why_wrong": "95% è un livello di disponibilità molto basso rispetto ai \"five 9s\" e non contiene alcun riferimento ai cinque nove."}, {"answer_text": "99.9%", "interpretation": "Tre nove anziché cinque", "why_wrong": "99.9% corrisponde a \"three 9s\", non a \"five 9s\", e rappresenta un livello di disponibilità significativamente inferiore."}, {"answer_text": "50.000%", "interpretation": "Valore chiaramente errato", "why_wrong": "50% rappresenterebbe un sistema operativo solo metà del tempo, un livello inaccettabile per qualsiasi servizio."}]}', '["ANZALONE_2b_ft_bc_easy_002"]');
INSERT INTO questions (batch_id, id, curriculum_area, lecturer, topic, clusters, cognitive_level, question_type, distractor_strategy, difficulty, source_file, source_file_page_number, topics, question_prompt, choices, explanation, related_questions) VALUES (4, 'BONONI_NetworkSecurity_easy_004', 'MOD. 2 - Sicurezza delle reti e dei sistemi', 'BONONI', 'Crittografia a chiave pubblica - concetto base', '["Crittografia a chiave pubblica"]', 'understand', 'conceptual', 'key_role_confusion', 'easy', 'materiale/BONONI - Sicurezza delle reti e dei sistemi/LBononi_IFTS_FAV_3_ NetworkSecurity_Bononi_IFTS2015.pdf', 15, '["public key", "private key", "crittografia asimmetrica"]', 'Nella crittografia a chiave pubblica, quale chiave viene usata per cifrare un messaggio destinato a Bob?', '[{"text": "Una chiave simmetrica condivisa tra Alice e Bob", "is_correct": false}, {"text": "La chiave pubblica di Bob", "is_correct": true}, {"text": "La chiave privata di Bob", "is_correct": false}, {"text": "La chiave privata di Alice", "is_correct": false}]', '{"right_answer": {"selected_answer": "La chiave pubblica di Bob", "rationale": "Nella crittografia a chiave pubblica, il mittente usa la chiave pubblica del destinatario (nota a tutti) per cifrare. Solo il destinatario, con la propria chiave privata, puo'' decifrare il messaggio.", "original_reference": {"path": "materiale/BONONI - Sicurezza delle reti e dei sistemi/LBononi_IFTS_FAV_3_ NetworkSecurity_Bononi_IFTS2015.pdf", "page": 16}}, "wrong_answers": [{"answer_text": "La chiave privata di Bob", "interpretation": "Lo studente inverte i ruoli delle chiavi", "why_wrong": "La chiave privata di Bob viene usata solo da Bob per decifrare, non dal mittente per cifrare."}, {"answer_text": "La chiave privata di Alice", "interpretation": "Lo studente confonde cifratura con firma digitale", "why_wrong": "La chiave privata di Alice viene usata per firmare, non per cifrare un messaggio diretto a Bob."}, {"answer_text": "Una chiave simmetrica condivisa tra Alice e Bob", "interpretation": "Lo studente confonde crittografia asimmetrica con simmetrica", "why_wrong": "Nella crittografia a chiave pubblica non serve una chiave condivisa preesistente: questo e'' il vantaggio rispetto alla crittografia simmetrica."}]}', '["BONONI_NetworkSecurity_medium_001"]');
INSERT INTO questions (batch_id, id, curriculum_area, lecturer, topic, clusters, cognitive_level, question_type, distractor_strategy, difficulty, source_file, source_file_page_number, topics, question_prompt, choices, explanation, related_questions) VALUES (4, 'BERNABEI_02_SicurezzaAdmin_easy_001', 'MOD. 3 - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft', 'BERNABEI', 'Snap-in AD - Active Directory Users and Computers', '["Amministrazione Active Directory", "Strumenti di gestione Windows"]', 'remember', 'concept', 'same_category', 'easy', 'materiale/BERNABEI - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft/Slide/02.Sicurezza amministrazione Active Directory.pptx', 5, '["Active Directory Users and Computers", "snap-in", "utenti", "gruppi"]', 'Quale snap-in di Active Directory viene utilizzato per le operazioni quotidiane più comuni, come la gestione di utenti, gruppi e computer?', '[{"text": "Group Policy Management", "is_correct": false}, {"text": "Active Directory Domains and Trusts", "is_correct": false}, {"text": "Active Directory Users and Computers", "is_correct": true}, {"text": "Active Directory Sites and Services", "is_correct": false}]', '{"right_answer": {"selected_answer": "Active Directory Users and Computers", "rationale": "Active Directory Users and Computers (ADUC) è lo snap-in dedicato alla gestione delle operazioni quotidiane più comuni riguardanti utenti, gruppi, computer e altri oggetti del dominio.", "original_reference": {"path": "materiale/BERNABEI - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft/Slide/02.Sicurezza amministrazione Active Directory.pptx", "page": 5}}, "wrong_answers": [{"answer_text": "Active Directory Sites and Services", "interpretation": "Snap-in per la topologia di rete", "why_wrong": "Active Directory Sites and Services è dedicato alla gestione delle repliche, della topologia di rete e dei servizi correlati, non alla gestione quotidiana di utenti e gruppi."}, {"answer_text": "Active Directory Domains and Trusts", "interpretation": "Snap-in per relazioni di trust", "why_wrong": "Active Directory Domains and Trusts è usato per la configurazione delle relazioni di trust fra domini e la gestione dei livelli di funzionalità, non per le operazioni quotidiane su utenti e gruppi."}, {"answer_text": "Group Policy Management", "interpretation": "Snap-in per le policy di gruppo", "why_wrong": "Group Policy Management è dedicato alla gestione delle Group Policy, non alla gestione diretta di utenti, gruppi e computer in Active Directory."}]}', '["BERNABEI_02_SicurezzaAdmin_very_easy_001"]');
INSERT INTO questions (batch_id, id, curriculum_area, lecturer, topic, clusters, cognitive_level, question_type, distractor_strategy, difficulty, source_file, source_file_page_number, topics, question_prompt, choices, explanation, related_questions) VALUES (4, 'BERNABEI_06_GroupPolicy_easy_004', 'MOD. 3 - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft', 'BERNABEI', 'GPO locali vs GPO di dominio', '["Group Policy", "Precedenze"]', 'understand', 'comparison', 'opposite_meaning', 'easy', 'materiale/BERNABEI - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft/Slide/06.Implementare le Group Policy.pptx', 13, '["GPO locali", "GPO di dominio", "priorita", "gpedit.msc"]', 'Quale affermazione e corretta riguardo alle GPO locali rispetto alle GPO di dominio?', '[{"text": "Le GPO locali hanno priorita piu bassa e vengono sovrascritte dalle GPO di dominio", "is_correct": true}, {"text": "Le GPO locali e quelle di dominio hanno la stessa priorita e si sommano sempre", "is_correct": false}, {"text": "Le GPO locali hanno priorita piu alta rispetto alle GPO di dominio", "is_correct": false}]', '{"right_answer": {"selected_answer": "Le GPO locali hanno priorita piu bassa e vengono sovrascritte dalle GPO di dominio", "rationale": "Le slide 13-14 specificano che le GPO locali sono applicate prima di qualunque GPO di dominio e che ogni impostazione specificata da una GPO di dominio sovrascrivera quanto definito dalle GPO locali.", "original_reference": {"path": "materiale/BERNABEI - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft/Slide/06.Implementare le Group Policy.pptx", "page": 13}}, "wrong_answers": [{"answer_text": "Le GPO locali hanno priorita piu alta rispetto alle GPO di dominio", "interpretation": "Inversione della gerarchia di precedenza", "why_wrong": "E il contrario: le GPO di dominio hanno priorita piu alta. L''ordine LSDOU prevede che le GPO locali siano processate per prime e quindi abbiano la priorita piu bassa."}, {"answer_text": "Le GPO locali e quelle di dominio hanno la stessa priorita e si sommano sempre", "interpretation": "Parita tra GPO locali e di dominio", "why_wrong": "Esiste una chiara gerarchia: le GPO di dominio prevalgono sulle GPO locali in caso di conflitto. Non hanno la stessa priorita."}]}', '["BERNABEI_06_GroupPolicy_very_easy_003", "BERNABEI_06_GroupPolicy_medium_002"]');
INSERT INTO questions (batch_id, id, curriculum_area, lecturer, topic, clusters, cognitive_level, question_type, distractor_strategy, difficulty, source_file, source_file_page_number, topics, question_prompt, choices, explanation, related_questions) VALUES (4, 'FORONI_07_GatheringInfo_medium_005', 'MOD. 4 - Implementazione e verifica della sicurezza delle reti e dei sistemi', 'FORONI', 'ARIN e IANA per il network range', '["ARIN", "IANA", "network range", "IP registry"]', 'understand', 'conceptual', 'common_confusion', 'medium', 'materiale/FORONI/Lezioni - Implementazione e verifica della sicurezza delle reti e dei sistemi/7.Gathering Target Information.pdf', 11, '["ARIN", "IANA", "network range", "subnet mask", "IP address"]', 'Dove puo'' un ethical hacker trovare informazioni sul range di indirizzi IP e sulla subnet mask di un sistema bersaglio?', '[{"text": "Nel codice sorgente delle pagine HTML del sito web del bersaglio", "is_correct": false}, {"text": "Nei registri Internet come ARIN o IANA (Internet Assigned Numbers Authority)", "is_correct": true}, {"text": "Tramite l''invio di email al reparto IT dell''organizzazione bersaglio", "is_correct": false}, {"text": "Nei file di log del web server del bersaglio, accessibili pubblicamente", "is_correct": false}]', '{"right_answer": {"selected_answer": "Nei registri Internet come ARIN o IANA (Internet Assigned Numbers Authority)", "rationale": "Il materiale specifica a pagina 11 che ''You can find IP addresses in Internet registries such as ARIN or the Internet Assigned Numbers Authority (IANA)''.", "original_reference": {"path": "materiale/FORONI/Lezioni - Implementazione e verifica della sicurezza delle reti e dei sistemi/7.Gathering Target Information.pdf", "page": 11}}, "wrong_answers": [{"answer_text": "Nei file di log del web server del bersaglio, accessibili pubblicamente", "interpretation": "Presuppone erroneamente che i log siano accessibili pubblicamente", "why_wrong": "I file di log del web server non sono normalmente accessibili pubblicamente. I registri ARIN e IANA sono invece database pubblici consultabili per informazioni sugli IP assegnati."}, {"answer_text": "Nel codice sorgente delle pagine HTML del sito web del bersaglio", "interpretation": "Confonde i dati di registrazione IP con il codice delle pagine web", "why_wrong": "Il codice HTML delle pagine web non contiene informazioni sui range di indirizzi IP o subnet mask. Queste informazioni sono nei registri Internet dedicati."}, {"answer_text": "Tramite l''invio di email al reparto IT dell''organizzazione bersaglio", "interpretation": "Confonde la ricerca nei registri pubblici con il social engineering", "why_wrong": "Chiedere direttamente al reparto IT sarebbe social engineering, non footprinting tecnico. I registri ARIN/IANA forniscono queste informazioni senza interazione con il bersaglio."}]}', '[]');
INSERT INTO questions (batch_id, id, curriculum_area, lecturer, topic, clusters, cognitive_level, question_type, distractor_strategy, difficulty, source_file, source_file_page_number, topics, question_prompt, choices, explanation, related_questions) VALUES (4, 'ANZALONE_3a_vnet_very_easy_002', 'MOD. 5 - Cloud privato e cloud pubblico al servizio della business continuity e disaster recovery', 'ANZALONE', 'Livello OSI del vSwitch', '["Networking Virtuale", "Infrastruttura VMware vSphere"]', 'remember', 'definition', 'obviously_wrong', 'very_easy', 'materiale/ANZALONE - Cloud privato e cloud pubblico al servizio della business continuity e disaster recovery/3a_vnet.pdf', 8, '["vSwitch", "Layer 2", "MAC address"]', 'A quale livello del modello OSI opera un vSphere standard switch?', '[{"text": "Layer 7: analizza il contenuto applicativo dei pacchetti HTTP e FTP", "is_correct": false}, {"text": "Layer 2: gestisce tabelle MAC e supporta VLAN con tagging 802.1Q", "is_correct": true}, {"text": "Layer 1: gestisce esclusivamente segnali elettrici sui cavi fisici", "is_correct": false}, {"text": "Layer 4: filtra il traffico in base alle porte TCP e UDP", "is_correct": false}]', '{"right_answer": {"selected_answer": "Layer 2: gestisce tabelle MAC e supporta VLAN con tagging 802.1Q", "rationale": "Il vSwitch opera a Layer 2 del modello OSI: mantiene tabelle di indirizzi MAC, inoltra frame in base al MAC di destinazione, supporta configurazioni VLAN e trunking IEEE 802.1Q.", "original_reference": {"path": "materiale/ANZALONE - Cloud privato e cloud pubblico al servizio della business continuity e disaster recovery/3a_vnet.pdf", "page": 8}}, "wrong_answers": [{"answer_text": "Layer 7: analizza il contenuto applicativo dei pacchetti HTTP e FTP", "interpretation": "Confonde il vSwitch con un application gateway", "why_wrong": "Il vSwitch non opera a livello applicativo; lavora a Layer 2 con frame Ethernet, senza ispezionare il contenuto delle applicazioni."}, {"answer_text": "Layer 1: gestisce esclusivamente segnali elettrici sui cavi fisici", "interpretation": "Confonde il vSwitch con il livello fisico", "why_wrong": "Layer 1 riguarda il mezzo fisico. Il vSwitch è un componente software che opera a Layer 2 gestendo frame e indirizzi MAC."}, {"answer_text": "Layer 4: filtra il traffico in base alle porte TCP e UDP", "interpretation": "Confonde il vSwitch con un firewall Layer 4", "why_wrong": "Il vSwitch non opera a Layer 4 e non filtra per porte TCP/UDP; la sua funzione è lo switching di frame Ethernet a Layer 2."}]}', '["ANZALONE_3a_vnet_very_easy_001"]');
INSERT INTO questions (batch_id, id, curriculum_area, lecturer, topic, clusters, cognitive_level, question_type, distractor_strategy, difficulty, source_file, source_file_page_number, topics, question_prompt, choices, explanation, related_questions) VALUES (4, 'FORONI_4_CertificatiPKI_easy_001', 'MOD. 4 - Implementazione e verifica della sicurezza delle reti e dei sistemi', 'FORONI', 'Proprieta'' dei certificati', '["certificati", "proprieta''", "CA", "verificabilita''"]', 'understand', 'conceptual', 'opposite_meaning', 'easy', 'materiale/FORONI/Lezioni - Implementazione e verifica della sicurezza delle reti e dei sistemi/4Certificati e PKI_2017.pdf', 12, '["proprieta'' certificati", "leggibilita''", "verificabilita''", "autorita'' di certificazione"]', 'Quali sono le tre proprieta'' fondamentali dei certificati digitali?', '[{"text": "Sono criptati, hanno validita'' illimitata, non possono essere revocati", "is_correct": false}, {"text": "Ognuno puo'' leggerli, ognuno puo'' verificarne l''autenticita'', solo la CA puo'' crearli e aggiornarli", "is_correct": true}, {"text": "Sono segreti, possono essere creati da chiunque, non hanno scadenza", "is_correct": false}, {"text": "Possono essere letti solo dal destinatario, sono verificabili solo dalla CA, possono essere creati da qualsiasi utente", "is_correct": false}]', '{"right_answer": {"selected_answer": "Ognuno puo'' leggerli, ognuno puo'' verificarne l''autenticita'', solo la CA puo'' crearli e aggiornarli", "rationale": "La slide 12 elenca le proprieta'': ''Ognuno puo'' leggerli e determinare nome e chiave pubblica. Ognuno puo'' verificarli ed assicurarsi dell''autenticita''. Solo l''Autorita'' puo'' crearli ed aggiornarli.''", "original_reference": {"path": "materiale/FORONI/Lezioni - Implementazione e verifica della sicurezza delle reti e dei sistemi/4Certificati e PKI_2017.pdf", "page": 12}}, "wrong_answers": [{"answer_text": "Sono segreti, possono essere creati da chiunque, non hanno scadenza", "interpretation": "Inverte tutte e tre le proprieta''", "why_wrong": "E'' il contrario: i certificati sono pubblici (non segreti), possono essere creati solo dalla CA (non da chiunque) e hanno un periodo di validita'' (non illimitato)."}, {"answer_text": "Possono essere letti solo dal destinatario, sono verificabili solo dalla CA, possono essere creati da qualsiasi utente", "interpretation": "Confonde la natura pubblica del certificato con un documento riservato", "why_wrong": "I certificati sono pubblici: OGNUNO puo'' leggerli e verificarli. Solo la CA puo'' crearli, non qualsiasi utente."}, {"answer_text": "Sono criptati, hanno validita'' illimitata, non possono essere revocati", "interpretation": "Attribuisce ai certificati proprieta'' opposte a quelle reali", "why_wrong": "I certificati non sono criptati (sono leggibili), hanno un periodo di validita'' definito e possono essere revocati dalla CA."}]}', '["FORONI_4_CertificatiPKI_very_easy_001"]');
INSERT INTO questions (batch_id, id, curriculum_area, lecturer, topic, clusters, cognitive_level, question_type, distractor_strategy, difficulty, source_file, source_file_page_number, topics, question_prompt, choices, explanation, related_questions) VALUES (4, 'BONONI_intro_reti2025A_very_easy_002', 'MOD. 2 - Sicurezza delle reti e dei sistemi', 'BONONI', 'Classificazione delle reti per dimensione', '["Fondamenti di reti"]', 'remember', 'definition', 'plausible_alternative', 'very_easy', 'materiale/BONONI - Sicurezza delle reti e dei sistemi/LBononi_IFTS_intro_reti2025Anew.pdf', 4, '["LAN", "Local Area Network", "classificazione reti"]', 'Cosa indica l''acronimo LAN nel contesto delle reti di calcolatori?', '[{"text": "Logical Address Number: un numero che identifica ogni dispositivo in Internet", "is_correct": false}, {"text": "Linear Array Network: una rete in cui i dispositivi sono collegati in linea retta", "is_correct": false}, {"text": "Large Access Node: un nodo di rete ad alta capacita'' per connessioni internazionali", "is_correct": false}, {"text": "Local Area Network: una rete che connette dispositivi in un''area limitata come un ufficio o un edificio", "is_correct": true}]', '{"right_answer": {"selected_answer": "Local Area Network: una rete che connette dispositivi in un''area limitata come un ufficio o un edificio", "rationale": "LAN sta per Local Area Network (rete locale). Le reti LAN connettono calcolatori nel raggio di qualche centinaio di metri, ad esempio in uffici, laboratori, edifici o campus universitari. Sono spesso gestite e mantenute da organizzazioni, universita'' o aziende.", "original_reference": {"path": "materiale/BONONI - Sicurezza delle reti e dei sistemi/LBononi_IFTS_intro_reti2025Anew.pdf", "page": 4}}, "wrong_answers": [{"answer_text": "Large Access Node: un nodo di rete ad alta capacita'' per connessioni internazionali", "interpretation": "Questa risposta inventa un acronimo plausibile ma errato", "why_wrong": "LAN non significa Large Access Node. Si riferisce a Local Area Network, cioe'' una rete locale a copertura geografica limitata."}, {"answer_text": "Linear Array Network: una rete in cui i dispositivi sono collegati in linea retta", "interpretation": "Questa risposta confonde LAN con una topologia specifica", "why_wrong": "LAN non indica una topologia lineare. E'' una classificazione basata sulla dimensione geografica, non sulla disposizione dei dispositivi."}, {"answer_text": "Logical Address Number: un numero che identifica ogni dispositivo in Internet", "interpretation": "Questa risposta confonde LAN con un concetto di indirizzamento", "why_wrong": "LAN non e'' un tipo di indirizzo. E'' una classe di rete locale, distinta da PAN, MAN e WAN per estensione geografica."}]}', '["BONONI_intro_reti2025A_easy_002"]');
INSERT INTO questions (batch_id, id, curriculum_area, lecturer, topic, clusters, cognitive_level, question_type, distractor_strategy, difficulty, source_file, source_file_page_number, topics, question_prompt, choices, explanation, related_questions) VALUES (4, 'FORONI_4b_CertSSL_easy_002', 'MOD. 4 - Implementazione e verifica della sicurezza delle reti e dei sistemi', 'FORONI', 'Quando dotarsi di un certificato SSL', '["SSL", "casi d''uso", "pagamenti", "form", "account"]', 'understand', 'conceptual', 'restriction_fallacy', 'easy', 'materiale/FORONI/Lezioni - Implementazione e verifica della sicurezza delle reti e dei sistemi/4certificatissl-commerciale register.pdf', 3, '["SSL", "pagamenti online", "form", "documenti", "account utente"]', 'Secondo il documento, in quali casi e'' particolarmente necessario dotarsi di un certificato SSL per il proprio sito web?', '[{"text": "Solo quando il sito ha piu'' di 10.000 visitatori al giorno", "is_correct": false}, {"text": "Solo quando il sito tratta dati sanitari o finanziari regolamentati dalla legge", "is_correct": false}, {"text": "Solo quando il sito e'' un e-commerce con piu'' di 1000 transazioni mensili", "is_correct": false}, {"text": "Quando il sito accetta pagamenti, include form per informazioni personali, permette il caricamento/scambio di documenti, o permette agli utenti di creare account", "is_correct": true}]', '{"right_answer": {"selected_answer": "Quando il sito accetta pagamenti, include form per informazioni personali, permette il caricamento/scambio di documenti, o permette agli utenti di creare account", "rationale": "La slide 3 elenca i casi in cui un certificato SSL diventa ''davvero necessario'': il sito ''accetta pagamenti'', ''include dei form che raccolgono informazioni personali'', ''permette il caricamento/lo scambio di documenti'', ''permette agli utenti di crearsi degli account''.", "original_reference": {"path": "materiale/FORONI/Lezioni - Implementazione e verifica della sicurezza delle reti e dei sistemi/4certificatissl-commerciale register.pdf", "page": 3}}, "wrong_answers": [{"answer_text": "Solo quando il sito e'' un e-commerce con piu'' di 1000 transazioni mensili", "interpretation": "Limita eccessivamente l''ambito al solo e-commerce di grandi dimensioni", "why_wrong": "Il documento indica che tutti i siti web dovrebbero dotarsi di SSL per il ranking Google. Non c''e'' una soglia di transazioni: qualsiasi sito che raccoglie dati lo necessita."}, {"answer_text": "Solo quando il sito tratta dati sanitari o finanziari regolamentati dalla legge", "interpretation": "Restringe l''uso SSL ai soli casi regolamentati", "why_wrong": "Il SSL e'' necessario in molti piu'' casi rispetto ai soli dati regolamentati: form, account utente, documenti e pagamenti di qualsiasi tipo."}, {"answer_text": "Solo quando il sito ha piu'' di 10.000 visitatori al giorno", "interpretation": "Lega l''uso SSL al volume di traffico", "why_wrong": "La necessita'' di SSL non dipende dal numero di visitatori ma dal tipo di dati trattati (pagamenti, form, documenti, account)."}]}', '["FORONI_4b_CertSSL_very_easy_001"]');
