INSERT INTO questions (batch_id, id, curriculum_area, lecturer, topic, clusters, cognitive_level, question_type, distractor_strategy, difficulty, source_file, source_file_page_number, topics, question_prompt, choices, explanation, related_questions) VALUES (5, 'FORONI_3_Crittografia_easy_006', 'MOD. 4 - Implementazione e verifica della sicurezza delle reti e dei sistemi', 'FORONI', 'RSA - principio di funzionamento', '["RSA", "numeri primi", "fattorizzazione", "prime factorization"]', 'understand', 'conceptual', 'related_concept', 'easy', 'materiale/FORONI/Lezioni - Implementazione e verifica della sicurezza delle reti e dei sistemi/3.CRITTOGRAFIA.pdf', 20, '["RSA", "numeri primi", "fattorizzazione", "n=p*q"]', 'Su quale problema matematico si basa la sicurezza dell''algoritmo RSA?', '[{"text": "La difficolta'' di calcolare il logaritmo discreto in un campo finito", "is_correct": false}, {"text": "La difficolta'' di trovare collisioni in una funzione hash crittografica", "is_correct": false}, {"text": "La difficolta'' computazionale della fattorizzazione di un numero n ottenuto dal prodotto di due numeri primi grandi p e q", "is_correct": true}]', '{"right_answer": {"selected_answer": "La difficolta'' computazionale della fattorizzazione di un numero n ottenuto dal prodotto di due numeri primi grandi p e q", "rationale": "La slide 20 spiega: ''L''algoritmo RSA lavora sfruttando i numeri primi e come chiave utilizza un numero n ottenuto proprio dal prodotto di due numeri primi p e q, cioe'' n = p * q. Per decriptare un messaggio cifrato con RSA e'' necessario decomporre la chiave n nei due numeri primi p e q: questo e'' computazionalmente impegnativo da ottenere''.", "original_reference": {"path": "materiale/FORONI/Lezioni - Implementazione e verifica della sicurezza delle reti e dei sistemi/3.CRITTOGRAFIA.pdf", "page": 20}}, "wrong_answers": [{"answer_text": "La difficolta'' di calcolare il logaritmo discreto in un campo finito", "interpretation": "Confonde RSA con il protocollo Diffie-Hellman o con la crittografia su curve ellittiche", "why_wrong": "Il problema del logaritmo discreto e'' alla base di altri sistemi crittografici come Diffie-Hellman e la crittografia su curve ellittiche (ECC), non di RSA."}, {"answer_text": "La difficolta'' di trovare collisioni in una funzione hash crittografica", "interpretation": "Confonde RSA con le funzioni di hashing", "why_wrong": "La resistenza alle collisioni riguarda le funzioni hash (come SHA), non l''algoritmo RSA che si basa sulla fattorizzazione."}]}', '["FORONI_3_Crittografia_very_easy_003"]');
INSERT INTO questions (batch_id, id, curriculum_area, lecturer, topic, clusters, cognitive_level, question_type, distractor_strategy, difficulty, source_file, source_file_page_number, topics, question_prompt, choices, explanation, related_questions) VALUES (5, 'ANZALONE_3a_vnet_easy_002', 'MOD. 5 - Cloud privato e cloud pubblico al servizio della business continuity e disaster recovery', 'ANZALONE', 'VLAN ID e default VLAN', '["Networking Virtuale", "Sicurezza delle Reti"]', 'remember', 'definition', 'obviously_wrong', 'easy', 'materiale/ANZALONE - Cloud privato e cloud pubblico al servizio della business continuity e disaster recovery/3a_vnet.pdf', 105, '["VLAN ID", "default VLAN", "802.1Q"]', 'Qual è il VLAN ID riservato alla default VLAN e qual è il range configurabile dall''utente?', '[{"text": "Default VLAN ID 100, range configurabile da 101 a 4096", "is_correct": false}, {"text": "Default VLAN ID 1, range configurabile da 2 a 4094", "is_correct": true}, {"text": "Default VLAN ID 0, range configurabile da 1 a 1024", "is_correct": false}, {"text": "Default VLAN ID 4095, range configurabile da 1 a 4094", "is_correct": false}]', '{"right_answer": {"selected_answer": "Default VLAN ID 1, range configurabile da 2 a 4094", "rationale": "Il VID 1 è riservato alla default VLAN. Il range di VLAN ID configurabili dall''utente va da 2 a 4094. Uno switch non configurato per VLAN ha tutti i port nella VLAN 1 di default.", "original_reference": {"path": "materiale/ANZALONE - Cloud privato e cloud pubblico al servizio della business continuity e disaster recovery/3a_vnet.pdf", "page": 105}}, "wrong_answers": [{"answer_text": "Default VLAN ID 0, range configurabile da 1 a 1024", "interpretation": "VID e range entrambi errati", "why_wrong": "Il VID 0 ha significato speciale (priority tagging), non è la default VLAN. Il range corretto arriva a 4094, non a 1024."}, {"answer_text": "Default VLAN ID 4095, range configurabile da 1 a 4094", "interpretation": "Inverte la posizione della default VLAN", "why_wrong": "Il VID 4095 è riservato per uso interno e non è la default VLAN. La default VLAN ha sempre ID 1."}, {"answer_text": "Default VLAN ID 100, range configurabile da 101 a 4096", "interpretation": "Valori completamente inventati", "why_wrong": "Il VID 100 non ha significato speciale e il range 101-4096 è errato. La default VLAN è ID 1 e il range va da 2 a 4094."}]}', '["ANZALONE_3a_vnet_very_easy_003"]');
INSERT INTO questions (batch_id, id, curriculum_area, lecturer, topic, clusters, cognitive_level, question_type, distractor_strategy, difficulty, source_file, source_file_page_number, topics, question_prompt, choices, explanation, related_questions) VALUES (5, 'BERNABEI_05_AccountComputer_easy_003', 'MOD. 3 - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft', 'BERNABEI', 'Attributi degli account computer', '["Account Computer", "Active Directory"]', 'remember', 'concept', 'plausible_alternative', 'easy', 'materiale/BERNABEI - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft/Slide/05.Gestire gli account computer.pptx', 15, '["attributi", "Location", "Managed By", "stampanti"]', 'L''attributo Location di un account computer in AD viene utilizzato da quali tipi di applicazioni?', '[{"text": "Applicazioni \"location-aware\", ad esempio la ricerca delle stampanti", "is_correct": true}, {"text": "Applicazioni di backup e disaster recovery", "is_correct": false}, {"text": "Applicazioni di monitoraggio delle performance del computer", "is_correct": false}, {"text": "Applicazioni di gestione delle licenze software", "is_correct": false}]', '{"right_answer": {"selected_answer": "Applicazioni \"location-aware\", ad esempio la ricerca delle stampanti", "rationale": "La slide 15 specifica che l''attributo Location e utilizzato dalle applicazioni \"location-aware\", con l''esempio della ricerca delle stampanti.", "original_reference": {"path": "materiale/BERNABEI - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft/Slide/05.Gestire gli account computer.pptx", "page": 15}}, "wrong_answers": [{"answer_text": "Applicazioni di monitoraggio delle performance del computer", "interpretation": "Location come dato per il monitoraggio", "why_wrong": "L''attributo Location non e legato al monitoraggio delle performance. Il materiale lo associa esplicitamente alle applicazioni location-aware come la ricerca stampanti."}, {"answer_text": "Applicazioni di backup e disaster recovery", "interpretation": "Location come parametro per i backup", "why_wrong": "L''attributo Location non ha relazione con le operazioni di backup. Serve a identificare la posizione fisica del computer per applicazioni location-aware."}, {"answer_text": "Applicazioni di gestione delle licenze software", "interpretation": "Location per tracciare le licenze", "why_wrong": "L''attributo Location non e utilizzato per la gestione delle licenze. Il suo scopo e fornire informazioni sulla posizione fisica a servizi come la ricerca stampanti."}]}', '["BERNABEI_05_AccountComputer_easy_004", "BERNABEI_05_AccountComputer_medium_003"]');
INSERT INTO questions (batch_id, id, curriculum_area, lecturer, topic, clusters, cognitive_level, question_type, distractor_strategy, difficulty, source_file, source_file_page_number, topics, question_prompt, choices, explanation, related_questions) VALUES (5, 'BERNABEI_04_GestireGruppi_medium_001', 'MOD. 3 - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft', 'BERNABEI', 'Modifica scope dei gruppi', '["Gestione Gruppi", "Amministrazione AD"]', 'understand', 'concept', 'plausible_alternative', 'medium', 'materiale/BERNABEI - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft/Slide/04.Gestire i gruppi.pptx', 26, '["group scope", "conversione", "Global", "Universal", "Domain Local"]', 'E possibile convertire direttamente un gruppo Global in Domain Local? Se no, quale passaggio intermedio e necessario?', '[{"text": "No, bisogna prima convertire il gruppo da Global a Universal, poi da Universal a Domain Local", "is_correct": true}, {"text": "Si, ma solo se il gruppo non ha membri attivi", "is_correct": false}, {"text": "No, bisogna eliminare il gruppo Global e ricrearlo come Domain Local", "is_correct": false}, {"text": "Si, la conversione diretta da Global a Domain Local e sempre possibile", "is_correct": false}]', '{"right_answer": {"selected_answer": "No, bisogna prima convertire il gruppo da Global a Universal, poi da Universal a Domain Local", "rationale": "La slide 26 indica che non e possibile passare direttamente da G a DL, ma e possibile con un passaggio intermedio: G → U → DL.", "original_reference": {"path": "materiale/BERNABEI - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft/Slide/04.Gestire i gruppi.pptx", "page": 26}}, "wrong_answers": [{"answer_text": "Si, la conversione diretta da Global a Domain Local e sempre possibile", "interpretation": "Conversione diretta possibile", "why_wrong": "Active Directory non consente la conversione diretta tra Global e Domain Local; il passaggio intermedio tramite Universal e obbligatorio."}, {"answer_text": "No, bisogna eliminare il gruppo Global e ricrearlo come Domain Local", "interpretation": "Eliminazione e ricreazione del gruppo", "why_wrong": "Non e necessario eliminare e ricreare il gruppo; basta effettuare due conversioni di scope passando per Universal. Eliminare il gruppo causerebbe la perdita del SID."}, {"answer_text": "Si, ma solo se il gruppo non ha membri attivi", "interpretation": "Conversione condizionata alla membership vuota", "why_wrong": "La conversione diretta G → DL non e possibile indipendentemente dalla membership; il passaggio intermedio per Universal e sempre richiesto."}]}', '["BERNABEI_04_GestireGruppi_easy_001", "BERNABEI_04_GestireGruppi_hard_001"]');
INSERT INTO questions (batch_id, id, curriculum_area, lecturer, topic, clusters, cognitive_level, question_type, distractor_strategy, difficulty, source_file, source_file_page_number, topics, question_prompt, choices, explanation, related_questions) VALUES (5, 'BONONI_intro_reti2025B_easy_004', 'MOD. 2 - Sicurezza delle reti e dei sistemi', 'BONONI', 'Protocollo ARP', '["Protocolli di supporto"]', 'understand', 'conceptual', 'related_concept', 'easy', 'materiale/BONONI - Sicurezza delle reti e dei sistemi/LBononi_RdC_intro_reti2025Bnew.pdf', 22, '["ARP", "MAC", "risoluzione indirizzi"]', 'A quale domanda risponde il protocollo ARP (Address Resolution Protocol)?', '[{"text": "Quale indirizzo IP corrisponde a un dato nome di dominio?", "is_correct": false}, {"text": "Quale indirizzo MAC corrisponde a un dato indirizzo IP sulla rete locale?", "is_correct": true}, {"text": "Quale porta TCP e'' associata a una data applicazione?", "is_correct": false}, {"text": "Quale server DHCP e'' disponibile nella rete locale?", "is_correct": false}]', '{"right_answer": {"selected_answer": "Quale indirizzo MAC corrisponde a un dato indirizzo IP sulla rete locale?", "rationale": "Il protocollo ARP consente a un router di scoprire l''indirizzo MAC corrispondente a un indirizzo IP. Il router genera un frame broadcast chiedendo ''quale e'' l''indirizzo MAC del dispositivo che ha questo indirizzo IP?'' e il dispositivo risponde con il proprio MAC.", "original_reference": {"path": "materiale/BONONI - Sicurezza delle reti e dei sistemi/LBononi_RdC_intro_reti2025Bnew.pdf", "page": 22}}, "wrong_answers": [{"answer_text": "Quale indirizzo IP corrisponde a un dato nome di dominio?", "interpretation": "Confonde ARP con il servizio DNS", "why_wrong": "La risoluzione nome di dominio -> indirizzo IP e'' compito del DNS, non di ARP. ARP opera tra livello MAC e livello IP."}, {"answer_text": "Quale porta TCP e'' associata a una data applicazione?", "interpretation": "Confonde ARP con concetti di livello trasporto", "why_wrong": "Le porte TCP riguardano il livello trasporto. ARP opera tra livello 2 (MAC) e livello 3 (IP)."}, {"answer_text": "Quale server DHCP e'' disponibile nella rete locale?", "interpretation": "Confonde ARP con la scoperta di server DHCP", "why_wrong": "La scoperta del DHCP server avviene attraverso il protocollo DHCP stesso, non tramite ARP."}]}', '[]');
INSERT INTO questions (batch_id, id, curriculum_area, lecturer, topic, clusters, cognitive_level, question_type, distractor_strategy, difficulty, source_file, source_file_page_number, topics, question_prompt, choices, explanation, related_questions) VALUES (5, 'BERNABEI_01_IntrodAD_very_easy_002', 'MOD. 3 - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft', 'BERNABEI', 'Protocollo LDAP', '["Active Directory Domain Services", "Protocolli di rete"]', 'remember', 'definition', 'obviously_wrong', 'very_easy', 'materiale/BERNABEI - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft/Slide/01.Introduzione ad Active Directory DS.pptx', 5, '["LDAP", "Lightweight Directory Access Protocol", "servizi directory"]', 'Cosa significa l''acronimo LDAP e qual e la sua funzione principale?', '[{"text": "Linked Domain Authentication Protocol: protocollo per la crittografia dei domini", "is_correct": false}, {"text": "Large Database Administration Platform: piattaforma per gestire grandi database relazionali", "is_correct": false}, {"text": "Lightweight Directory Access Protocol: protocollo per accedere ai servizi directory come Active Directory", "is_correct": true}, {"text": "Local Data Access Procedure: procedura per l''accesso ai dati su disco locale", "is_correct": false}]', '{"right_answer": {"selected_answer": "Lightweight Directory Access Protocol: protocollo per accedere ai servizi directory come Active Directory", "rationale": "La slide 5 definisce LDAP come Lightweight Directory Access Protocol, un protocollo software che lavora con vari servizi directory, fornendo un mezzo per gestire utenti e gruppi archiviati in Active Directory.", "original_reference": {"path": "materiale/BERNABEI - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft/Slide/01.Introduzione ad Active Directory DS.pptx", "page": 5}}, "wrong_answers": [{"answer_text": "Local Data Access Procedure: procedura per l''accesso ai dati su disco locale", "interpretation": "Acronimo inventato plausibile", "why_wrong": "L''acronimo corretto e Lightweight Directory Access Protocol, non ha relazione con l''accesso ai dischi locali."}, {"answer_text": "Linked Domain Authentication Protocol: protocollo per la crittografia dei domini", "interpretation": "Acronimo inventato che suona tecnico", "why_wrong": "LDAP non riguarda la crittografia dei domini; e un protocollo per interrogare e modificare servizi directory."}, {"answer_text": "Large Database Administration Platform: piattaforma per gestire grandi database relazionali", "interpretation": "Confusione con piattaforme di database", "why_wrong": "LDAP non e una piattaforma di amministrazione database relazionali; e un protocollo per servizi di directory."}]}', '["BERNABEI_01_IntrodAD_very_easy_001", "BERNABEI_01_IntrodAD_easy_002"]');
INSERT INTO questions (batch_id, id, curriculum_area, lecturer, topic, clusters, cognitive_level, question_type, distractor_strategy, difficulty, source_file, source_file_page_number, topics, question_prompt, choices, explanation, related_questions) VALUES (5, 'GUIDAZZOLI_file_access_very_easy_001', 'MOD. 3 - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft', 'GUIDAZZOLI', 'Definizione di autenticazione', '["Autenticazione", "Autorizzazione", "Sicurezza accessi"]', 'remember', 'definition', 'obviously_wrong', 'very_easy', 'materiale/GUIDAZZOLI - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft/File Access and Printers.ppt', 4, '["autenticazione", "authentication", "identita''", "verifica"]', 'Cos''e'' l''autenticazione (authentication) in un sistema Windows?', '[{"text": "Il processo di partizionamento di un disco rigido in volumi", "is_correct": false}, {"text": "Il processo di verifica dell''identita'' di un utente o di un dispositivo", "is_correct": true}, {"text": "Il processo di stampa di un documento su una stampante di rete", "is_correct": false}, {"text": "Il processo di compressione dei file per risparmiare spazio disco", "is_correct": false}]', '{"right_answer": {"selected_answer": "Il processo di verifica dell''identita'' di un utente o di un dispositivo", "rationale": "Il materiale definisce: ''Authentication: Verifying the identity of something or someone''. L''autenticazione risponde alla domanda ''Who are you?''.", "original_reference": {"path": "materiale/GUIDAZZOLI - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft/File Access and Printers.ppt", "page": 4}}, "wrong_answers": [{"answer_text": "Il processo di compressione dei file per risparmiare spazio disco", "interpretation": "Confonde l''autenticazione con la compressione NTFS", "why_wrong": "La compressione dei file e'' una funzionalita'' NTFS separata, non ha nulla a che fare con la verifica dell''identita''."}, {"answer_text": "Il processo di stampa di un documento su una stampante di rete", "interpretation": "Confonde l''autenticazione con il printing", "why_wrong": "La stampa e'' un''operazione di output, non un processo di verifica dell''identita''."}, {"answer_text": "Il processo di partizionamento di un disco rigido in volumi", "interpretation": "Confonde l''autenticazione con la gestione disco", "why_wrong": "Il partizionamento riguarda la suddivisione dei dischi, non la verifica dell''identita'' degli utenti."}]}', '["GUIDAZZOLI_file_access_very_easy_002", "GUIDAZZOLI_file_access_easy_001"]');
INSERT INTO questions (batch_id, id, curriculum_area, lecturer, topic, clusters, cognitive_level, question_type, distractor_strategy, difficulty, source_file, source_file_page_number, topics, question_prompt, choices, explanation, related_questions) VALUES (5, 'BONONI_NAT_easy_001', 'MOD. 2 - Sicurezza delle reti e dei sistemi', 'BONONI', 'Motivazioni del NAT', '["NAT", "gestione indirizzi IP"]', 'understand', 'conceptual', 'opposite_statement', 'easy', 'materiale/BONONI - Sicurezza delle reti e dei sistemi/NAT-IPv4.pdf', 2, '["NAT", "ISP", "indirizzamento IP", "cambio provider"]', 'Quale affermazione sui vantaggi del NAT e'' corretta riguardo al cambio di ISP?', '[{"text": "Si puo'' cambiare ISP senza modificare gli indirizzi dei dispositivi nella rete locale", "is_correct": true}, {"text": "Il cambio di ISP e'' impossibile se si utilizza il NAT", "is_correct": false}, {"text": "Il cambio di ISP richiede sempre la riconfigurazione di tutti gli host interni", "is_correct": false}, {"text": "Il NAT assegna automaticamente un nuovo indirizzo pubblico a ogni host interno dopo il cambio", "is_correct": false}]', '{"right_answer": {"selected_answer": "Si puo'' cambiare ISP senza modificare gli indirizzi dei dispositivi nella rete locale", "rationale": "Il materiale elenca tra le motivazioni del NAT: ''can change ISP without changing addresses of devices in local network''. Poiche'' solo l''indirizzo pubblico del router NAT cambia, gli indirizzi privati interni restano invariati.", "original_reference": {"path": "materiale/BONONI - Sicurezza delle reti e dei sistemi/NAT-IPv4.pdf", "page": 2}}, "wrong_answers": [{"answer_text": "Il cambio di ISP richiede sempre la riconfigurazione di tutti gli host interni", "interpretation": "Nega il vantaggio fondamentale del NAT nella gestione degli indirizzi", "why_wrong": "E'' proprio il contrario: il NAT disaccoppia gli indirizzi interni da quelli esterni, rendendo il cambio di ISP trasparente per la rete locale."}, {"answer_text": "Il NAT assegna automaticamente un nuovo indirizzo pubblico a ogni host interno dopo il cambio", "interpretation": "Fraintende il funzionamento del NAT pensando che assegni IP pubblici a ogni host", "why_wrong": "Il NAT non assegna indirizzi pubblici agli host interni. Gli host interni mantengono i propri indirizzi privati; solo il router NAT ha l''indirizzo pubblico."}, {"answer_text": "Il cambio di ISP e'' impossibile se si utilizza il NAT", "interpretation": "Afferma il contrario della realta''", "why_wrong": "Il NAT facilita il cambio di ISP, non lo impedisce. Basta aggiornare l''IP pubblico sul router NAT, senza toccare la configurazione interna."}]}', '[]');
INSERT INTO questions (batch_id, id, curriculum_area, lecturer, topic, clusters, cognitive_level, question_type, distractor_strategy, difficulty, source_file, source_file_page_number, topics, question_prompt, choices, explanation, related_questions) VALUES (5, 'FORONI_2_Fondamenti_easy_005', 'MOD. 4 - Implementazione e verifica della sicurezza delle reti e dei sistemi', 'FORONI', 'Livelli di sicurezza logica', '["sicurezza perimetrale", "sicurezza endpoint", "sicurezza applicativa"]', 'understand', 'conceptual', 'category_confusion', 'easy', 'materiale/FORONI/Lezioni - Implementazione e verifica della sicurezza delle reti e dei sistemi/2.Fondamenti Cybersecurity .pdf', 12, '["sicurezza perimetrale", "sicurezza endpoint", "sicurezza applicativa", "livelli"]', 'Quali sono i tre livelli di sicurezza logica (non fisica) presentati nel materiale?', '[{"text": "Sicurezza del database, sicurezza del cloud e sicurezza mobile", "is_correct": false}, {"text": "Sicurezza perimetrale, sicurezza degli endpoint e sicurezza applicativa", "is_correct": true}, {"text": "Sicurezza hardware, sicurezza firmware e sicurezza BIOS", "is_correct": false}, {"text": "Sicurezza dell''edificio, sicurezza anti-incendio e sicurezza anti-allagamento", "is_correct": false}]', '{"right_answer": {"selected_answer": "Sicurezza perimetrale, sicurezza degli endpoint e sicurezza applicativa", "rationale": "Il materiale presenta tre livelli di sicurezza logica: sicurezza perimetrale (01), sicurezza degli endpoint (02) e sicurezza applicativa (03).", "original_reference": {"path": "materiale/FORONI/Lezioni - Implementazione e verifica della sicurezza delle reti e dei sistemi/2.Fondamenti Cybersecurity .pdf", "page": 12}}, "wrong_answers": [{"answer_text": "Sicurezza dell''edificio, sicurezza anti-incendio e sicurezza anti-allagamento", "interpretation": "Confonde i livelli di sicurezza logica con i componenti della sicurezza fisica", "why_wrong": "Questi sono componenti della sicurezza fisica, non della sicurezza logica."}, {"answer_text": "Sicurezza del database, sicurezza del cloud e sicurezza mobile", "interpretation": "Inventa categorie non presenti nel materiale", "why_wrong": "Queste categorie non sono presentate nel materiale come i tre livelli di sicurezza logica."}, {"answer_text": "Sicurezza hardware, sicurezza firmware e sicurezza BIOS", "interpretation": "Confonde i livelli logici con componenti hardware di basso livello", "why_wrong": "Questi sono livelli hardware/firmware, non i tre livelli di sicurezza logica definiti nel materiale."}]}', '["FORONI_2_Fondamenti_easy_004"]');
INSERT INTO questions (batch_id, id, curriculum_area, lecturer, topic, clusters, cognitive_level, question_type, distractor_strategy, difficulty, source_file, source_file_page_number, topics, question_prompt, choices, explanation, related_questions) VALUES (5, 'BERNABEI_06_GroupPolicy_easy_002', 'MOD. 3 - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft', 'BERNABEI', 'Client-Side Extensions', '["Group Policy", "CSE"]', 'understand', 'concept', 'partial_knowledge', 'easy', 'materiale/BERNABEI - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft/Slide/06.Implementare le Group Policy.pptx', 9, '["CSE", "Security CSE", "pull model", "cache"]', 'Ogni quanto tempo la Security CSE riapplica comunque le impostazioni, anche senza modifiche alla GPO?', '[{"text": "Solo al riavvio del computer", "is_correct": false}, {"text": "Ogni 90 minuti", "is_correct": false}, {"text": "Ogni 16 ore", "is_correct": true}]', '{"right_answer": {"selected_answer": "Ogni 16 ore", "rationale": "La slide 9 specifica che la Security CSE riapplica comunque le modifiche ogni 16 ore, a differenza delle altre CSE che applicano solo se la GPO e stata modificata.", "original_reference": {"path": "materiale/BERNABEI - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft/Slide/06.Implementare le Group Policy.pptx", "page": 9}}, "wrong_answers": [{"answer_text": "Ogni 90 minuti", "interpretation": "Confusione con il ciclo di refresh standard", "why_wrong": "90 minuti (piu un offset casuale fino a 30 min) e l''intervallo di refresh generico delle GPO, non il ciclo specifico della Security CSE."}, {"answer_text": "Solo al riavvio del computer", "interpretation": "La Security CSE riapplica solo allo startup", "why_wrong": "La Security CSE riapplica le impostazioni ogni 16 ore indipendentemente dal riavvio, garantendo cosi il ripristino di eventuali modifiche manuali."}]}', '["BERNABEI_06_GroupPolicy_easy_003", "BERNABEI_06_GroupPolicy_medium_005"]');
INSERT INTO questions (batch_id, id, curriculum_area, lecturer, topic, clusters, cognitive_level, question_type, distractor_strategy, difficulty, source_file, source_file_page_number, topics, question_prompt, choices, explanation, related_questions) VALUES (5, 'ANZALONE_1a_virtualIntro_medium_007', 'MOD. 5 - Cloud privato e cloud pubblico al servizio della business continuity e disaster recovery', 'ANZALONE', 'Limitazioni della virtualizzazione - casi improbabili', '["Virtualizzazione e Hypervisor"]', 'apply', 'best_practice', 'common_misconception', 'medium', 'materiale/ANZALONE - Cloud privato e cloud pubblico al servizio della business continuity e disaster recovery/1a_virtualIntro.pdf', 120, '["limitazioni", "telefonia", "hypervisor nested", "licensing", "virtualizzazione improbabile"]', 'In quali scenari la virtualizzazione e'' considerata improbabile o sconsigliata secondo le slide del corso?', '[{"text": "Qualsiasi applicazione web, database relazionali e server di posta elettronica", "is_correct": false}, {"text": "Tutti i sistemi operativi Linux, che non supportano nativamente la virtualizzazione", "is_correct": false}, {"text": "Applicazioni di telefonia con adattatori telco, installazione di un hypervisor dentro una VM, e software non supportato su piattaforme virtualizzate", "is_correct": true}, {"text": "Solo applicazioni grafiche 3D che richiedono GPU dedicate", "is_correct": false}]', '{"right_answer": {"selected_answer": "Applicazioni di telefonia con adattatori telco, installazione di un hypervisor dentro una VM, e software non supportato su piattaforme virtualizzate", "rationale": "Le slide identificano tre scenari in cui la virtualizzazione e'' improbabile: 1) applicazioni di telefonia con adattatori telco che non si mappano alle VM, 2) installare un altro hypervisor dentro una VM (nested virtualization) che funziona male, 3) software vendor che non supportano il proprio prodotto in ambienti virtualizzati. A questi si aggiungono problemi di licensing specifici.", "original_reference": {"path": "materiale/ANZALONE - Cloud privato e cloud pubblico al servizio della business continuity e disaster recovery/1a_virtualIntro.pdf", "page": 120}}, "wrong_answers": [{"answer_text": "Qualsiasi applicazione web, database relazionali e server di posta elettronica", "interpretation": "Questa risposta elenca workload che sono ottimi candidati per la virtualizzazione", "why_wrong": "Applicazioni web, database e server email sono tra i workload piu'' comunemente virtualizzati. Non rientrano tra gli scenari improbabili."}, {"answer_text": "Solo applicazioni grafiche 3D che richiedono GPU dedicate", "interpretation": "Questa risposta e'' troppo restrittiva e parzialmente superata", "why_wrong": "Sebbene le applicazioni grafiche intensive possano avere limitazioni, con GPU passthrough e vGPU la virtualizzazione e'' possibile. Le slide citano scenari specifici diversi."}, {"answer_text": "Tutti i sistemi operativi Linux, che non supportano nativamente la virtualizzazione", "interpretation": "Questa risposta contiene un''affermazione completamente falsa", "why_wrong": "Linux supporta nativamente la virtualizzazione tramite KVM integrato nel kernel. La maggior parte delle soluzioni di virtualizzazione (Xen, KVM, Proxmox) sono basate su Linux."}]}', '[]');
INSERT INTO questions (batch_id, id, curriculum_area, lecturer, topic, clusters, cognitive_level, question_type, distractor_strategy, difficulty, source_file, source_file_page_number, topics, question_prompt, choices, explanation, related_questions) VALUES (5, 'FORONI_07_GatheringInfo_medium_001', 'MOD. 4 - Implementazione e verifica della sicurezza delle reti e dei sistemi', 'FORONI', 'Sette passi della metodologia di information gathering', '["information gathering", "footprinting", "network range", "OS detection"]', 'understand', 'conceptual', 'order_scramble', 'medium', 'materiale/FORONI/Lezioni - Implementazione e verifica della sicurezza delle reti e dei sistemi/7.Gathering Target Information.pdf', 7, '["sette passi", "footprinting", "network range", "active machines", "open ports", "OS detection", "services", "network mapping"]', 'Nella metodologia di information gathering, quale attivita'' viene eseguita nei primi due dei sette passi logici?', '[{"text": "Footprinting: raccolta delle informazioni iniziali e localizzazione del range di rete", "is_correct": true}, {"text": "Mappatura della rete e identificazione dei servizi sulle porte", "is_correct": false}, {"text": "Scanning delle porte e rilevamento del sistema operativo", "is_correct": false}, {"text": "Sfruttamento delle vulnerabilita'' e mantenimento dell''accesso", "is_correct": false}]', '{"right_answer": {"selected_answer": "Footprinting: raccolta delle informazioni iniziali e localizzazione del range di rete", "rationale": "Il materiale spiega a pagina 6-7 che ''Footprinting is performed during the first two steps of unearthing initial information and locating the network range''.", "original_reference": {"path": "materiale/FORONI/Lezioni - Implementazione e verifica della sicurezza delle reti e dei sistemi/7.Gathering Target Information.pdf", "page": 7}}, "wrong_answers": [{"answer_text": "Scanning delle porte e rilevamento del sistema operativo", "interpretation": "Indica passi successivi della metodologia", "why_wrong": "La scansione delle porte (passo 4) e il rilevamento del sistema operativo (passo 5) avvengono dopo il footprinting. I primi due passi sono la raccolta iniziale e il network range."}, {"answer_text": "Mappatura della rete e identificazione dei servizi sulle porte", "interpretation": "Indica gli ultimi passi della metodologia", "why_wrong": "La mappatura della rete (passo 7) e l''identificazione dei servizi (passo 6) sono gli ultimi passi. Il footprinting (passi 1-2) li precede."}, {"answer_text": "Sfruttamento delle vulnerabilita'' e mantenimento dell''accesso", "interpretation": "Confonde la raccolta informazioni con le fasi di attacco", "why_wrong": "Lo sfruttamento delle vulnerabilita'' e il mantenimento dell''accesso sono fasi di attacco successive. L''information gathering e'' interamente dedicata alla raccolta di informazioni."}]}', '[]');
INSERT INTO questions (batch_id, id, curriculum_area, lecturer, topic, clusters, cognitive_level, question_type, distractor_strategy, difficulty, source_file, source_file_page_number, topics, question_prompt, choices, explanation, related_questions) VALUES (5, 'FORONI_09_DeepWeb_very_easy_001', 'MOD. 4 - Implementazione e verifica della sicurezza delle reti e dei sistemi', 'FORONI', 'Definizione di deep web', '["deep web", "motori di ricerca", "indicizzazione"]', 'remember', 'definition', 'category_confusion', 'very_easy', 'materiale/FORONI/Lezioni - Implementazione e verifica della sicurezza delle reti e dei sistemi/9. il_deep_web.pdf', 2, '["deep web", "contenuti non indicizzati", "motori di ricerca"]', 'Cosa si intende per ''deep web''?', '[{"text": "La parte di Internet accessibile solo tramite connessioni a banda larga", "is_correct": false}, {"text": "Un tipo speciale di browser per navigare in modo piu'' veloce", "is_correct": false}, {"text": "Un sistema di archiviazione cloud per documenti aziendali riservati", "is_correct": false}, {"text": "L''insieme dei contenuti su Internet che non sono indicizzati dai motori di ricerca", "is_correct": true}]', '{"right_answer": {"selected_answer": "L''insieme dei contenuti su Internet che non sono indicizzati dai motori di ricerca", "rationale": "Il materiale definisce a pagina 2 il deep web come ''una classe di contenuti su Internet che, per diversi motivi tecnici, non e'' indicizzato dai motori di ricerca''.", "original_reference": {"path": "materiale/FORONI/Lezioni - Implementazione e verifica della sicurezza delle reti e dei sistemi/9. il_deep_web.pdf", "page": 2}}, "wrong_answers": [{"answer_text": "Un tipo speciale di browser per navigare in modo piu'' veloce", "interpretation": "Confonde il deep web con un software di navigazione", "why_wrong": "Il deep web non e'' un browser ma una porzione di Internet. I browser come TOR possono accedere al deep web, ma il deep web stesso e'' un insieme di contenuti."}, {"answer_text": "La parte di Internet accessibile solo tramite connessioni a banda larga", "interpretation": "Confonde l''accessibilita'' tecnica con la non-indicizzazione", "why_wrong": "L''accesso al deep web non dipende dalla velocita'' della connessione. Il deep web e'' definito dalla mancata indicizzazione da parte dei motori di ricerca."}, {"answer_text": "Un sistema di archiviazione cloud per documenti aziendali riservati", "interpretation": "Confonde il deep web con servizi cloud specifici", "why_wrong": "Il deep web non e'' un servizio cloud. E'' l''insieme di tutti i contenuti Internet non indicizzati, che include molte tipologie diverse di risorse."}]}', '[]');
INSERT INTO questions (batch_id, id, curriculum_area, lecturer, topic, clusters, cognitive_level, question_type, distractor_strategy, difficulty, source_file, source_file_page_number, topics, question_prompt, choices, explanation, related_questions) VALUES (5, 'ANZALONE_4a_storage_easy_002', 'MOD. 5 - Cloud privato e cloud pubblico al servizio della business continuity e disaster recovery', 'ANZALONE', 'Snapshot in VMware', '["Storage", "VMware vSphere", "Protezione dati"]', 'understand', 'concept', 'common_misconception', 'easy', 'materiale/ANZALONE - Cloud privato e cloud pubblico al servizio della business continuity e disaster recovery/4a_storage.pdf', 72, '["snapshot", "VMware", "virtual machine", "delta disk"]', 'Che cos''e'' uno snapshot in VMware e a cosa serve?', '[{"text": "Una copia dello stato della VM in un dato momento, utile per tornare a uno stato precedente", "is_correct": true}, {"text": "Un backup completo della VM che duplica tutti i file su un disco esterno", "is_correct": false}, {"text": "Un''immagine fotografica dello schermo della VM salvata come file PNG", "is_correct": false}, {"text": "Un tool per monitorare le prestazioni della CPU in tempo reale", "is_correct": false}]', '{"right_answer": {"selected_answer": "Una copia dello stato della VM in un dato momento, utile per tornare a uno stato precedente", "rationale": "Uno snapshot VMware cattura lo stato della VM (dati su tutti i dischi virtuali e stato di alimentazione) in un dato istante. Crea un delta disk che registra solo le modifiche successive, permettendo di tornare rapidamente allo stato precedente.", "original_reference": {"path": "materiale/ANZALONE - Cloud privato e cloud pubblico al servizio della business continuity e disaster recovery/4a_storage.pdf", "page": 72}}, "wrong_answers": [{"answer_text": "Un backup completo della VM che duplica tutti i file su un disco esterno", "interpretation": "Confonde snapshot con backup completo", "why_wrong": "Uno snapshot NON e'' un backup completo. Non duplica tutti i file, ma crea un delta disk che registra solo le modifiche. VMware stessa avverte che gli snapshot non dovrebbero sostituire i backup."}, {"answer_text": "Un''immagine fotografica dello schermo della VM salvata come file PNG", "interpretation": "Interpreta letteralmente il termine ''snapshot'' come fotografia", "why_wrong": "Il termine ''snapshot'' in ambito VMware non indica uno screenshot, ma una cattura dello stato completo della macchina virtuale (dischi, memoria, configurazione)."}, {"answer_text": "Un tool per monitorare le prestazioni della CPU in tempo reale", "interpretation": "Confonde snapshot con uno strumento di monitoring", "why_wrong": "Uno snapshot non e'' uno strumento di monitoraggio. E'' un meccanismo per catturare e preservare lo stato di una VM."}]}', '["ANZALONE_4a_storage_easy_003"]');
INSERT INTO questions (batch_id, id, curriculum_area, lecturer, topic, clusters, cognitive_level, question_type, distractor_strategy, difficulty, source_file, source_file_page_number, topics, question_prompt, choices, explanation, related_questions) VALUES (5, 'ANZALONE_2a_introVmware_easy_003', 'MOD. 5 - Cloud privato e cloud pubblico al servizio della business continuity e disaster recovery', 'ANZALONE', 'Hardware Compatibility List (HCL)', '["Infrastruttura VMware vSphere"]', 'remember', 'best_practice', 'obviously_wrong', 'easy', 'materiale/ANZALONE - Cloud privato e cloud pubblico al servizio della business continuity e disaster recovery/2a_introVmware.pdf', 15, '["HCL", "compatibilità hardware", "server platform"]', 'Quale strumento fornisce VMware per verificare la compatibilità dell''hardware con ESXi prima del deployment?', '[{"text": "Lo speed test della connessione Internet del datacenter", "is_correct": false}, {"text": "La Hardware Compatibility List (HCL) disponibile sul sito VMware", "is_correct": true}, {"text": "Il benchmark grafico della scheda video del server", "is_correct": false}, {"text": "Il VMware Fusion Player per test su workstation Mac", "is_correct": false}]', '{"right_answer": {"selected_answer": "La Hardware Compatibility List (HCL) disponibile sul sito VMware", "rationale": "VMware fornisce una Hardware Compatibility List (HCL) consultabile online per verificare che l''hardware sia testato e supportato con ESXi, garantendo il funzionamento atteso.", "original_reference": {"path": "materiale/ANZALONE - Cloud privato e cloud pubblico al servizio della business continuity e disaster recovery/2a_introVmware.pdf", "page": 15}}, "wrong_answers": [{"answer_text": "Il VMware Fusion Player per test su workstation Mac", "interpretation": "Software di virtualizzazione desktop", "why_wrong": "VMware Fusion è un prodotto di virtualizzazione per macOS, non uno strumento per verificare la compatibilità hardware dei server con ESXi."}, {"answer_text": "Lo speed test della connessione Internet del datacenter", "interpretation": "Test di velocità di rete", "why_wrong": "Uno speed test misura la banda Internet, non verifica la compatibilità dei componenti hardware con l''hypervisor ESXi."}, {"answer_text": "Il benchmark grafico della scheda video del server", "interpretation": "Test prestazioni GPU", "why_wrong": "Le prestazioni grafiche non sono rilevanti per la compatibilità con ESXi. La HCL verifica storage controller, NIC e piattaforme server certificate."}]}', '[]');
INSERT INTO questions (batch_id, id, curriculum_area, lecturer, topic, clusters, cognitive_level, question_type, distractor_strategy, difficulty, source_file, source_file_page_number, topics, question_prompt, choices, explanation, related_questions) VALUES (5, 'ANZALONE_2b_ft_bc_very_easy_001', 'MOD. 5 - Cloud privato e cloud pubblico al servizio della business continuity e disaster recovery', 'ANZALONE', 'Definizione di Business Continuity', '["Business Continuity e Disaster Recovery"]', 'remember', 'definition', 'obviously_wrong', 'very_easy', 'materiale/ANZALONE - Cloud privato e cloud pubblico al servizio della business continuity e disaster recovery/2b_ft_bc.pdf', 3, '["business continuity", "definizione"]', 'Qual è l''obiettivo principale della Business Continuity in un''organizzazione?', '[{"text": "Sviluppare nuove applicazioni software per il mercato consumer", "is_correct": false}, {"text": "Ridurre il numero di dipendenti necessari per gestire l''infrastruttura IT", "is_correct": false}, {"text": "Garantire che le funzioni essenziali possano continuare durante e dopo un disastro", "is_correct": true}, {"text": "Aumentare il fatturato annuale dell''azienda ottimizzando i costi di produzione", "is_correct": false}]', '{"right_answer": {"selected_answer": "Garantire che le funzioni essenziali possano continuare durante e dopo un disastro", "rationale": "La Business Continuity descrive i processi e le procedure che un''organizzazione mette in atto per garantire che le funzioni essenziali possano continuare durante e dopo un disastro, prevenendo l''interruzione dei servizi mission-critical e ristabilendo il pieno funzionamento il più rapidamente possibile.", "original_reference": {"path": "materiale/ANZALONE - Cloud privato e cloud pubblico al servizio della business continuity e disaster recovery/2b_ft_bc.pdf", "page": 3}}, "wrong_answers": [{"answer_text": "Aumentare il fatturato annuale dell''azienda ottimizzando i costi di produzione", "interpretation": "Obiettivo commerciale generico", "why_wrong": "La Business Continuity non riguarda l''aumento del fatturato, ma la continuità operativa delle funzioni critiche in caso di disastro."}, {"answer_text": "Ridurre il numero di dipendenti necessari per gestire l''infrastruttura IT", "interpretation": "Riduzione del personale", "why_wrong": "La Business Continuity non ha come scopo la riduzione del personale, bensì la protezione delle funzioni essenziali dell''organizzazione."}, {"answer_text": "Sviluppare nuove applicazioni software per il mercato consumer", "interpretation": "Sviluppo software", "why_wrong": "Lo sviluppo software è un''attività completamente diversa dalla Business Continuity, che si concentra sulla resilienza operativa."}]}', '["ANZALONE_2b_ft_bc_easy_001"]');
INSERT INTO questions (batch_id, id, curriculum_area, lecturer, topic, clusters, cognitive_level, question_type, distractor_strategy, difficulty, source_file, source_file_page_number, topics, question_prompt, choices, explanation, related_questions) VALUES (5, 'BERNABEI_07_SicurezzaGPO_easy_004', 'MOD. 3 - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft', 'BERNABEI', 'Password Policy - valori predefiniti', '["Group Policy", "Password Policy", "Sicurezza Account"]', 'remember', 'definition', 'close_value', 'easy', 'materiale/BERNABEI - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft/Slide/07.Gestire la sicurezza e le configurazioni tramite le Group Policy Settings.pptx', 19, '["password history", "max password age", "min length", "complexity"]', 'Qual e il valore predefinito di ''Enforce password history'' nella Default Domain Policy?', '[{"text": "12 password ricordate", "is_correct": false}, {"text": "5 password ricordate", "is_correct": false}, {"text": "24 password ricordate", "is_correct": true}]', '{"right_answer": {"selected_answer": "24 password ricordate", "rationale": "La slide 19 elenca i valori predefiniti delle Password Policies, tra cui Enforce password history impostato a 24, che impedisce il riutilizzo delle ultime 24 password.", "original_reference": {"path": "materiale/BERNABEI - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft/Slide/07.Gestire la sicurezza e le configurazioni tramite le Group Policy Settings.pptx", "page": 19}}, "wrong_answers": [{"answer_text": "12 password ricordate", "interpretation": "Un valore intermedio plausibile", "why_wrong": "Il valore predefinito non e 12 ma 24 password. La cronologia di 24 password offre una protezione piu robusta contro il riutilizzo."}, {"answer_text": "5 password ricordate", "interpretation": "Un valore basso di cronologia", "why_wrong": "Il valore predefinito e significativamente piu alto: 24 password. Un valore di 5 sarebbe troppo basso per prevenire efficacemente il riutilizzo delle password."}]}', '["BERNABEI_07_SicurezzaGPO_easy_005", "BERNABEI_07_SicurezzaGPO_medium_003"]');
INSERT INTO questions (batch_id, id, curriculum_area, lecturer, topic, clusters, cognitive_level, question_type, distractor_strategy, difficulty, source_file, source_file_page_number, topics, question_prompt, choices, explanation, related_questions) VALUES (5, 'FORONI_08_NormativeSicurezza_easy_001', 'MOD. 4 - Implementazione e verifica della sicurezza delle reti e dei sistemi', 'FORONI', 'Articolo 12 della Dichiarazione Universale dei Diritti dell''Uomo', '["privacy", "diritti umani", "storia della privacy"]', 'remember', 'factual', 'article_number_confusion', 'easy', 'materiale/FORONI/Lezioni - Implementazione e verifica della sicurezza delle reti e dei sistemi/8.Informativa normative sulla sicurezza dei dati.pdf', 5, '["Dichiarazione Universale", "articolo 12", "vita privata"]', 'Quale articolo della Dichiarazione Universale dei Diritti dell''Uomo (1948) sancisce il diritto alla protezione della vita privata?', '[{"text": "Articolo 1", "is_correct": false}, {"text": "Articolo 21", "is_correct": false}, {"text": "Articolo 12", "is_correct": true}, {"text": "Articolo 8", "is_correct": false}]', '{"right_answer": {"selected_answer": "Articolo 12", "rationale": "L''articolo 12 della Dichiarazione Universale dei Diritti dell''Uomo stabilisce che nessun individuo potra'' essere sottoposto a interferenze arbitrarie nella sua vita privata, famiglia, domicilio o corrispondenza.", "original_reference": {"path": "materiale/FORONI/Lezioni - Implementazione e verifica della sicurezza delle reti e dei sistemi/8.Informativa normative sulla sicurezza dei dati.pdf", "page": 5}}, "wrong_answers": [{"answer_text": "Articolo 8", "interpretation": "Confonde con l''articolo 8 della CEDU (Convenzione Europea dei Diritti dell''Uomo)", "why_wrong": "L''articolo 8 e'' quello della CEDU che tutela la vita privata, non della Dichiarazione Universale."}, {"answer_text": "Articolo 1", "interpretation": "Confonde con l''articolo sulla dignita'' e uguaglianza", "why_wrong": "L''articolo 1 riguarda la liberta'' e l''uguaglianza in dignita'' e diritti, non la privacy."}, {"answer_text": "Articolo 21", "interpretation": "Confonde con l''articolo sul diritto di partecipazione politica", "why_wrong": "L''articolo 21 riguarda il diritto di partecipare al governo del proprio paese, non la vita privata."}]}', '[]');
INSERT INTO questions (batch_id, id, curriculum_area, lecturer, topic, clusters, cognitive_level, question_type, distractor_strategy, difficulty, source_file, source_file_page_number, topics, question_prompt, choices, explanation, related_questions) VALUES (5, 'GUIDAZZOLI_file_access_medium_002', 'MOD. 3 - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft', 'GUIDAZZOLI', 'Compressione NTFS e spazio disco', '["NTFS", "Compressione", "Gestione disco"]', 'understand', 'concept', 'partial_truth', 'medium', 'materiale/GUIDAZZOLI - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft/File Access and Printers.ppt', 27, '["compressione NTFS", "spazio disco", "dimensione file", "attributo NTFS"]', 'Come calcola Windows lo spazio disco occupato da un file compresso con la compressione NTFS?', '[{"text": "Non e'' possibile calcolare lo spazio di un file compresso NTFS", "is_correct": false}, {"text": "In base alla dimensione compressa effettiva su disco", "is_correct": false}, {"text": "In base alla dimensione del file moltiplicata per due", "is_correct": false}, {"text": "In base alla dimensione non compressa del file originale", "is_correct": true}]', '{"right_answer": {"selected_answer": "In base alla dimensione non compressa del file originale", "rationale": "Il materiale afferma: ''NTFS calculates disk space based on uncompressed file size''. Inoltre, ''Applications that open a compressed file only see the uncompressed data''.", "original_reference": {"path": "materiale/GUIDAZZOLI - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft/File Access and Printers.ppt", "page": 27}}, "wrong_answers": [{"answer_text": "In base alla dimensione compressa effettiva su disco", "interpretation": "Risposta plausibile ma contraria a quanto indicato nel materiale", "why_wrong": "Anche se sembrerebbe logico, il materiale afferma che NTFS calcola lo spazio basandosi sulla dimensione non compressa."}, {"answer_text": "Non e'' possibile calcolare lo spazio di un file compresso NTFS", "interpretation": "Assume una limitazione tecnica inesistente", "why_wrong": "Windows calcola normalmente lo spazio dei file compressi, semplicemente lo basa sulla dimensione non compressa."}, {"answer_text": "In base alla dimensione del file moltiplicata per due", "interpretation": "Inventa una formula di calcolo inesistente", "why_wrong": "Non esiste alcuna moltiplicazione. Il calcolo si basa sulla dimensione originale non compressa del file."}]}', '["GUIDAZZOLI_file_access_medium_003"]');
INSERT INTO questions (batch_id, id, curriculum_area, lecturer, topic, clusters, cognitive_level, question_type, distractor_strategy, difficulty, source_file, source_file_page_number, topics, question_prompt, choices, explanation, related_questions) VALUES (5, 'BONONI_NetworkSecurity_medium_002', 'MOD. 2 - Sicurezza delle reti e dei sistemi', 'BONONI', 'Protocolli di autenticazione - evoluzione', '["Autenticazione"]', 'understand', 'conceptual', 'protocol_version_confusion', 'medium', 'materiale/BONONI - Sicurezza delle reti e dei sistemi/LBononi_IFTS_FAV_3_ NetworkSecurity_Bononi_IFTS2015.pdf', 32, '["nonce", "playback attack", "ap4.0", "autenticazione"]', 'Nel protocollo di autenticazione ap4.0, quale meccanismo viene introdotto per prevenire il playback attack?', '[{"text": "L''invio della password cifrata con un algoritmo a chiave pubblica", "is_correct": false}, {"text": "Un nonce (numero usato una sola volta) che Alice deve restituire cifrato con la chiave condivisa", "is_correct": true}, {"text": "La verifica dell''indirizzo IP del mittente tramite reverse DNS", "is_correct": false}, {"text": "L''utilizzo di un timestamp firmato digitalmente", "is_correct": false}]', '{"right_answer": {"selected_answer": "Un nonce (numero usato una sola volta) che Alice deve restituire cifrato con la chiave condivisa", "rationale": "Il protocollo ap4.0 introduce il nonce: Bob invia ad Alice un numero R usato una sola volta nella vita. Alice deve restituire R cifrato con la chiave simmetrica condivisa K_AB. Questo prova che Alice e'' ''live'' (non e'' un replay) e conosce la chiave segreta.", "original_reference": {"path": "materiale/BONONI - Sicurezza delle reti e dei sistemi/LBononi_IFTS_FAV_3_ NetworkSecurity_Bononi_IFTS2015.pdf", "page": 32}}, "wrong_answers": [{"answer_text": "L''invio della password cifrata con un algoritmo a chiave pubblica", "interpretation": "Lo studente descrive il protocollo ap3.1, non ap4.0", "why_wrong": "L''invio della password cifrata (ap3.1) e'' vulnerabile al playback attack: Trudy puo'' registrare e riprodurre il pacchetto cifrato."}, {"answer_text": "L''utilizzo di un timestamp firmato digitalmente", "interpretation": "Lo studente confonde con un meccanismo alternativo non trattato", "why_wrong": "Il materiale descrive l''uso del nonce, non dei timestamp. Il nonce e'' un numero usato una sola volta, non un marcatore temporale."}, {"answer_text": "La verifica dell''indirizzo IP del mittente tramite reverse DNS", "interpretation": "Lo studente propone una verifica gia'' dimostrata vulnerabile (ap2.0)", "why_wrong": "La verifica dell''IP e'' il meccanismo del protocollo ap2.0, gia'' dimostrato vulnerabile all''IP spoofing."}]}', '["BONONI_NetworkSecurity_hard_002"]');
