INSERT INTO questions (batch_id, id, curriculum_area, lecturer, topic, clusters, cognitive_level, question_type, distractor_strategy, difficulty, source_file, source_file_page_number, topics, question_prompt, choices, explanation, related_questions) VALUES (6, 'FORONI_08_NormativeSicurezza_very_easy_001', 'MOD. 4 - Implementazione e verifica della sicurezza delle reti e dei sistemi', 'FORONI', 'Data di applicazione del GDPR', '["GDPR", "normativa privacy", "regolamento europeo"]', 'remember', 'factual', 'date_confusion', 'very_easy', 'materiale/FORONI/Lezioni - Implementazione e verifica della sicurezza delle reti e dei sistemi/8.Informativa normative sulla sicurezza dei dati.pdf', 12, '["GDPR", "25 maggio 2018", "regolamento europeo"]', 'A partire da quale data il GDPR (General Data Protection Regulation) e'' divenuto pienamente applicabile in tutti gli Stati membri dell''UE?', '[{"text": "25 maggio 2020", "is_correct": false}, {"text": "1 gennaio 2016", "is_correct": false}, {"text": "1 giugno 2017", "is_correct": false}, {"text": "25 maggio 2018", "is_correct": true}]', '{"right_answer": {"selected_answer": "25 maggio 2018", "rationale": "Il GDPR, approvato nel 2016, e'' divenuto pienamente applicabile il 25 maggio 2018 dopo un periodo transitorio di due anni, come indicato nel materiale.", "original_reference": {"path": "materiale/FORONI/Lezioni - Implementazione e verifica della sicurezza delle reti e dei sistemi/8.Informativa normative sulla sicurezza dei dati.pdf", "page": 12}}, "wrong_answers": [{"answer_text": "1 gennaio 2016", "interpretation": "Confonde la data di approvazione con quella di piena applicabilita''", "why_wrong": "Nel 2016 il GDPR fu approvato dal Parlamento Europeo, ma la piena applicabilita'' decorse solo dal 25 maggio 2018."}, {"answer_text": "25 maggio 2020", "interpretation": "Errore sulla data, posticipata di due anni", "why_wrong": "Il 2020 non corrisponde ad alcuna scadenza GDPR; la data corretta e'' il 25 maggio 2018."}, {"answer_text": "1 giugno 2017", "interpretation": "Data inventata plausibile nel periodo di transizione", "why_wrong": "Nessuna milestone GDPR corrisponde a questa data. Il regolamento e'' divenuto applicabile il 25 maggio 2018."}]}', '["FORONI_08_NormativeSicurezza_easy_002"]');
INSERT INTO questions (batch_id, id, curriculum_area, lecturer, topic, clusters, cognitive_level, question_type, distractor_strategy, difficulty, source_file, source_file_page_number, topics, question_prompt, choices, explanation, related_questions) VALUES (6, 'ANZALONE_2a_introVmware_easy_001', 'MOD. 5 - Cloud privato e cloud pubblico al servizio della business continuity e disaster recovery', 'ANZALONE', 'VMkernel - funzioni core', '["Virtualizzazione e Hypervisor"]', 'remember', 'definition', 'obviously_wrong', 'easy', 'materiale/ANZALONE - Cloud privato e cloud pubblico al servizio della business continuity e disaster recovery/2a_introVmware.pdf', 9, '["VMkernel", "CPU scheduling", "memory management"]', 'Quali funzioni principali svolge il VMkernel in ESXi per gestire l''accesso delle VM all''hardware fisico?', '[{"text": "Backup su nastro, archiviazione documentale e indicizzazione file", "is_correct": false}, {"text": "Rendering grafico, elaborazione audio e gestione stampanti", "is_correct": false}, {"text": "CPU scheduling, memory management e virtual switch data processing", "is_correct": true}, {"text": "Gestione email, calendario e rubrica contatti", "is_correct": false}]', '{"right_answer": {"selected_answer": "CPU scheduling, memory management e virtual switch data processing", "rationale": "Il VMkernel è il nucleo della virtualizzazione in ESXi e gestisce l''accesso delle VM all''hardware fisico fornendo CPU scheduling, memory management e virtual switch data processing.", "original_reference": {"path": "materiale/ANZALONE - Cloud privato e cloud pubblico al servizio della business continuity e disaster recovery/2a_introVmware.pdf", "page": 9}}, "wrong_answers": [{"answer_text": "Rendering grafico, elaborazione audio e gestione stampanti", "interpretation": "Funzioni multimediali e periferiche", "why_wrong": "Queste sono funzioni tipiche di un sistema operativo desktop, non di un hypervisor come ESXi che si occupa di virtualizzazione delle risorse server."}, {"answer_text": "Gestione email, calendario e rubrica contatti", "interpretation": "Funzioni di produttività", "why_wrong": "Queste sono funzioni di applicazioni di produttività, non di un kernel di virtualizzazione come il VMkernel."}, {"answer_text": "Backup su nastro, archiviazione documentale e indicizzazione file", "interpretation": "Funzioni di storage e archiviazione", "why_wrong": "Il VMkernel non gestisce backup o archiviazione documentale. Si occupa specificamente di schedulazione CPU, gestione memoria e networking virtuale."}]}', '["ANZALONE_2a_introVmware_very_easy_001"]');
INSERT INTO questions (batch_id, id, curriculum_area, lecturer, topic, clusters, cognitive_level, question_type, distractor_strategy, difficulty, source_file, source_file_page_number, topics, question_prompt, choices, explanation, related_questions) VALUES (6, 'BERNABEI_06b_Loopback_very_easy_001', 'MOD. 3 - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft', 'BERNABEI', 'Comportamento predefinito delle Group Policy', '["Group Policy", "Loopback Processing"]', 'remember', 'definition', 'common_misconception', 'very_easy', 'materiale/BERNABEI - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft/Slide/06b.Loopback policies.pptx', 5, '["Computer Configuration", "User Configuration", "OU"]', 'Nel comportamento predefinito delle GPO, da quale OU viene applicata la Computer Configuration?', '[{"text": "Dalla Default Domain Policy a livello di dominio", "is_correct": false}, {"text": "Dalla OU in cui si trova l''oggetto utente", "is_correct": false}, {"text": "Dalla OU in cui si trova l''oggetto computer", "is_correct": true}]', '{"right_answer": {"selected_answer": "Dalla OU in cui si trova l''oggetto computer", "rationale": "La slide 5 mostra che nel comportamento predefinito la Computer Configuration viene applicata dalla GPO linkate alla OU del computer, mentre la User Configuration dalla OU dell''utente.", "original_reference": {"path": "materiale/BERNABEI - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft/Slide/06b.Loopback policies.pptx", "page": 5}}, "wrong_answers": [{"answer_text": "Dalla OU in cui si trova l''oggetto utente", "interpretation": "La Computer Configuration segue la posizione dell''utente", "why_wrong": "La Computer Configuration segue sempre la posizione dell''oggetto computer in AD, non quella dell''utente. E'' la User Configuration che normalmente segue la OU dell''utente."}, {"answer_text": "Dalla Default Domain Policy a livello di dominio", "interpretation": "La Computer Configuration proviene solo dalla Default Domain Policy", "why_wrong": "La Default Domain Policy e solo una delle GPO applicabili. La Computer Configuration viene determinata dalla gerarchia di GPO linkate alla OU del computer, non solo dalla Default Domain Policy."}]}', '["BERNABEI_06b_Loopback_very_easy_002", "BERNABEI_06b_Loopback_easy_001"]');
INSERT INTO questions (batch_id, id, curriculum_area, lecturer, topic, clusters, cognitive_level, question_type, distractor_strategy, difficulty, source_file, source_file_page_number, topics, question_prompt, choices, explanation, related_questions) VALUES (6, 'ANZALONE_1a_virtualIntro_easy_003', 'MOD. 5 - Cloud privato e cloud pubblico al servizio della business continuity e disaster recovery', 'ANZALONE', 'Terminologia host e guest', '["Virtualizzazione e Hypervisor"]', 'remember', 'definition', 'obviously_wrong', 'easy', 'materiale/ANZALONE - Cloud privato e cloud pubblico al servizio della business continuity e disaster recovery/1a_virtualIntro.pdf', 20, '["host", "guest", "terminologia", "virtualizzazione"]', 'Nella terminologia della virtualizzazione, come vengono chiamati il server fisico e i server virtuali?', '[{"text": "Il server fisico e'' il client, i server virtuali sono i master", "is_correct": false}, {"text": "Il server fisico e'' il nodo, i server virtuali sono i cluster", "is_correct": false}, {"text": "Il server fisico e'' l''host, i server virtuali sono i guest", "is_correct": true}, {"text": "Il server fisico e'' il guest, i server virtuali sono gli host", "is_correct": false}]', '{"right_answer": {"selected_answer": "Il server fisico e'' l''host, i server virtuali sono i guest", "rationale": "Nella virtualizzazione, il server fisico e'' chiamato host (ospitante) e i server virtuali che vi girano sopra sono chiamati guest (ospiti). Ogni guest si comporta come una macchina fisica indipendente.", "original_reference": {"path": "materiale/ANZALONE - Cloud privato e cloud pubblico al servizio della business continuity e disaster recovery/1a_virtualIntro.pdf", "page": 20}}, "wrong_answers": [{"answer_text": "Il server fisico e'' il client, i server virtuali sono i master", "interpretation": "Questa risposta usa terminologia client-server e master-slave", "why_wrong": "Client e master sono termini di altre architetture (client-server, master-slave). Nella virtualizzazione i termini corretti sono host e guest."}, {"answer_text": "Il server fisico e'' il guest, i server virtuali sono gli host", "interpretation": "Questa risposta inverte i termini corretti", "why_wrong": "E'' l''esatto contrario: l''host e'' il server fisico che ospita, il guest e'' il server virtuale ospitato."}, {"answer_text": "Il server fisico e'' il nodo, i server virtuali sono i cluster", "interpretation": "Questa risposta usa terminologia del clustering", "why_wrong": "Nodo e cluster sono termini dell''architettura distribuita/cluster, non della virtualizzazione. I termini corretti sono host e guest."}]}', '[]');
INSERT INTO questions (batch_id, id, curriculum_area, lecturer, topic, clusters, cognitive_level, question_type, distractor_strategy, difficulty, source_file, source_file_page_number, topics, question_prompt, choices, explanation, related_questions) VALUES (6, 'BONONI_NAT_easy_003', 'MOD. 2 - Sicurezza delle reti e dei sistemi', 'BONONI', 'Connessioni simultanee NAT', '["NAT", "port number"]', 'remember', 'factual', 'magnitude_error', 'easy', 'materiale/BONONI - Sicurezza delle reti e dei sistemi/NAT-IPv4.pdf', 5, '["NAT", "porta 16-bit", "connessioni simultanee"]', 'Quante connessioni simultanee puo'' supportare approssimativamente un NAT con un singolo indirizzo IP pubblico?', '[{"text": "Illimitate, perche'' il NAT non ha vincoli sul numero di connessioni", "is_correct": false}, {"text": "Massimo 1.024, limitato alle well-known port", "is_correct": false}, {"text": "Circa 60.000, grazie al campo porta a 16 bit", "is_correct": true}, {"text": "Massimo 256, una per ogni valore dell''ultimo ottetto dell''IP", "is_correct": false}]', '{"right_answer": {"selected_answer": "Circa 60.000, grazie al campo porta a 16 bit", "rationale": "Il materiale indica che il campo porta a 16 bit consente ''60,000 simultaneous connections with a single LAN-side address''. Il valore teorico massimo di un campo a 16 bit e'' 65.536, ma circa 60.000 sono effettivamente utilizzabili.", "original_reference": {"path": "materiale/BONONI - Sicurezza delle reti e dei sistemi/NAT-IPv4.pdf", "page": 5}}, "wrong_answers": [{"answer_text": "Massimo 1.024, limitato alle well-known port", "interpretation": "Confonde il range delle well-known port (0-1023) con il limite del NAT", "why_wrong": "Le well-known port (0-1023) sono solo un sottoinsieme. Il NAT utilizza l''intero range a 16 bit per le porte, non solo le well-known."}, {"answer_text": "Massimo 256, una per ogni valore dell''ultimo ottetto dell''IP", "interpretation": "Confonde i numeri di porta con gli ottetti dell''indirizzo IP", "why_wrong": "Il numero di connessioni dipende dal campo porta (16 bit), non dagli ottetti dell''indirizzo IP. 256 sarebbe il range di un singolo ottetto a 8 bit."}, {"answer_text": "Illimitate, perche'' il NAT non ha vincoli sul numero di connessioni", "interpretation": "Ignora il vincolo fisico del campo porta a 16 bit", "why_wrong": "Il NAT ha un limite intrinseco dato dai 16 bit del campo porta. Non puo'' supportare connessioni illimitate con un singolo indirizzo IP."}]}', '[]');
INSERT INTO questions (batch_id, id, curriculum_area, lecturer, topic, clusters, cognitive_level, question_type, distractor_strategy, difficulty, source_file, source_file_page_number, topics, question_prompt, choices, explanation, related_questions) VALUES (6, 'BERNABEI_12_DominiForeste_medium_001', 'MOD. 3 - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft', 'BERNABEI', 'ADMT - Requisiti e gestione gruppi', '["ADMT", "Migrazione", "Gruppi", "Trust"]', 'understand', 'conceptual', 'plausible_but_wrong', 'medium', 'materiale/BERNABEI - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft/Slide/12.Gestire domini e foreste.pptx', 6, '["ADMT", "trust", "gruppi Global", "Universal", "sIDHistory"]', 'Nella migrazione intra-forest con ADMT, cosa accade ai gruppi Global quando vengono spostati?', '[{"text": "Vengono eliminati e ricreati come Domain Local nel dominio di destinazione", "is_correct": false}, {"text": "Rimangono Global e mantengono tutti i membri originali senza modifiche", "is_correct": false}, {"text": "Vengono creati come Universal, perché i gruppi Global possono contenere solo oggetti dello stesso dominio", "is_correct": true}]', '{"right_answer": {"selected_answer": "Vengono creati come Universal, perché i gruppi Global possono contenere solo oggetti dello stesso dominio", "rationale": "La slide 6 spiega che nella migrazione intra-forest i gruppi vengono spostati, ma i gruppi Global vengono creati come Universal. Questo perché i gruppi Global possono contenere soltanto oggetti dello stesso dominio, e durante la migrazione gli utenti potrebbero trovarsi in domini diversi.", "original_reference": {"path": "materiale/BERNABEI - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft/Slide/12.Gestire domini e foreste.pptx", "page": 6}}, "wrong_answers": [{"answer_text": "Rimangono Global e mantengono tutti i membri originali senza modifiche", "interpretation": "Nessuna conversione necessaria", "why_wrong": "I gruppi Global non possono rimanere Global dopo lo spostamento perché i loro membri potrebbero trovarsi ancora nel dominio sorgente. Vengono convertiti in Universal."}, {"answer_text": "Vengono eliminati e ricreati come Domain Local nel dominio di destinazione", "interpretation": "Conversione a Domain Local", "why_wrong": "I gruppi non vengono convertiti in Domain Local. Vengono convertiti in Universal per mantenere la compatibilità cross-domain durante la fase di migrazione."}]}', '["BERNABEI_12_DominiForeste_easy_001", "BERNABEI_12_DominiForeste_easy_003"]');
INSERT INTO questions (batch_id, id, curriculum_area, lecturer, topic, clusters, cognitive_level, question_type, distractor_strategy, difficulty, source_file, source_file_page_number, topics, question_prompt, choices, explanation, related_questions) VALUES (6, 'BERNABEI_08_DNS_easy_005', 'MOD. 3 - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft', 'BERNABEI', 'Query ricorsiva vs iterativa', '["DNS", "Query DNS", "Risoluzione nomi"]', 'understand', 'comparison', 'plausible_misconception', 'easy', 'materiale/BERNABEI - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft/Slide/08.Configurazione di un server Domain Name System (DNS).pptx', 12, '["query ricorsiva", "query iterativa", "risoluzione DNS"]', 'Qual e la differenza principale fra una query DNS ricorsiva e una iterativa?', '[{"text": "La query ricorsiva e utilizzata solo dai root servers; la query iterativa e utilizzata solo dai client", "is_correct": false}, {"text": "La query ricorsiva interroga un solo server DNS; la query iterativa interroga tutti i server contemporaneamente", "is_correct": false}, {"text": "Nella query ricorsiva il server DNS prosegue la ricerca fino a fornire un risultato finale; nella iterativa restituisce solo le informazioni che possiede", "is_correct": true}]', '{"right_answer": {"selected_answer": "Nella query ricorsiva il server DNS prosegue la ricerca fino a fornire un risultato finale; nella iterativa restituisce solo le informazioni che possiede", "rationale": "La slide 12 definisce la query ricorsiva come quella in cui il server DNS si occupa di proseguire la ricerca e fornire un risultato finale, mentre nella iterativa il server restituisce soltanto le informazioni che possiede (''best guess'').", "original_reference": {"path": "materiale/BERNABEI - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft/Slide/08.Configurazione di un server Domain Name System (DNS).pptx", "page": 12}}, "wrong_answers": [{"answer_text": "La query ricorsiva interroga un solo server DNS; la query iterativa interroga tutti i server contemporaneamente", "interpretation": "Ricorsiva come query singola e iterativa come query parallela", "why_wrong": "Non si tratta di quanti server vengono interrogati ne di interrogazioni parallele. La differenza sta in chi si occupa di proseguire la ricerca: il server (ricorsiva) o il client (iterativa)."}, {"answer_text": "La query ricorsiva e utilizzata solo dai root servers; la query iterativa e utilizzata solo dai client", "interpretation": "Divisione rigida per tipo di attore", "why_wrong": "Questa divisione non e corretta. I client tipicamente inviano query ricorsive al proprio DNS server, e quest''ultimo esegue query iterative verso altri server (inclusi i root servers)."}]}', '["BERNABEI_08_DNS_medium_001", "BERNABEI_08_DNS_easy_004"]');
INSERT INTO questions (batch_id, id, curriculum_area, lecturer, topic, clusters, cognitive_level, question_type, distractor_strategy, difficulty, source_file, source_file_page_number, topics, question_prompt, choices, explanation, related_questions) VALUES (6, 'BONONI_NetworkSecurity_medium_007', 'MOD. 2 - Sicurezza delle reti e dei sistemi', 'BONONI', 'IPsec - protocolli AH e ESP', '["Sicurezza a livello di rete"]', 'understand', 'comparison', 'false_distinction', 'medium', 'materiale/BONONI - Sicurezza delle reti e dei sistemi/LBononi_IFTS_FAV_3_ NetworkSecurity_Bononi_IFTS2015.pdf', 76, '["IPsec", "AH", "ESP", "authentication", "confidentiality"]', 'Qual e'' la differenza principale tra il protocollo AH e il protocollo ESP in IPsec?', '[{"text": "AH opera a livello di trasporto mentre ESP opera a livello di rete", "is_correct": false}, {"text": "AH fornisce autenticazione e integrita'' ma NON confidenzialita'', mentre ESP fornisce tutte e tre", "is_correct": true}, {"text": "AH usa chiavi simmetriche mentre ESP usa chiavi pubbliche", "is_correct": false}, {"text": "AH e'' usato solo per IPv4 mentre ESP e'' usato solo per IPv6", "is_correct": false}]', '{"right_answer": {"selected_answer": "AH fornisce autenticazione e integrita'' ma NON confidenzialita'', mentre ESP fornisce tutte e tre", "rationale": "Il protocollo AH (Authentication Header) fornisce autenticazione della sorgente e integrita'' dei dati, ma non la confidenzialita''. Il protocollo ESP (Encapsulation Security Payload) fornisce segretezza, autenticazione dell''host e integrita'' dei dati.", "original_reference": {"path": "materiale/BONONI - Sicurezza delle reti e dei sistemi/LBononi_IFTS_FAV_3_ NetworkSecurity_Bononi_IFTS2015.pdf", "page": 76}}, "wrong_answers": [{"answer_text": "AH opera a livello di trasporto mentre ESP opera a livello di rete", "interpretation": "Lo studente confonde i livelli di operazione", "why_wrong": "Entrambi i protocolli operano a livello di rete (network layer) all''interno di IPsec."}, {"answer_text": "AH usa chiavi simmetriche mentre ESP usa chiavi pubbliche", "interpretation": "Lo studente inventa una distinzione nel tipo di crittografia", "why_wrong": "Entrambi i protocolli utilizzano la Security Association (SA) per gestire le chiavi. La differenza e'' nelle garanzie di sicurezza fornite, non nel tipo di chiavi."}, {"answer_text": "AH e'' usato solo per IPv4 mentre ESP e'' usato solo per IPv6", "interpretation": "Lo studente confonde con le versioni del protocollo IP", "why_wrong": "Entrambi i protocolli possono essere usati con qualsiasi versione di IP. La distinzione riguarda le proprieta'' di sicurezza, non la versione IP."}]}', '["BONONI_NetworkSecurity_hard_005"]');
INSERT INTO questions (batch_id, id, curriculum_area, lecturer, topic, clusters, cognitive_level, question_type, distractor_strategy, difficulty, source_file, source_file_page_number, topics, question_prompt, choices, explanation, related_questions) VALUES (6, 'BERNABEI_07_SicurezzaGPO_medium_003', 'MOD. 3 - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft', 'BERNABEI', 'Domain Password Policy - precedenza', '["Group Policy", "Password Policy", "Domain Controller"]', 'understand', 'concept', 'common_misconception', 'medium', 'materiale/BERNABEI - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft/Slide/07.Gestire la sicurezza e le configurazioni tramite le Group Policy Settings.pptx', 23, '["Domain Password Policy", "Default Domain Policy", "Domain Controllers", "precedenza GPO"]', 'Come viene determinata la Password Policy di dominio applicata a tutti gli utenti del dominio?', '[{"text": "Dalla GPO linkata alla OU in cui risiede l''account utente specifico", "is_correct": false}, {"text": "Dalla GPO con la precedenza piu alta linkata al container dei Domain Controllers; tipicamente la Default Domain Policy", "is_correct": true}, {"text": "Dalla media dei valori configurati in tutte le GPO linkate alle varie OU del dominio", "is_correct": false}]', '{"right_answer": {"selected_answer": "Dalla GPO con la precedenza piu alta linkata al container dei Domain Controllers; tipicamente la Default Domain Policy", "rationale": "La slide 23 spiega che la Password Policy di dominio e determinata dalla GPO con la precedenza piu alta applicata ai Domain Controller. Di default, questa e la Default Domain Policy, che si applica uniformemente a tutti gli account del dominio.", "original_reference": {"path": "materiale/BERNABEI - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft/Slide/07.Gestire la sicurezza e le configurazioni tramite le Group Policy Settings.pptx", "page": 23}}, "wrong_answers": [{"answer_text": "Dalla media dei valori configurati in tutte le GPO linkate alle varie OU del dominio", "interpretation": "Le Password Policies vengono mediate tra le GPO", "why_wrong": "Le Password Policies non vengono mai mediate. Esiste una sola Password Policy di dominio, determinata dalla GPO con la massima precedenza sui Domain Controller. Non c''e aggregazione o media."}, {"answer_text": "Dalla GPO linkata alla OU in cui risiede l''account utente specifico", "interpretation": "Ogni OU puo avere la propria Password Policy", "why_wrong": "Le Password Policies configurate su OU diverse dalla radice del dominio non vengono applicate agli account utente. Per avere policy password diverse serve la funzionalita Fine-Grained Password Policy (PSO)."}]}', '["BERNABEI_07_SicurezzaGPO_easy_004", "BERNABEI_07_SicurezzaGPO_hard_003"]');
INSERT INTO questions (batch_id, id, curriculum_area, lecturer, topic, clusters, cognitive_level, question_type, distractor_strategy, difficulty, source_file, source_file_page_number, topics, question_prompt, choices, explanation, related_questions) VALUES (6, 'FORONI_3_Crittografia_very_easy_002', 'MOD. 4 - Implementazione e verifica della sicurezza delle reti e dei sistemi', 'FORONI', 'Crittografia simmetrica - concetto base', '["crittografia simmetrica", "chiave comune", "shared key"]', 'remember', 'definition', 'common_confusion', 'very_easy', 'materiale/FORONI/Lezioni - Implementazione e verifica della sicurezza delle reti e dei sistemi/3.CRITTOGRAFIA.pdf', 7, '["crittografia simmetrica", "chiave comune"]', 'Nella crittografia simmetrica (symmetric cryptography), quale affermazione e'' corretta?', '[{"text": "Non e'' necessaria alcuna chiave, basta conoscere l''algoritmo", "is_correct": false}, {"text": "La chiave di cifratura coincide con quella di decifratura ed e'' chiamata chiave comune (shared key)", "is_correct": true}, {"text": "Si utilizzano due chiavi diverse: una pubblica e una privata", "is_correct": false}]', '{"right_answer": {"selected_answer": "La chiave di cifratura coincide con quella di decifratura ed e'' chiamata chiave comune (shared key)", "rationale": "Come indicato nella slide 7: ''Quando la chiave di cifratura coincide con quella di decifratura lo schema crittografico si dice simmetrico e la chiave prende il nome di chiave comune''.", "original_reference": {"path": "materiale/FORONI/Lezioni - Implementazione e verifica della sicurezza delle reti e dei sistemi/3.CRITTOGRAFIA.pdf", "page": 7}}, "wrong_answers": [{"answer_text": "Si utilizzano due chiavi diverse: una pubblica e una privata", "interpretation": "Confonde crittografia simmetrica con asimmetrica", "why_wrong": "L''uso di chiave pubblica e chiave privata e'' caratteristico della crittografia asimmetrica, non di quella simmetrica."}, {"answer_text": "Non e'' necessaria alcuna chiave, basta conoscere l''algoritmo", "interpretation": "Confonde il concetto di crittografia con la semplice codifica", "why_wrong": "La crittografia si compone di due elementi: algoritmo e parametro/i (chiave). La chiave e'' indispensabile."}]}', '["FORONI_3_Crittografia_easy_003"]');
INSERT INTO questions (batch_id, id, curriculum_area, lecturer, topic, clusters, cognitive_level, question_type, distractor_strategy, difficulty, source_file, source_file_page_number, topics, question_prompt, choices, explanation, related_questions) VALUES (6, 'FORONI_2_Fondamenti_medium_003', 'MOD. 4 - Implementazione e verifica della sicurezza delle reti e dei sistemi', 'FORONI', 'IDS vs IPS', '["IDS", "IPS", "Intrusion Detection System", "Intrusion Prevention System"]', 'analyze', 'comparison', 'common_confusion', 'medium', 'materiale/FORONI/Lezioni - Implementazione e verifica della sicurezza delle reti e dei sistemi/2.Fondamenti Cybersecurity .pdf', 15, '["IDS", "IPS", "intrusion detection", "intrusion prevention", "traffico di rete"]', 'Qual e'' la differenza fondamentale tra un IDS (Intrusion Detection System) e un IPS (Intrusion Prevention System)?', '[{"text": "L''IDS e l''IPS sono sinonimi che indicano lo stesso tipo di sistema di protezione", "is_correct": false}, {"text": "L''IDS e'' un dispositivo hardware, l''IPS e'' sempre un software", "is_correct": false}, {"text": "L''IDS rileva accessi non autorizzati analizzando il traffico in ingresso; l''IPS identifica un tentativo di intrusione non ancora eseguito e lo blocca attivamente", "is_correct": true}, {"text": "L''IDS opera solo sulla rete interna, l''IPS opera solo sulla rete esterna", "is_correct": false}]', '{"right_answer": {"selected_answer": "L''IDS rileva accessi non autorizzati analizzando il traffico in ingresso; l''IPS identifica un tentativo di intrusione non ancora eseguito e lo blocca attivamente", "rationale": "Il materiale definisce l''IDS come apparati o software che analizzano il traffico di rete in ingresso per rilevare sequenze di dati riconoscibili come accessi non autorizzati dall''esterno. L''IPS e'' definito come sistemi che, analizzando il traffico di rete, identificano un tentativo di intrusione non autorizzato e non ancora eseguito e lo bloccano.", "original_reference": {"path": "materiale/FORONI/Lezioni - Implementazione e verifica della sicurezza delle reti e dei sistemi/2.Fondamenti Cybersecurity .pdf", "page": 15}}, "wrong_answers": [{"answer_text": "L''IDS e'' un dispositivo hardware, l''IPS e'' sempre un software", "interpretation": "Crea una falsa distinzione hardware/software", "why_wrong": "Il materiale indica che l''IDS puo'' essere sia un apparato sia un software. La distinzione IDS/IPS non e'' basata sulla forma hardware o software."}, {"answer_text": "L''IDS opera solo sulla rete interna, l''IPS opera solo sulla rete esterna", "interpretation": "Limita erroneamente l''ambito operativo di ciascun sistema", "why_wrong": "Entrambi i sistemi fanno parte della sicurezza perimetrale e operano analizzando il traffico di rete. Non c''e'' una distinzione rete interna/esterna."}, {"answer_text": "L''IDS e l''IPS sono sinonimi che indicano lo stesso tipo di sistema di protezione", "interpretation": "Considera i due sistemi come identici", "why_wrong": "L''IDS rileva le intrusioni (detection), l''IPS le previene bloccandole (prevention). Sono sistemi complementari ma con funzioni distinte."}]}', '["FORONI_2_Fondamenti_hard_002"]');
INSERT INTO questions (batch_id, id, curriculum_area, lecturer, topic, clusters, cognitive_level, question_type, distractor_strategy, difficulty, source_file, source_file_page_number, topics, question_prompt, choices, explanation, related_questions) VALUES (6, 'ANZALONE_3a_vnet_medium_004', 'MOD. 5 - Cloud privato e cloud pubblico al servizio della business continuity e disaster recovery', 'ANZALONE', 'Tipi di VLAN per membership', '["Networking Virtuale", "Sicurezza delle Reti"]', 'understand', 'concept', 'common_misconception', 'medium', 'materiale/ANZALONE - Cloud privato e cloud pubblico al servizio della business continuity e disaster recovery/3a_vnet.pdf', 87, '["VLAN", "Layer 1 port-based", "Layer 2 MAC", "Layer 3 IP subnet", "membership"]', 'Quale tipo di VLAN consente la mobilità dell''utente senza riconfigurazione, basandosi sull''indirizzo MAC della workstation?', '[{"text": "Layer 1 VLAN basata su porta: la membership è legata alla porta fisica dello switch", "is_correct": false}, {"text": "Layer 3 VLAN basata su IP subnet: la membership è determinata dall''indirizzo di rete", "is_correct": false}, {"text": "Higher Layer VLAN basata su applicazioni: la membership dipende dal tipo di protocollo applicativo", "is_correct": false}, {"text": "Layer 2 VLAN basata su MAC address: la membership segue la workstation indipendentemente dalla porta", "is_correct": true}]', '{"right_answer": {"selected_answer": "Layer 2 VLAN basata su MAC address: la membership segue la workstation indipendentemente dalla porta", "rationale": "La VLAN Layer 2 basata su MAC address associa la membership all''indirizzo MAC della scheda di rete. Quando una workstation viene spostata, non è necessaria alcuna riconfigurazione perché il MAC rimane lo stesso.", "original_reference": {"path": "materiale/ANZALONE - Cloud privato e cloud pubblico al servizio della business continuity e disaster recovery/3a_vnet.pdf", "page": 89}}, "wrong_answers": [{"answer_text": "Layer 1 VLAN basata su porta: la membership è legata alla porta fisica dello switch", "interpretation": "Sceglie il tipo che non supporta mobilità", "why_wrong": "La VLAN port-based lega la membership alla porta fisica. Se l''utente si sposta, l''amministratore deve riconfigurare la VLAN, quindi non consente mobilità."}, {"answer_text": "Layer 3 VLAN basata su IP subnet: la membership è determinata dall''indirizzo di rete", "interpretation": "Confonde mobilità MAC con mobilità IP", "why_wrong": "Sebbene Layer 3 consenta mobilità senza riconfigurazione manuale, la domanda chiede specificamente la membership basata su MAC address, che è Layer 2."}, {"answer_text": "Higher Layer VLAN basata su applicazioni: la membership dipende dal tipo di protocollo applicativo", "interpretation": "Sceglie il tipo più avanzato pensando sia il migliore", "why_wrong": "Le Higher Layer VLAN si basano su applicazioni o servizi, non su MAC address. Non rispondono al criterio della domanda."}]}', '["ANZALONE_3a_vnet_very_easy_003"]');
INSERT INTO questions (batch_id, id, curriculum_area, lecturer, topic, clusters, cognitive_level, question_type, distractor_strategy, difficulty, source_file, source_file_page_number, topics, question_prompt, choices, explanation, related_questions) VALUES (6, 'FORONI_07_GatheringInfo_easy_006', 'MOD. 4 - Implementazione e verifica della sicurezza delle reti e dei sistemi', 'FORONI', 'Tipi di social engineering', '["social engineering", "human-based", "computer-based", "phishing"]', 'understand', 'conceptual', 'common_confusion', 'easy', 'materiale/FORONI/Lezioni - Implementazione e verifica della sicurezza delle reti e dei sistemi/7.Gathering Target Information.pdf', 20, '["human-based", "computer-based", "phishing", "help desk"]', 'Come vengono classificati i due tipi principali di attacchi di social engineering?', '[{"text": "Passivo (solo osservazione) e attivo (modifica dei dati)", "is_correct": false}, {"text": "Human-based (interazione persona-persona) e computer-based (tramite software, es. phishing)", "is_correct": true}, {"text": "Interno (da dipendenti) ed esterno (da hacker sconosciuti)", "is_correct": false}, {"text": "Locale (nella stessa rete) e remoto (da Internet)", "is_correct": false}]', '{"right_answer": {"selected_answer": "Human-based (interazione persona-persona) e computer-based (tramite software, es. phishing)", "rationale": "Il materiale classifica a pagina 20 il social engineering in due tipi: human-based (person-to-person interaction, es. chiamare l''help desk) e computer-based (tramite software, es. phishing via email).", "original_reference": {"path": "materiale/FORONI/Lezioni - Implementazione e verifica della sicurezza delle reti e dei sistemi/7.Gathering Target Information.pdf", "page": 20}}, "wrong_answers": [{"answer_text": "Interno (da dipendenti) ed esterno (da hacker sconosciuti)", "interpretation": "Classifica per provenienza dell''attaccante anziche'' per metodo", "why_wrong": "Questa classificazione riguarda la provenienza dell''attacco (insider vs outsider), non il metodo di social engineering. La classificazione corretta e'' per tipo di interazione."}, {"answer_text": "Passivo (solo osservazione) e attivo (modifica dei dati)", "interpretation": "Confonde la classificazione degli attacchi generici con quella del social engineering", "why_wrong": "La distinzione passivo/attivo si applica alla reconnaissance e agli attacchi di rete, non alla classificazione del social engineering."}, {"answer_text": "Locale (nella stessa rete) e remoto (da Internet)", "interpretation": "Classifica per posizione geografica anziche'' per metodo", "why_wrong": "La classificazione locale/remoto si applica ai vettori di attacco di rete. Il social engineering si classifica per tipo di interazione: umana o tramite computer."}]}', '[]');
INSERT INTO questions (batch_id, id, curriculum_area, lecturer, topic, clusters, cognitive_level, question_type, distractor_strategy, difficulty, source_file, source_file_page_number, topics, question_prompt, choices, explanation, related_questions) VALUES (6, 'FORONI_09_DeepWeb_easy_002', 'MOD. 4 - Implementazione e verifica della sicurezza delle reti e dei sistemi', 'FORONI', 'Origine della rete TOR', '["TOR", "US Naval Research Lab", "2002", "anonimato"]', 'remember', 'factual', 'common_confusion', 'easy', 'materiale/FORONI/Lezioni - Implementazione e verifica della sicurezza delle reti e dei sistemi/9. il_deep_web.pdf', 6, '["TOR", "US Naval Research Laboratory", "2002", "comunicazioni anonime"]', 'Da chi e'' stata originariamente sviluppata la rete TOR e quando e'' stata introdotta?', '[{"text": "Dalla US Naval Research Laboratory, introdotta nel 2002", "is_correct": true}, {"text": "Da Google, introdotta nel 2010 come progetto open source", "is_correct": false}, {"text": "Dall''NSA (National Security Agency), introdotta nel 1995", "is_correct": false}, {"text": "Da un gruppo di hacker anonimi, introdotta nel 2008", "is_correct": false}]', '{"right_answer": {"selected_answer": "Dalla US Naval Research Laboratory, introdotta nel 2002", "rationale": "Il materiale spiega a pagina 6 che ''la rete TOR e'' stata originariamente sviluppata dalla US Naval Research Laboratory ed introdotta nel 2002 per consentire comunicazioni anonime''.", "original_reference": {"path": "materiale/FORONI/Lezioni - Implementazione e verifica della sicurezza delle reti e dei sistemi/9. il_deep_web.pdf", "page": 6}}, "wrong_answers": [{"answer_text": "Da Google, introdotta nel 2010 come progetto open source", "interpretation": "Attribuisce erroneamente TOR a un''azienda commerciale", "why_wrong": "TOR non e'' stato sviluppato da Google ma dalla US Naval Research Laboratory, un laboratorio di ricerca della Marina americana, nel 2002."}, {"answer_text": "Dall''NSA (National Security Agency), introdotta nel 1995", "interpretation": "Confonde il laboratorio di ricerca della Marina con l''NSA", "why_wrong": "TOR e'' stato sviluppato dalla US Naval Research Laboratory, non dall''NSA. Inoltre la data corretta e'' il 2002, non il 1995."}, {"answer_text": "Da un gruppo di hacker anonimi, introdotta nel 2008", "interpretation": "Associa TOR a origini clandestine", "why_wrong": "TOR ha origini istituzionali, non clandestine. E'' stato sviluppato dalla US Naval Research Laboratory nel 2002, non da hacker nel 2008."}]}', '[]');
INSERT INTO questions (batch_id, id, curriculum_area, lecturer, topic, clusters, cognitive_level, question_type, distractor_strategy, difficulty, source_file, source_file_page_number, topics, question_prompt, choices, explanation, related_questions) VALUES (6, 'FORONI_4_CertificatiPKI_easy_003', 'MOD. 4 - Implementazione e verifica della sicurezza delle reti e dei sistemi', 'FORONI', 'Motivi di revoca dei certificati', '["revoca certificati", "certificate revocation", "CRL"]', 'remember', 'factual', 'plausible_but_wrong', 'easy', 'materiale/FORONI/Lezioni - Implementazione e verifica della sicurezza delle reti e dei sistemi/4Certificati e PKI_2017.pdf', 17, '["revoca certificati", "compromissione chiave", "CRL"]', 'Quale dei seguenti NON e'' un motivo di revoca di un certificato digitale elencato nella lezione?', '[{"text": "Perdita o malfunzionamento del security token", "is_correct": false}, {"text": "Il certificato e'' scaduto naturalmente alla data di fine validita''", "is_correct": true}, {"text": "Compromissione della chiave privata dell''utente", "is_correct": false}, {"text": "Le informazioni contenute non sono piu'' valide (es. cambio affiliazione)", "is_correct": false}]', '{"right_answer": {"selected_answer": "Il certificato e'' scaduto naturalmente alla data di fine validita''", "rationale": "La scadenza naturale non richiede revoca: il certificato semplicemente cessa di essere valido alla data di fine validita''. La revoca si richiede quando un certificato ancora valido deve essere invalidato anticipatamente. La slide 17 elenca i motivi: compromissione chiave privata, info non piu'' valide, non piu'' utile per lo scopo, compromissione algoritmo, perdita security token, cambio politiche di sicurezza.", "original_reference": {"path": "materiale/FORONI/Lezioni - Implementazione e verifica della sicurezza delle reti e dei sistemi/4Certificati e PKI_2017.pdf", "page": 17}}, "wrong_answers": [{"answer_text": "Compromissione della chiave privata dell''utente", "interpretation": "Potrebbe pensare che la compromissione della chiave non comporti revoca", "why_wrong": "La compromissione della chiave privata e'' il primo motivo di revoca elencato nella slide 17."}, {"answer_text": "Le informazioni contenute non sono piu'' valide (es. cambio affiliazione)", "interpretation": "Potrebbe non associare il cambio di informazioni alla revoca", "why_wrong": "La slide elenca ''Info non piu'' valide (ad es., cambio affiliazione)'' come motivo di revoca."}, {"answer_text": "Perdita o malfunzionamento del security token", "interpretation": "Potrebbe pensare che si tratti solo di un problema tecnico locale", "why_wrong": "La slide elenca esplicitamente ''Perdita o malfunzionamento di security token, perdita di password o PIN'' come motivo di revoca."}]}', '["FORONI_4_CertificatiPKI_medium_001"]');
INSERT INTO questions (batch_id, id, curriculum_area, lecturer, topic, clusters, cognitive_level, question_type, distractor_strategy, difficulty, source_file, source_file_page_number, topics, question_prompt, choices, explanation, related_questions) VALUES (6, 'ANZALONE_4a_storage_easy_007', 'MOD. 5 - Cloud privato e cloud pubblico al servizio della business continuity e disaster recovery', 'ANZALONE', 'Tipi di migrazione vSphere', '["VMware vSphere", "Migrazione"]', 'remember', 'definition', 'related_domain', 'easy', 'materiale/ANZALONE - Cloud privato e cloud pubblico al servizio della business continuity e disaster recovery/4a_storage.pdf', 82, '["vMotion", "Storage vMotion", "cold migration", "migrazione"]', 'Quale tipo di migrazione vSphere consente di spostare una VM accesa su un altro host senza interruzioni?', '[{"text": "Migration of a suspended virtual machine", "is_correct": false}, {"text": "Migration with vMotion", "is_correct": true}, {"text": "Cold Migration", "is_correct": false}]', '{"right_answer": {"selected_answer": "Migration with vMotion", "rationale": "La migrazione con vMotion permette di spostare una VM accesa (powered-on) su un nuovo host senza interruzione della disponibilita'' della macchina virtuale.", "original_reference": {"path": "materiale/ANZALONE - Cloud privato e cloud pubblico al servizio della business continuity e disaster recovery/4a_storage.pdf", "page": 83}}, "wrong_answers": [{"answer_text": "Cold Migration", "interpretation": "Migrazione a freddo, richiede la VM spenta", "why_wrong": "La cold migration sposta una VM che e'' powered-off, non una VM in esecuzione. Richiede lo spegnimento preventivo della macchina virtuale."}, {"answer_text": "Migration of a suspended virtual machine", "interpretation": "Migrazione di una VM in stato sospeso", "why_wrong": "Questa migrazione sposta una VM in stato sospeso (non in esecuzione attiva). La VM deve essere prima sospesa, non e'' una migrazione live."}]}', '["ANZALONE_4a_storage_easy_004"]');
INSERT INTO questions (batch_id, id, curriculum_area, lecturer, topic, clusters, cognitive_level, question_type, distractor_strategy, difficulty, source_file, source_file_page_number, topics, question_prompt, choices, explanation, related_questions) VALUES (6, 'BERNABEI_04_GestireGruppi_easy_006', 'MOD. 3 - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft', 'BERNABEI', 'Special Identities', '["Gestione Gruppi", "Active Directory", "Sicurezza"]', 'remember', 'definition', 'plausible_alternative', 'easy', 'materiale/BERNABEI - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft/Slide/04.Gestire i gruppi.pptx', 38, '["Special Identities", "Everyone", "Authenticated Users", "Anonymous Logon"]', 'Cosa include la Special Identity ''Everyone'' in Active Directory?', '[{"text": "Authenticated Users e Guest, ma non Anonymous Logon", "is_correct": true}, {"text": "Esclusivamente gli account del dominio locale, senza Guest", "is_correct": false}, {"text": "Solo gli utenti che hanno effettuato un logon interattivo", "is_correct": false}, {"text": "Tutti gli utenti autenticati, gli ospiti e anche Anonymous Logon", "is_correct": false}]', '{"right_answer": {"selected_answer": "Authenticated Users e Guest, ma non Anonymous Logon", "rationale": "La slide 38 specifica che Everyone include Authenticated Users e Guest, ma non Anonymous Logon.", "original_reference": {"path": "materiale/BERNABEI - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft/Slide/04.Gestire i gruppi.pptx", "page": 38}}, "wrong_answers": [{"answer_text": "Tutti gli utenti autenticati, gli ospiti e anche Anonymous Logon", "interpretation": "Everyone come gruppo che include tutto", "why_wrong": "Il materiale specifica che Everyone non include Anonymous Logon, contrariamente a quanto si potrebbe pensare dal nome."}, {"answer_text": "Solo gli utenti che hanno effettuato un logon interattivo", "interpretation": "Everyone limitato al logon interattivo", "why_wrong": "Questa descrizione corrisponde alla Special Identity ''Interactive'', non a ''Everyone''."}, {"answer_text": "Esclusivamente gli account del dominio locale, senza Guest", "interpretation": "Everyone limitato al dominio locale", "why_wrong": "Everyone include anche Guest e non e limitato al dominio locale; e una Special Identity piu ampia."}]}', '["BERNABEI_04_GestireGruppi_easy_005", "BERNABEI_04_GestireGruppi_medium_005"]');
INSERT INTO questions (batch_id, id, curriculum_area, lecturer, topic, clusters, cognitive_level, question_type, distractor_strategy, difficulty, source_file, source_file_page_number, topics, question_prompt, choices, explanation, related_questions) VALUES (6, 'GUIDAZZOLI_file_access_medium_003', 'MOD. 3 - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft', 'GUIDAZZOLI', 'NTLM vs Kerberos', '["Autenticazione", "Kerberos", "NTLM", "Compatibilita''"]', 'understand', 'comparison', 'obviously_wrong', 'medium', 'materiale/GUIDAZZOLI - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft/File Access and Printers.ppt', 5, '["NTLM", "Kerberos", "backward compatibility", "pre-Windows 2000"]', 'In quale situazione viene utilizzato il protocollo NTLM invece di Kerberos?', '[{"text": "Quando la rete utilizza esclusivamente connessioni Wi-Fi", "is_correct": false}, {"text": "Per compatibilita'' con computer che eseguono sistemi operativi precedenti a Windows 2000", "is_correct": true}, {"text": "Quando il computer ha piu'' di 16 GB di RAM installata", "is_correct": false}, {"text": "Solo quando si accede a stampanti condivise in rete", "is_correct": false}]', '{"right_answer": {"selected_answer": "Per compatibilita'' con computer che eseguono sistemi operativi precedenti a Windows 2000", "rationale": "Il materiale specifica: ''NTLM - Used for backward compatibility with computers running pre-Windows 2000 operating systems and some applications''.", "original_reference": {"path": "materiale/GUIDAZZOLI - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft/File Access and Printers.ppt", "page": 5}}, "wrong_answers": [{"answer_text": "Quando la rete utilizza esclusivamente connessioni Wi-Fi", "interpretation": "Collega erroneamente il protocollo di autenticazione al tipo di connessione", "why_wrong": "Il tipo di connessione (Wi-Fi o cablata) non influenza la scelta tra NTLM e Kerberos."}, {"answer_text": "Solo quando si accede a stampanti condivise in rete", "interpretation": "Limita NTLM a un tipo specifico di risorsa", "why_wrong": "NTLM non e'' limitato alle stampanti. Viene usato per qualsiasi risorsa quando serve compatibilita'' con sistemi pre-2000."}, {"answer_text": "Quando il computer ha piu'' di 16 GB di RAM installata", "interpretation": "Collega erroneamente il protocollo alla quantita'' di RAM", "why_wrong": "La quantita'' di RAM non ha alcuna relazione con la scelta del protocollo di autenticazione."}]}', '["GUIDAZZOLI_file_access_easy_003"]');
INSERT INTO questions (batch_id, id, curriculum_area, lecturer, topic, clusters, cognitive_level, question_type, distractor_strategy, difficulty, source_file, source_file_page_number, topics, question_prompt, choices, explanation, related_questions) VALUES (6, 'BONONI_intro_reti2025B_easy_002', 'MOD. 2 - Sicurezza delle reti e dei sistemi', 'BONONI', 'Composizione indirizzo IP', '["Indirizzamento IP"]', 'remember', 'factual', 'related_concept', 'easy', 'materiale/BONONI - Sicurezza delle reti e dei sistemi/LBononi_RdC_intro_reti2025Bnew.pdf', 7, '["IPv4", "network number", "host number"]', 'Da quali due parti e'' sempre composto un indirizzo IPv4?', '[{"text": "Nome di dominio e indirizzo e-mail dell''utente", "is_correct": false}, {"text": "Network number (numero della rete) e host number (numero dell''host nella rete)", "is_correct": true}, {"text": "Indirizzo del router e indirizzo del server DNS", "is_correct": false}, {"text": "Indirizzo MAC e numero di porta TCP", "is_correct": false}]', '{"right_answer": {"selected_answer": "Network number (numero della rete) e host number (numero dell''host nella rete)", "rationale": "Ogni indirizzo IP e'' sempre composto da due parti: il numero della rete IP alla quale appartiene la scheda (network number) e il numero dell''interfaccia di rete (host number) all''interno della rete.", "original_reference": {"path": "materiale/BONONI - Sicurezza delle reti e dei sistemi/LBononi_RdC_intro_reti2025Bnew.pdf", "page": 7}}, "wrong_answers": [{"answer_text": "Indirizzo MAC e numero di porta TCP", "interpretation": "Mescola concetti di livelli diversi", "why_wrong": "L''indirizzo MAC opera a livello 2 e il numero di porta a livello 4. L''indirizzo IP e'' di livello 3 ed e'' composto da network number e host number."}, {"answer_text": "Nome di dominio e indirizzo e-mail dell''utente", "interpretation": "Confonde con concetti di livello applicazione", "why_wrong": "I nomi di dominio e gli indirizzi e-mail sono concetti di livello applicazione, non fanno parte della struttura di un indirizzo IP."}, {"answer_text": "Indirizzo del router e indirizzo del server DNS", "interpretation": "Confonde la struttura dell''IP con i parametri di configurazione di rete", "why_wrong": "Questi sono parametri di configurazione della rete, non le due componenti interne di un singolo indirizzo IP."}]}', '[]');
INSERT INTO questions (batch_id, id, curriculum_area, lecturer, topic, clusters, cognitive_level, question_type, distractor_strategy, difficulty, source_file, source_file_page_number, topics, question_prompt, choices, explanation, related_questions) VALUES (6, 'BERNABEI_05_AccountComputer_very_easy_002', 'MOD. 3 - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft', 'BERNABEI', 'Domain join: riavvio necessario', '["Account Computer", "Domain Join"]', 'remember', 'definition', 'obviously_wrong', 'very_easy', 'materiale/BERNABEI - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft/Slide/05.Gestire gli account computer.pptx', 8, '["domain join", "riavvio", "credenziali"]', 'Dopo aver effettuato la join a dominio di un computer, quale operazione e necessaria per completare il processo?', '[{"text": "La reinstallazione del sistema operativo", "is_correct": false}, {"text": "Il riavvio del computer", "is_correct": true}, {"text": "L''eliminazione del profilo locale dell''utente", "is_correct": false}]', '{"right_answer": {"selected_answer": "Il riavvio del computer", "rationale": "La slide 8 specifica che dopo la join a dominio e necessario il riavvio del computer per completare l''operazione.", "original_reference": {"path": "materiale/BERNABEI - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft/Slide/05.Gestire gli account computer.pptx", "page": 8}}, "wrong_answers": [{"answer_text": "L''eliminazione del profilo locale dell''utente", "interpretation": "Cancellazione profili come requisito della join", "why_wrong": "L''eliminazione dei profili locali non e richiesta dalla join a dominio. I profili locali esistenti restano disponibili."}, {"answer_text": "La reinstallazione del sistema operativo", "interpretation": "Reinstallazione come parte del processo di join", "why_wrong": "La reinstallazione del sistema operativo non e affatto necessaria per la join a dominio. E sufficiente un semplice riavvio."}]}', '["BERNABEI_05_AccountComputer_very_easy_001", "BERNABEI_05_AccountComputer_easy_002"]');
