INSERT INTO questions (batch_id, id, curriculum_area, lecturer, topic, clusters, cognitive_level, question_type, distractor_strategy, difficulty, source_file, source_file_page_number, topics, question_prompt, choices, explanation, related_questions) VALUES (7, 'BERNABEI_03_GestioneUtenti_medium_001', 'MOD. 3 - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft', 'BERNABEI', 'Creazione utenti con DSAdd', '["Active Directory", "Gestione Utenti", "Strumenti da riga di comando"]', 'apply', 'scenario', 'partial_match', 'medium', 'materiale/BERNABEI - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft/Slide/03. Gestione utenti.pptx', 7, '["dsadd", "New-ADUser", "creazione utente", "ChangePasswordAtLogon"]', 'Si deve creare un utente con DSAdd imponendo il cambio password al primo accesso. Quale parametro è necessario specificare?', '[{"text": "-ChangePasswordAtLogon $true", "is_correct": false}, {"text": "-disabled no", "is_correct": false}, {"text": "-mustchpwd yes", "is_correct": true}, {"text": "-pwd {newpassword}", "is_correct": false}]', '{"right_answer": {"selected_answer": "-mustchpwd yes", "rationale": "Nell''esempio DSAdd mostrato nel materiale, il parametro -mustchpwd yes viene utilizzato per forzare l''utente a cambiare la password al primo accesso: dsadd user \"cn=...\" -disabled no -pwd {1234aa123} -mustchpwd yes.", "original_reference": {"path": "materiale/BERNABEI - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft/Slide/03. Gestione utenti.pptx", "page": 7}}, "wrong_answers": [{"answer_text": "-ChangePasswordAtLogon $true", "interpretation": "Parametro PowerShell", "why_wrong": "-ChangePasswordAtLogon $true è il parametro equivalente usato con il cmdlet PowerShell New-ADUser, non con il comando DSAdd."}, {"answer_text": "-disabled no", "interpretation": "Stato dell''account", "why_wrong": "Il parametro -disabled no indica che l''account deve essere creato come abilitato, ma non forza il cambio password al primo logon."}, {"answer_text": "-pwd {newpassword}", "interpretation": "Impostazione password", "why_wrong": "Il parametro -pwd serve per specificare la password iniziale dell''account, ma non impone il cambio password al primo accesso."}]}', '["BERNABEI_03_GestioneUtenti_easy_002", "BERNABEI_03_GestioneUtenti_hard_001"]');
INSERT INTO questions (batch_id, id, curriculum_area, lecturer, topic, clusters, cognitive_level, question_type, distractor_strategy, difficulty, source_file, source_file_page_number, topics, question_prompt, choices, explanation, related_questions) VALUES (7, 'ANZALONE_2a_introVmware_easy_002', 'MOD. 5 - Cloud privato e cloud pubblico al servizio della business continuity e disaster recovery', 'ANZALONE', 'ESXi Installable vs Embedded', '["Virtualizzazione e Hypervisor"]', 'understand', 'comparison', 'obviously_wrong', 'easy', 'materiale/ANZALONE - Cloud privato e cloud pubblico al servizio della business continuity e disaster recovery/2a_introVmware.pdf', 14, '["ESXi Installable", "ESXi Embedded", "OEM"]', 'Cosa distingue ESXi Embedded da ESXi Installable nella piattaforma vSphere?', '[{"text": "ESXi Embedded funziona solo su cloud pubblico, mentre Installable è per datacenter on-premise", "is_correct": false}, {"text": "ESXi Embedded non richiede alcun server fisico per funzionare", "is_correct": false}, {"text": "ESXi Embedded è installato dal produttore hardware (OEM) su un dispositivo di storage persistente interno", "is_correct": true}, {"text": "ESXi Embedded supporta un numero maggiore di VM rispetto alla versione Installable", "is_correct": false}]', '{"right_answer": {"selected_answer": "ESXi Embedded è installato dal produttore hardware (OEM) su un dispositivo di storage persistente interno", "rationale": "ESXi Embedded si riferisce all''installazione OEM dell''hypervisor vSphere su un dispositivo di storage persistente integrato nell''hardware qualificato, come una porta USB interna o uno slot per SD card.", "original_reference": {"path": "materiale/ANZALONE - Cloud privato e cloud pubblico al servizio della business continuity e disaster recovery/2a_introVmware.pdf", "page": 14}}, "wrong_answers": [{"answer_text": "ESXi Embedded supporta un numero maggiore di VM rispetto alla versione Installable", "interpretation": "Differenza nelle capacità di hosting VM", "why_wrong": "Non esiste questa differenza nel numero di VM supportate. La distinzione riguarda esclusivamente la modalità di installazione e il supporto hardware."}, {"answer_text": "ESXi Embedded funziona solo su cloud pubblico, mentre Installable è per datacenter on-premise", "interpretation": "Differenza nell''ambiente di deployment", "why_wrong": "Entrambe le versioni sono per server fisici on-premise. ESXi Embedded è preinstallato dall''OEM, non è legato al cloud pubblico."}, {"answer_text": "ESXi Embedded non richiede alcun server fisico per funzionare", "interpretation": "Funzionamento senza hardware", "why_wrong": "ESXi Embedded richiede hardware fisico qualificato. La parola ''Embedded'' si riferisce al fatto che è pre-installato nel server dal produttore, non che funziona senza hardware."}]}', '[]');
INSERT INTO questions (batch_id, id, curriculum_area, lecturer, topic, clusters, cognitive_level, question_type, distractor_strategy, difficulty, source_file, source_file_page_number, topics, question_prompt, choices, explanation, related_questions) VALUES (7, 'FORONI_08_NormativeSicurezza_medium_001', 'MOD. 4 - Implementazione e verifica della sicurezza delle reti e dei sistemi', 'FORONI', 'Sanzioni GDPR: fasce e importi massimi', '["GDPR", "sanzioni", "violazioni"]', 'understand', 'factual', 'value_confusion', 'medium', 'materiale/FORONI/Lezioni - Implementazione e verifica della sicurezza delle reti e dei sistemi/8.Informativa normative sulla sicurezza dei dati.pdf', 22, '["sanzioni GDPR", "10 milioni", "20 milioni", "fatturato"]', 'Il GDPR prevede due livelli di sanzioni amministrative. Qual e'' l''importo massimo previsto per le violazioni piu'' gravi?', '[{"text": "10 milioni di euro o il 2% del fatturato mondiale annuo, se superiore", "is_correct": false}, {"text": "50 milioni di euro o il 10% del fatturato mondiale annuo, se superiore", "is_correct": false}, {"text": "5 milioni di euro o l''1% del fatturato mondiale annuo, se superiore", "is_correct": false}, {"text": "20 milioni di euro o il 4% del fatturato mondiale annuo, se superiore", "is_correct": true}]', '{"right_answer": {"selected_answer": "20 milioni di euro o il 4% del fatturato mondiale annuo, se superiore", "rationale": "Il GDPR prevede due livelli di sanzioni: quelle minori fino a 10 milioni di euro (o 2% del fatturato) e quelle maggiori fino a 20 milioni di euro (o 4% del fatturato mondiale annuo), applicabili per le violazioni piu'' gravi.", "original_reference": {"path": "materiale/FORONI/Lezioni - Implementazione e verifica della sicurezza delle reti e dei sistemi/8.Informativa normative sulla sicurezza dei dati.pdf", "page": 22}}, "wrong_answers": [{"answer_text": "10 milioni di euro o il 2% del fatturato mondiale annuo, se superiore", "interpretation": "Confonde il livello sanzionatorio minore con quello maggiore", "why_wrong": "Questa e'' la soglia delle sanzioni minori. Le violazioni piu'' gravi prevedono fino a 20 milioni di euro o il 4% del fatturato."}, {"answer_text": "50 milioni di euro o il 10% del fatturato mondiale annuo, se superiore", "interpretation": "Sovrastima gli importi sanzionatori del GDPR", "why_wrong": "Il GDPR non prevede sanzioni cosi'' elevate. Il massimo e'' 20 milioni di euro o il 4% del fatturato."}, {"answer_text": "5 milioni di euro o l''1% del fatturato mondiale annuo, se superiore", "interpretation": "Sottostima gli importi sanzionatori", "why_wrong": "Questi importi sono inferiori anche alla soglia minore prevista dal GDPR (10 milioni / 2%)."}]}', '[]');
INSERT INTO questions (batch_id, id, curriculum_area, lecturer, topic, clusters, cognitive_level, question_type, distractor_strategy, difficulty, source_file, source_file_page_number, topics, question_prompt, choices, explanation, related_questions) VALUES (7, 'ANZALONE_3a_vnet_very_easy_001', 'MOD. 5 - Cloud privato e cloud pubblico al servizio della business continuity e disaster recovery', 'ANZALONE', 'Definizione di virtual network', '["Networking Virtuale", "Infrastruttura VMware vSphere"]', 'remember', 'definition', 'obviously_wrong', 'very_easy', 'materiale/ANZALONE - Cloud privato e cloud pubblico al servizio della business continuity e disaster recovery/3a_vnet.pdf', 3, '["virtual network", "physical network", "ESXi"]', 'Che cos''è una virtual network in ambiente VMware ESXi?', '[{"text": "Una rete di macchine virtuali connesse logicamente tra loro su un singolo host fisico", "is_correct": true}, {"text": "Un servizio DNS che risolve i nomi delle macchine virtuali in indirizzi MAC", "is_correct": false}, {"text": "Un protocollo di crittografia per proteggere il traffico tra datacenter remoti", "is_correct": false}, {"text": "Una rete wireless che collega dispositivi mobili tramite Bluetooth", "is_correct": false}]', '{"right_answer": {"selected_answer": "Una rete di macchine virtuali connesse logicamente tra loro su un singolo host fisico", "rationale": "Una virtual network è una rete di macchine virtuali in esecuzione su una singola macchina fisica, connesse logicamente tra loro per poter scambiare dati. Le VM possono essere collegate alle reti virtuali create quando si aggiunge una rete.", "original_reference": {"path": "materiale/ANZALONE - Cloud privato e cloud pubblico al servizio della business continuity e disaster recovery/3a_vnet.pdf", "page": 3}}, "wrong_answers": [{"answer_text": "Una rete wireless che collega dispositivi mobili tramite Bluetooth", "interpretation": "Confonde reti virtuali con reti wireless", "why_wrong": "Le reti virtuali VMware non hanno nulla a che fare con il Bluetooth o le reti wireless; sono reti logiche create nel software dell''hypervisor."}, {"answer_text": "Un servizio DNS che risolve i nomi delle macchine virtuali in indirizzi MAC", "interpretation": "Confonde networking virtuale con servizi di risoluzione nomi", "why_wrong": "Il DNS risolve nomi in indirizzi IP, non MAC. Una virtual network è una rete logica di VM, non un servizio di naming."}, {"answer_text": "Un protocollo di crittografia per proteggere il traffico tra datacenter remoti", "interpretation": "Confonde reti virtuali con protocolli di sicurezza", "why_wrong": "Una virtual network non è un protocollo di crittografia; è una rete logica che consente la comunicazione tra VM sullo stesso host."}]}', '["ANZALONE_3a_vnet_very_easy_002"]');
INSERT INTO questions (batch_id, id, curriculum_area, lecturer, topic, clusters, cognitive_level, question_type, distractor_strategy, difficulty, source_file, source_file_page_number, topics, question_prompt, choices, explanation, related_questions) VALUES (7, 'BERNABEI_08_DNS_easy_003', 'MOD. 3 - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft', 'BERNABEI', 'Secure dynamic updates', '["DNS", "Dynamic Updates", "Sicurezza DNS"]', 'understand', 'conceptual', 'related_concept', 'easy', 'materiale/BERNABEI - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft/Slide/08.Configurazione di un server Domain Name System (DNS).pptx', 8, '["Dynamic Updates", "Secure dynamic updates", "DNS spoofing"]', 'Quale vantaggio offrono i secure dynamic updates in una zona DNS?', '[{"text": "Impediscono qualsiasi modifica ai record DNS, rendendo la zona di sola lettura", "is_correct": false}, {"text": "Crittografano tutti i record DNS memorizzati nella zona con algoritmo AES-256", "is_correct": false}, {"text": "Permettono la registrazione dei record soltanto da parte di macchine membre del dominio, prevenendo lo spoofing DNS", "is_correct": true}]', '{"right_answer": {"selected_answer": "Permettono la registrazione dei record soltanto da parte di macchine membre del dominio, prevenendo lo spoofing DNS", "rationale": "La slide 8 spiega che i secure dynamic updates permettono soltanto la registrazione di macchine ''sicure'' (membre del dominio) sul DNS, ideale per prevenire lo spoofing.", "original_reference": {"path": "materiale/BERNABEI - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft/Slide/08.Configurazione di un server Domain Name System (DNS).pptx", "page": 8}}, "wrong_answers": [{"answer_text": "Crittografano tutti i record DNS memorizzati nella zona con algoritmo AES-256", "interpretation": "Secure updates come crittografia dei dati", "why_wrong": "I secure dynamic updates non riguardano la crittografia dei record. Il termine ''secure'' si riferisce all''autenticazione delle macchine che possono registrare record, non alla cifratura dei dati."}, {"answer_text": "Impediscono qualsiasi modifica ai record DNS, rendendo la zona di sola lettura", "interpretation": "Secure come immutabilita della zona", "why_wrong": "I secure dynamic updates non rendono la zona immutabile. Al contrario, consentono aggiornamenti dinamici, ma solo da parte di macchine autenticate nel dominio."}]}', '["BERNABEI_08_DNS_easy_004", "BERNABEI_08_DNS_medium_002"]');
INSERT INTO questions (batch_id, id, curriculum_area, lecturer, topic, clusters, cognitive_level, question_type, distractor_strategy, difficulty, source_file, source_file_page_number, topics, question_prompt, choices, explanation, related_questions) VALUES (7, 'FORONI_3_Crittografia_easy_002', 'MOD. 4 - Implementazione e verifica della sicurezza delle reti e dei sistemi', 'FORONI', 'Crittoanalisi e brute force', '["crittoanalisi", "brute force", "social engineering"]', 'understand', 'conceptual', 'related_concept', 'easy', 'materiale/FORONI/Lezioni - Implementazione e verifica della sicurezza delle reti e dei sistemi/3.CRITTOGRAFIA.pdf', 6, '["crittoanalisi", "brute force attack", "social engineering"]', 'La crittoanalisi (cryptanalysis) si occupa di violare il messaggio cifrato. Quali sono le tre tecniche in cui si distingue?', '[{"text": "Cifratura simmetrica, cifratura asimmetrica e hashing", "is_correct": false}, {"text": "Man-in-the-middle, replay attack e spoofing", "is_correct": false}, {"text": "Frequency analysis, dictionary attack e rainbow tables", "is_correct": false}, {"text": "Crittoanalisi, brute force attack e social engineering", "is_correct": true}]', '{"right_answer": {"selected_answer": "Crittoanalisi, brute force attack e social engineering", "rationale": "La slide 6 indica esplicitamente che la violazione dei messaggi cifrati si distingue in tre approcci: crittoanalisi (analisi delle debolezze dell''algoritmo), brute force attack (prova sistematica di tutte le chiavi) e social engineering.", "original_reference": {"path": "materiale/FORONI/Lezioni - Implementazione e verifica della sicurezza delle reti e dei sistemi/3.CRITTOGRAFIA.pdf", "page": 6}}, "wrong_answers": [{"answer_text": "Cifratura simmetrica, cifratura asimmetrica e hashing", "interpretation": "Confonde le tecniche di attacco con i tipi di crittografia", "why_wrong": "Queste sono categorie di algoritmi crittografici, non tecniche di violazione dei messaggi cifrati."}, {"answer_text": "Man-in-the-middle, replay attack e spoofing", "interpretation": "Confonde con attacchi di rete generici", "why_wrong": "Questi sono attacchi di rete, non tecniche specifiche di crittoanalisi come elencato nella lezione."}, {"answer_text": "Frequency analysis, dictionary attack e rainbow tables", "interpretation": "Elenca tecniche specifiche di crittoanalisi ma non corrisponde alla classificazione della lezione", "why_wrong": "Pur essendo tecniche reali, non corrispondono alla classificazione presentata nelle slide, che distingue crittoanalisi, brute force e social engineering."}]}', '[]');
INSERT INTO questions (batch_id, id, curriculum_area, lecturer, topic, clusters, cognitive_level, question_type, distractor_strategy, difficulty, source_file, source_file_page_number, topics, question_prompt, choices, explanation, related_questions) VALUES (7, 'BONONI_intro_reti2025A_medium_002', 'MOD. 2 - Sicurezza delle reti e dei sistemi', 'BONONI', 'Partizioni di rete e ridondanza', '["Topologie di rete", "Infrastrutture di rete"]', 'understand', 'conceptual', 'related_concept', 'medium', 'materiale/BONONI - Sicurezza delle reti e dei sistemi/LBononi_IFTS_intro_reti2025Anew.pdf', 12, '["partizione di rete", "ridondanza", "guasto", "topologia a maglia"]', 'Cos''e'' una partizione di rete e come si puo'' ridurre il rischio che si verifichi?', '[{"text": "E'' un errore software del protocollo di rete; si risolve aggiornando il firmware di tutte le schede di rete", "is_correct": false}, {"text": "E'' la divisione della banda in canali multipli; si riduce il rischio aumentando il numero di switch", "is_correct": false}, {"text": "E'' la suddivisione volontaria della rete in sottoreti; si riduce il rischio eliminando tutti i router intermedi", "is_correct": false}, {"text": "E'' un gruppo di componenti isolato dal resto della rete a causa di guasti; si riduce il rischio usando cammini ridondanti (topologia a maglia)", "is_correct": true}]', '{"right_answer": {"selected_answer": "E'' un gruppo di componenti isolato dal resto della rete a causa di guasti; si riduce il rischio usando cammini ridondanti (topologia a maglia)", "rationale": "In una rete parzialmente connessa, un guasto di una connessione puo'' creare un insieme di componenti separato dagli altri, detto ''partizione''. Le reti con topologie a maglia (connessioni multiple) riducono questo rischio grazie alla ridondanza dei cammini, che permette di aggirare i collegamenti guasti.", "original_reference": {"path": "materiale/BONONI - Sicurezza delle reti e dei sistemi/LBononi_IFTS_intro_reti2025Anew.pdf", "page": 12}}, "wrong_answers": [{"answer_text": "E'' la suddivisione volontaria della rete in sottoreti; si riduce il rischio eliminando tutti i router intermedi", "interpretation": "Questa risposta confonde partizione accidentale con subnetting volontario", "why_wrong": "Una partizione di rete e'' un evento negativo causato da guasti, non una suddivisione volontaria. Eliminare i router non risolverebbe il problema ma lo aggraverebbe."}, {"answer_text": "E'' un errore software del protocollo di rete; si risolve aggiornando il firmware di tutte le schede di rete", "interpretation": "Questa risposta attribuisce la partizione a un bug software", "why_wrong": "Le partizioni possono essere causate da guasti fisici o da cattive applicazioni dei protocolli, ma non sono semplicemente errori firmware. La ridondanza dei cammini e'' la soluzione principale."}, {"answer_text": "E'' la divisione della banda in canali multipli; si riduce il rischio aumentando il numero di switch", "interpretation": "Questa risposta confonde partizione di rete con multiplazione", "why_wrong": "La partizione di rete non riguarda la divisione della banda in canali. E'' l''isolamento di gruppi di nodi causato da guasti nelle connessioni fisiche."}]}', '["BONONI_intro_reti2025A_very_easy_003"]');
INSERT INTO questions (batch_id, id, curriculum_area, lecturer, topic, clusters, cognitive_level, question_type, distractor_strategy, difficulty, source_file, source_file_page_number, topics, question_prompt, choices, explanation, related_questions) VALUES (7, 'BERNABEI_02_SicurezzaAdmin_very_easy_001', 'MOD. 3 - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft', 'BERNABEI', 'Console MMC - definizione base', '["Amministrazione Active Directory", "Strumenti di gestione Windows"]', 'remember', 'definition', 'obviously_wrong', 'very_easy', 'materiale/BERNABEI - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft/Slide/02.Sicurezza amministrazione Active Directory.pptx', 4, '["MMC", "console", "snap-in"]', 'Qual è lo strumento principale di Windows che ospita gli snap-in per l''amministrazione di Active Directory?', '[{"text": "L''applicazione Microsoft Word", "is_correct": false}, {"text": "La console MMC (Microsoft Management Console)", "is_correct": true}, {"text": "Il lettore multimediale Windows Media Player", "is_correct": false}, {"text": "Il browser Internet Explorer", "is_correct": false}]', '{"right_answer": {"selected_answer": "La console MMC (Microsoft Management Console)", "rationale": "La console MMC è lo strumento grafico di Windows che ospita gli snap-in amministrativi, tra cui quelli di Active Directory. Contiene il Console Tree, il Details Pane e l''Actions Pane per la gestione centralizzata.", "original_reference": {"path": "materiale/BERNABEI - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft/Slide/02.Sicurezza amministrazione Active Directory.pptx", "page": 4}}, "wrong_answers": [{"answer_text": "Il browser Internet Explorer", "interpretation": "Browser web", "why_wrong": "Internet Explorer è un browser per la navigazione web, non uno strumento di amministrazione di sistema per gestire Active Directory."}, {"answer_text": "Il lettore multimediale Windows Media Player", "interpretation": "Applicazione multimediale", "why_wrong": "Windows Media Player è un lettore multimediale, completamente estraneo all''amministrazione di Active Directory."}, {"answer_text": "L''applicazione Microsoft Word", "interpretation": "Software di videoscrittura", "why_wrong": "Microsoft Word è un elaboratore di testi della suite Office, non ha alcuna funzionalità di amministrazione di sistema."}]}', '["BERNABEI_02_SicurezzaAdmin_easy_001"]');
INSERT INTO questions (batch_id, id, curriculum_area, lecturer, topic, clusters, cognitive_level, question_type, distractor_strategy, difficulty, source_file, source_file_page_number, topics, question_prompt, choices, explanation, related_questions) VALUES (7, 'BERNABEI_01_IntrodAD_medium_002', 'MOD. 3 - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft', 'BERNABEI', 'Partizioni logiche di Active Directory', '["Active Directory Domain Services", "Componenti AD"]', 'understand', 'concept', 'related_domain', 'medium', 'materiale/BERNABEI - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft/Slide/01.Introduzione ad Active Directory DS.pptx', 21, '["partizioni AD", "Domain NC", "Schema", "Configuration", "Global Catalog", "DNS"]', 'Quali sono le partizioni logiche contenute nel data store ntds.dit di Active Directory?', '[{"text": "C:\\ drive, D:\\ drive, System Reserved, Recovery", "is_correct": false}, {"text": "Domain Naming Context, Schema, Configuration, Global Catalog (PAS), DNS/partizioni applicative", "is_correct": true}, {"text": "SYSVOL, Group Policy, Kerberos Tickets, DNS Cache", "is_correct": false}, {"text": "Users, Computers, Printers, Shared Folders", "is_correct": false}]', '{"right_answer": {"selected_answer": "Domain Naming Context, Schema, Configuration, Global Catalog (PAS), DNS/partizioni applicative", "rationale": "La slide 21 elenca le partizioni logiche del data store: Domain Naming Context, Schema, Configuration, Global Catalog (Partial Attribute Set) e DNS o altre partizioni applicative.", "original_reference": {"path": "materiale/BERNABEI - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft/Slide/01.Introduzione ad Active Directory DS.pptx", "page": 21}}, "wrong_answers": [{"answer_text": "SYSVOL, Group Policy, Kerberos Tickets, DNS Cache", "interpretation": "Confusione tra partizioni logiche e altri componenti AD", "why_wrong": "SYSVOL e una cartella separata dal database, le Group Policy sono oggetti contenuti nel database, e Kerberos Tickets non sono partizioni del data store."}, {"answer_text": "Users, Computers, Printers, Shared Folders", "interpretation": "Confusione tra partizioni e oggetti contenuti nel database", "why_wrong": "Questi sono tipi di oggetti memorizzati all''interno del database, non le partizioni logiche del data store."}, {"answer_text": "C:\\ drive, D:\\ drive, System Reserved, Recovery", "interpretation": "Confusione tra partizioni logiche AD e partizioni disco", "why_wrong": "Le partizioni logiche di AD sono divisioni interne del database ntds.dit, non partizioni del disco fisico."}]}', '["BERNABEI_01_IntrodAD_easy_004", "BERNABEI_01_IntrodAD_hard_001"]');
INSERT INTO questions (batch_id, id, curriculum_area, lecturer, topic, clusters, cognitive_level, question_type, distractor_strategy, difficulty, source_file, source_file_page_number, topics, question_prompt, choices, explanation, related_questions) VALUES (7, 'BONONI_NetworkSecurity_easy_006', 'MOD. 2 - Sicurezza delle reti e dei sistemi', 'BONONI', 'Tipi di firewall', '["Firewall e controllo accessi"]', 'remember', 'factual', 'alternative_classification', 'easy', 'materiale/BONONI - Sicurezza delle reti e dei sistemi/LBononi_IFTS_FAV_3_ NetworkSecurity_Bononi_IFTS2015.pdf', 53, '["packet filtering", "application gateway", "firewall"]', 'Quali sono i due tipi principali di firewall descritti nel materiale?', '[{"text": "Stateful firewall e stateless firewall", "is_correct": false}, {"text": "Network firewall e host firewall", "is_correct": false}, {"text": "Packet filtering e application-level gateway", "is_correct": true}, {"text": "Hardware firewall e software firewall", "is_correct": false}]', '{"right_answer": {"selected_answer": "Packet filtering e application-level gateway", "rationale": "Il materiale descrive due tipi di firewall: il packet filtering (che filtra pacchetto per pacchetto in base a IP, porte, ICMP, bit SYN/ACK) e l''application-level gateway (che filtra anche in base ai dati applicativi).", "original_reference": {"path": "materiale/BONONI - Sicurezza delle reti e dei sistemi/LBononi_IFTS_FAV_3_ NetworkSecurity_Bononi_IFTS2015.pdf", "page": 53}}, "wrong_answers": [{"answer_text": "Hardware firewall e software firewall", "interpretation": "Lo studente confonde la classificazione funzionale con quella implementativa", "why_wrong": "La distinzione hardware/software riguarda l''implementazione, non la classificazione funzionale presentata nel materiale."}, {"answer_text": "Stateful firewall e stateless firewall", "interpretation": "Lo studente cita una classificazione diversa", "why_wrong": "Sebbene valida in altri contesti, questa classificazione non e'' quella presentata nel materiale del corso."}, {"answer_text": "Network firewall e host firewall", "interpretation": "Lo studente confonde con una classificazione per posizionamento", "why_wrong": "Questa classificazione si basa sul punto di installazione, non sulla modalita'' di filtraggio descritta nel materiale."}]}', '["BONONI_NetworkSecurity_very_easy_003", "BONONI_NetworkSecurity_medium_005"]');
INSERT INTO questions (batch_id, id, curriculum_area, lecturer, topic, clusters, cognitive_level, question_type, distractor_strategy, difficulty, source_file, source_file_page_number, topics, question_prompt, choices, explanation, related_questions) VALUES (7, 'ANZALONE_4a_storage_medium_001', 'MOD. 5 - Cloud privato e cloud pubblico al servizio della business continuity e disaster recovery', 'ANZALONE', 'Thin provisioning vs thick provisioning', '["Storage", "VMware vSphere", "Provisioning"]', 'understand', 'comparison', 'common_misconception', 'medium', 'materiale/ANZALONE - Cloud privato e cloud pubblico al servizio della business continuity e disaster recovery/4a_storage.pdf', 63, '["thin provisioning", "thick provisioning", "VMDK", "allocazione storage"]', 'Qual e'' la differenza principale tra un disco virtuale thick provisioned e uno thin provisioned in VMware?', '[{"text": "Il thick supporta solo file di testo, il thin supporta qualsiasi tipo di file", "is_correct": false}, {"text": "Il thick pre-alloca tutto lo spazio alla creazione, il thin alloca spazio solo quando viene scritto", "is_correct": true}, {"text": "Il thick e'' piu'' veloce in lettura, il thin e'' piu'' veloce in scrittura in tutti i casi", "is_correct": false}, {"text": "Il thick puo'' essere utilizzato solo su datastore locali, il thin solo su SAN", "is_correct": false}]', '{"right_answer": {"selected_answer": "Il thick pre-alloca tutto lo spazio alla creazione, il thin alloca spazio solo quando viene scritto", "rationale": "Un disco thick provisioned pre-alloca tutto lo spazio specificato alla creazione (es. 10 GB = 10 GB allocati subito). Un disco thin provisioned non pre-alloca: lo spazio fisico viene allocato on-demand solo quando i blocchi vengono effettivamente scritti.", "original_reference": {"path": "materiale/ANZALONE - Cloud privato e cloud pubblico al servizio della business continuity e disaster recovery/4a_storage.pdf", "page": 63}}, "wrong_answers": [{"answer_text": "Il thick supporta solo file di testo, il thin supporta qualsiasi tipo di file", "interpretation": "Limita erroneamente il tipo di contenuto in base al provisioning", "why_wrong": "Il tipo di provisioning non ha alcuna relazione con il tipo di file supportato. Entrambi i formati possono contenere qualsiasi tipo di dato."}, {"answer_text": "Il thick puo'' essere utilizzato solo su datastore locali, il thin solo su SAN", "interpretation": "Limita ciascun tipo di provisioning a un tipo di storage specifico", "why_wrong": "Sia il thick che il thin provisioning possono essere utilizzati su qualsiasi tipo di datastore (locale, SAN, NAS)."}, {"answer_text": "Il thick e'' piu'' veloce in lettura, il thin e'' piu'' veloce in scrittura in tutti i casi", "interpretation": "Generalizza erroneamente le prestazioni", "why_wrong": "Il thick eager zeroed puo'' avere prestazioni di scrittura migliori perche'' i blocchi sono gia'' azzerati. Il thin non e'' piu'' veloce in scrittura: deve allocare blocchi on-demand."}]}', '["ANZALONE_4a_storage_medium_002"]');
INSERT INTO questions (batch_id, id, curriculum_area, lecturer, topic, clusters, cognitive_level, question_type, distractor_strategy, difficulty, source_file, source_file_page_number, topics, question_prompt, choices, explanation, related_questions) VALUES (7, 'BERNABEI_04_GestireGruppi_easy_001', 'MOD. 3 - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft', 'BERNABEI', 'Distribution vs Security groups', '["Gestione Gruppi", "Active Directory"]', 'remember', 'comparison', 'plausible_alternative', 'easy', 'materiale/BERNABEI - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft/Slide/04.Gestire i gruppi.pptx', 11, '["distribution groups", "security groups", "email", "SID"]', 'Quale affermazione descrive correttamente i Distribution groups rispetto ai Security groups?', '[{"text": "I Distribution groups sono utilizzati solo da applicazioni email e non possiedono un SID", "is_correct": true}, {"text": "I Distribution groups e i Security groups sono funzionalmente identici", "is_correct": false}, {"text": "I Distribution groups possono essere utilizzati nelle ACL ma non come liste di distribuzione", "is_correct": false}, {"text": "I Distribution groups possiedono un SID e possono ricevere permessi", "is_correct": false}]', '{"right_answer": {"selected_answer": "I Distribution groups sono utilizzati solo da applicazioni email e non possiedono un SID", "rationale": "La slide 11 specifica che i Distribution groups sono utilizzati solo da applicazioni email e non possiedono un SID, pertanto non possono ricevere permessi.", "original_reference": {"path": "materiale/BERNABEI - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft/Slide/04.Gestire i gruppi.pptx", "page": 11}}, "wrong_answers": [{"answer_text": "I Distribution groups possiedono un SID e possono ricevere permessi", "interpretation": "I Distribution groups come security principal", "why_wrong": "I Distribution groups non possiedono un SID; questa e una caratteristica esclusiva dei Security groups."}, {"answer_text": "I Distribution groups possono essere utilizzati nelle ACL ma non come liste di distribuzione", "interpretation": "Inversione delle funzionalita dei Distribution groups", "why_wrong": "E esattamente il contrario: i Distribution groups vengono usati come liste di distribuzione email e non possono essere inseriti nelle ACL."}, {"answer_text": "I Distribution groups e i Security groups sono funzionalmente identici", "interpretation": "Nessuna differenza tra i due tipi", "why_wrong": "I due tipi hanno funzionalita distinte: i Security groups hanno un SID e gestiscono permessi, i Distribution groups servono solo per la distribuzione email."}]}', '["BERNABEI_04_GestireGruppi_very_easy_001", "BERNABEI_04_GestireGruppi_medium_001"]');
INSERT INTO questions (batch_id, id, curriculum_area, lecturer, topic, clusters, cognitive_level, question_type, distractor_strategy, difficulty, source_file, source_file_page_number, topics, question_prompt, choices, explanation, related_questions) VALUES (7, 'ANZALONE_2b_ft_bc_easy_002', 'MOD. 5 - Cloud privato e cloud pubblico al servizio della business continuity e disaster recovery', 'ANZALONE', 'Business Recovery Plan vs Business Resumption Plan', '["Business Continuity e Disaster Recovery"]', 'understand', 'comparison', 'obviously_wrong', 'easy', 'materiale/ANZALONE - Cloud privato e cloud pubblico al servizio della business continuity e disaster recovery/2b_ft_bc.pdf', 5, '["business recovery plan", "business resumption plan", "alternate location"]', 'Qual è la caratteristica distintiva del business recovery plan rispetto al business resumption plan?', '[{"text": "Prevede il recupero delle funzioni aziendali in una sede alternativa", "is_correct": true}, {"text": "È dedicato al backup giornaliero automatico dei database aziendali", "is_correct": false}, {"text": "Prevede il mantenimento dei servizi essenziali nella sede della crisi", "is_correct": false}, {"text": "Si occupa esclusivamente della protezione dei dati nel cloud pubblico", "is_correct": false}]', '{"right_answer": {"selected_answer": "Prevede il recupero delle funzioni aziendali in una sede alternativa", "rationale": "Il business recovery plan specifica i mezzi per recuperare le funzioni aziendali in una sede alternativa (alternate location), a differenza del business resumption plan che si concentra sul mantenimento dei servizi essenziali nella sede della crisi.", "original_reference": {"path": "materiale/ANZALONE - Cloud privato e cloud pubblico al servizio della business continuity e disaster recovery/2b_ft_bc.pdf", "page": 5}}, "wrong_answers": [{"answer_text": "Prevede il mantenimento dei servizi essenziali nella sede della crisi", "interpretation": "Confonde recovery plan con resumption plan", "why_wrong": "Questa è la definizione del business resumption plan, non del business recovery plan. Il resumption plan opera nella sede della crisi, il recovery plan in una sede alternativa."}, {"answer_text": "Si occupa esclusivamente della protezione dei dati nel cloud pubblico", "interpretation": "Focus errato sul cloud", "why_wrong": "Il business recovery plan non è limitato al cloud pubblico; riguarda il recupero delle funzioni aziendali in una qualsiasi sede alternativa."}, {"answer_text": "È dedicato al backup giornaliero automatico dei database aziendali", "interpretation": "Attività operativa di backup", "why_wrong": "Il backup dei database è un''attività operativa specifica, non la definizione del business recovery plan che ha una portata strategica molto più ampia."}]}', '["ANZALONE_2b_ft_bc_easy_001"]');
INSERT INTO questions (batch_id, id, curriculum_area, lecturer, topic, clusters, cognitive_level, question_type, distractor_strategy, difficulty, source_file, source_file_page_number, topics, question_prompt, choices, explanation, related_questions) VALUES (7, 'FORONI_4_CertificatiPKI_medium_004', 'MOD. 4 - Implementazione e verifica della sicurezza delle reti e dei sistemi', 'FORONI', 'Certificati wildcard e multi-dominio', '["wildcard certificate", "multi-domain certificate", "singolo dominio", "FQDN"]', 'understand', 'comparison', 'common_confusion', 'medium', 'materiale/FORONI/Lezioni - Implementazione e verifica della sicurezza delle reti e dei sistemi/4Certificati e PKI_2017.pdf', 70, '["singolo dominio", "wildcard", "multi-dominio", "sottodomini"]', 'Un''azienda possiede il dominio esempio.org e vuole proteggere con SSL anche a.esempio.org, b.esempio.org e c.esempio.org. Quale tipo di certificato e'' piu'' adatto?', '[{"text": "Certificato singolo dominio: basta un certificato per esempio.org e copre automaticamente tutti i sottodomini", "is_correct": false}, {"text": "Certificato EV: necessario perche'' richiede la protezione di piu'' URL", "is_correct": false}, {"text": "Certificato Wildcard: protegge un nome di dominio e un numero illimitato di suoi sottodomini", "is_correct": true}, {"text": "Certificato Multi-Dominio: necessario perche'' si tratta di quattro domini diversi", "is_correct": false}]', '{"right_answer": {"selected_answer": "Certificato Wildcard: protegge un nome di dominio e un numero illimitato di suoi sottodomini", "rationale": "La slide 70 spiega: ''Wildcard: Protegge un nome di dominio ed un numero illimitato di suoi sottodomini. Ad es., a.esempio.org, b.esempio.org''. Essendo tutti sottodomini dello stesso dominio, il wildcard e'' la scelta ideale.", "original_reference": {"path": "materiale/FORONI/Lezioni - Implementazione e verifica della sicurezza delle reti e dei sistemi/4Certificati e PKI_2017.pdf", "page": 70}}, "wrong_answers": [{"answer_text": "Certificato singolo dominio: basta un certificato per esempio.org e copre automaticamente tutti i sottodomini", "interpretation": "Pensa che un certificato singolo dominio copra anche i sottodomini", "why_wrong": "Un certificato singolo dominio protegge solo un Fully Qualified Domain Name (es. www.esempio.org), NON i suoi sottodomini automaticamente."}, {"answer_text": "Certificato Multi-Dominio: necessario perche'' si tratta di quattro domini diversi", "interpretation": "Confonde sottodomini con domini diversi", "why_wrong": "a.esempio.org, b.esempio.org sono sottodomini dello stesso dominio, non domini diversi. Il Multi-Dominio serve per domini completamente diversi (es. esempio.org e esempio.com)."}, {"answer_text": "Certificato EV: necessario perche'' richiede la protezione di piu'' URL", "interpretation": "Confonde il tipo di certificato (DV/OV/EV) con la copertura dei domini", "why_wrong": "EV indica il livello di validazione, non la copertura dei domini. Un certificato EV puo'' essere singolo dominio. Per coprire i sottodomini serve un wildcard."}]}', '["FORONI_4_CertificatiPKI_medium_003"]');
INSERT INTO questions (batch_id, id, curriculum_area, lecturer, topic, clusters, cognitive_level, question_type, distractor_strategy, difficulty, source_file, source_file_page_number, topics, question_prompt, choices, explanation, related_questions) VALUES (7, 'FORONI_2_Fondamenti_very_easy_001', 'MOD. 4 - Implementazione e verifica della sicurezza delle reti e dei sistemi', 'FORONI', 'Motivi per adottare misure di sicurezza', '["minacce", "sicurezza informatica", "catastrofi", "malware"]', 'remember', 'factual', 'common_confusion', 'very_easy', 'materiale/FORONI/Lezioni - Implementazione e verifica della sicurezza delle reti e dei sistemi/2.Fondamenti Cybersecurity .pdf', 5, '["catastrofi naturali", "software malevolo", "hacker", "attivita'' illecite"]', 'Quali sono le quattro categorie di minacce da cui ci si vuole proteggere adottando misure di sicurezza informatica?', '[{"text": "Bug software, guasti hardware, errori di compilazione, mancanza di documentazione", "is_correct": false}, {"text": "Virus biologici, interferenze radio, obsolescenza dei sistemi, errori di battitura", "is_correct": false}, {"text": "Catastrofi naturali e incidenti imprevisti, software malevolo, aggressione da parte di hacker, attivita'' scorrette e illecite", "is_correct": true}]', '{"right_answer": {"selected_answer": "Catastrofi naturali e incidenti imprevisti, software malevolo, aggressione da parte di hacker, attivita'' scorrette e illecite", "rationale": "Il materiale elenca esplicitamente queste quattro categorie: 1) catastrofi naturali e incidenti imprevisti, 2) aggressione da parte di hacker, 3) software malevolo, 4) attivita'' scorrette e illecite.", "original_reference": {"path": "materiale/FORONI/Lezioni - Implementazione e verifica della sicurezza delle reti e dei sistemi/2.Fondamenti Cybersecurity .pdf", "page": 5}}, "wrong_answers": [{"answer_text": "Bug software, guasti hardware, errori di compilazione, mancanza di documentazione", "interpretation": "Confonde le minacce alla sicurezza con problemi generici di qualita'' del software", "why_wrong": "Questi sono problemi di qualita'' del software e manutenzione, non le minacce alla sicurezza informatica descritte nel materiale."}, {"answer_text": "Virus biologici, interferenze radio, obsolescenza dei sistemi, errori di battitura", "interpretation": "Mescola concetti irrilevanti con problemi generici IT", "why_wrong": "I virus biologici e le interferenze radio non rientrano nelle categorie di minacce alla sicurezza informatica. Il materiale identifica minacce specifiche come malware, hacker e attivita'' illecite."}]}', '["FORONI_2_Fondamenti_easy_001"]');
INSERT INTO questions (batch_id, id, curriculum_area, lecturer, topic, clusters, cognitive_level, question_type, distractor_strategy, difficulty, source_file, source_file_page_number, topics, question_prompt, choices, explanation, related_questions) VALUES (7, 'ANZALONE_1a_virtualIntro_easy_001', 'MOD. 5 - Cloud privato e cloud pubblico al servizio della business continuity e disaster recovery', 'ANZALONE', 'Tipi di virtualizzazione', '["Virtualizzazione e Hypervisor"]', 'remember', 'concept', 'obviously_wrong', 'easy', 'materiale/ANZALONE - Cloud privato e cloud pubblico al servizio della business continuity e disaster recovery/1a_virtualIntro.pdf', 8, '["storage virtualization", "server virtualization", "network virtualization", "application virtualization"]', 'Quali sono i quattro tipi principali di virtualizzazione citati nel corso?', '[{"text": "CPU, GPU, RAM e SSD virtualization", "is_correct": false}, {"text": "Audio, video, stampa e fax virtualization", "is_correct": false}, {"text": "Email, chat, browser e desktop virtualization", "is_correct": false}, {"text": "Storage, server, network e application virtualization", "is_correct": true}]', '{"right_answer": {"selected_answer": "Storage, server, network e application virtualization", "rationale": "Le slide elencano quattro tipi principali di virtualizzazione: storage virtualization (aggregazione di dispositivi di storage), server virtualization (partizionamento di un server fisico in VM), network virtualization (segmentazione logica di una rete fisica) e application virtualization (accesso alle applicazioni senza installazione locale).", "original_reference": {"path": "materiale/ANZALONE - Cloud privato e cloud pubblico al servizio della business continuity e disaster recovery/1a_virtualIntro.pdf", "page": 8}}, "wrong_answers": [{"answer_text": "Audio, video, stampa e fax virtualization", "interpretation": "Questa risposta elenca periferiche multimediali e di ufficio", "why_wrong": "Non si tratta di categorie di virtualizzazione riconosciute. Storage, server, network e application sono i tipi principali."}, {"answer_text": "CPU, GPU, RAM e SSD virtualization", "interpretation": "Questa risposta elenca singoli componenti hardware", "why_wrong": "Questi sono componenti hardware individuali, non categorie di virtualizzazione. La virtualizzazione opera a livelli piu'' alti: storage, server, network, application."}, {"answer_text": "Email, chat, browser e desktop virtualization", "interpretation": "Questa risposta confonde applicazioni con categorie di virtualizzazione", "why_wrong": "Questi sono esempi di applicazioni o servizi, non le categorie di virtualizzazione trattate nel corso."}]}', '[]');
INSERT INTO questions (batch_id, id, curriculum_area, lecturer, topic, clusters, cognitive_level, question_type, distractor_strategy, difficulty, source_file, source_file_page_number, topics, question_prompt, choices, explanation, related_questions) VALUES (7, 'BERNABEI_09_AdminDC_easy_002', 'MOD. 3 - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft', 'BERNABEI', 'Tre opzioni di promozione DC', '["Domain Controller", "Installazione DC", "Active Directory"]', 'remember', 'factual', 'plausible_wrong', 'easy', 'materiale/BERNABEI - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft/Slide/09.Amministrazione dei Domain Controller.pptx', 4, '["DC promotion", "foresta", "dominio", "Promote DC"]', 'Durante la promozione di un server a Domain Controller, quale tra le seguenti NON e un''opzione disponibile nel wizard?', '[{"text": "Add a new forest", "is_correct": false}, {"text": "Add a domain controller to an existing domain", "is_correct": false}, {"text": "Add a new organizational unit to an existing domain", "is_correct": true}]', '{"right_answer": {"selected_answer": "Add a new organizational unit to an existing domain", "rationale": "La slide 4 elenca le tre opzioni del wizard: Add a new forest, Add a new domain to an existing forest, Add a domain controller to an existing domain. L''aggiunta di una OU non e un''opzione del wizard di promozione DC.", "original_reference": {"path": "materiale/BERNABEI - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft/Slide/09.Amministrazione dei Domain Controller.pptx", "page": 4}}, "wrong_answers": [{"answer_text": "Add a new forest", "interpretation": "Opzione non valida nel wizard", "why_wrong": "Add a new forest e una delle tre opzioni effettivamente presenti nel wizard di promozione DC, come indicato nella slide 4."}, {"answer_text": "Add a domain controller to an existing domain", "interpretation": "Opzione non valida nel wizard", "why_wrong": "Add a domain controller to an existing domain e una delle tre opzioni reali del wizard, usata per aggiungere un ulteriore DC a un dominio esistente."}]}', '["BERNABEI_09_AdminDC_very_easy_002", "BERNABEI_09_AdminDC_easy_003"]');
INSERT INTO questions (batch_id, id, curriculum_area, lecturer, topic, clusters, cognitive_level, question_type, distractor_strategy, difficulty, source_file, source_file_page_number, topics, question_prompt, choices, explanation, related_questions) VALUES (7, 'FORONI_1_CIA_easy_002', 'MOD. 4 - Implementazione e verifica della sicurezza delle reti e dei sistemi', 'FORONI', 'Integrita'' (Integrity)', '["integrity", "integrita''", "manomissione", "alterazione"]', 'understand', 'conceptual', 'partial_truth', 'easy', 'materiale/FORONI/Lezioni - Implementazione e verifica della sicurezza delle reti e dei sistemi/1.CIA.pdf', 5, '["integrita''", "integrity", "manomissione", "alterazione"]', 'Secondo il principio di integrita'' (Integrity), oltre ai dati, cosa deve essere protetto da alterazioni?', '[{"text": "Unicamente le credenziali di accesso degli utenti", "is_correct": false}, {"text": "Solo i documenti classificati come riservati", "is_correct": false}, {"text": "Esclusivamente i backup archiviati su supporti fisici", "is_correct": false}, {"text": "I sistemi operativi e i software applicativi", "is_correct": true}]', '{"right_answer": {"selected_answer": "I sistemi operativi e i software applicativi", "rationale": "Il materiale specifica che l''integrita'' riguarda anche i sistemi operativi e i software applicativi, la cui alterazione puo'' consentire intromissioni e conseguenti danni.", "original_reference": {"path": "materiale/FORONI/Lezioni - Implementazione e verifica della sicurezza delle reti e dei sistemi/1.CIA.pdf", "page": 5}}, "wrong_answers": [{"answer_text": "Solo i documenti classificati come riservati", "interpretation": "Limita erroneamente l''integrita'' ai soli documenti riservati", "why_wrong": "L''integrita'' si applica a tutte le informazioni, i beni digitali e i software, non solo ai documenti classificati."}, {"answer_text": "Esclusivamente i backup archiviati su supporti fisici", "interpretation": "Restringe il concetto di integrita'' ai soli backup fisici", "why_wrong": "L''integrita'' copre tutti i dati e i software del sistema, non solo i backup. Inoltre, il materiale enfatizza anche i sistemi operativi e le applicazioni."}, {"answer_text": "Unicamente le credenziali di accesso degli utenti", "interpretation": "Restringe il concetto di integrita'' alle sole credenziali", "why_wrong": "Le credenziali sono solo una parte delle informazioni da proteggere. L''integrita'' riguarda tutti i dati, i digital asset e i software."}]}', '["FORONI_1_CIA_very_easy_001", "FORONI_1_CIA_medium_001"]');
INSERT INTO questions (batch_id, id, curriculum_area, lecturer, topic, clusters, cognitive_level, question_type, distractor_strategy, difficulty, source_file, source_file_page_number, topics, question_prompt, choices, explanation, related_questions) VALUES (7, 'FORONI_07_GatheringInfo_easy_004', 'MOD. 4 - Implementazione e verifica della sicurezza delle reti e dei sistemi', 'FORONI', 'Record DNS di tipo MX', '["DNS", "record MX", "mail server"]', 'remember', 'factual', 'term_swap', 'easy', 'materiale/FORONI/Lezioni - Implementazione e verifica della sicurezza delle reti e dei sistemi/7.Gathering Target Information.pdf', 12, '["DNS", "record MX", "mail exchange", "mail server"]', 'Cosa identifica un record DNS di tipo MX (Mail Exchange)?', '[{"text": "Il mail server responsabile per il dominio", "is_correct": true}, {"text": "L''indirizzo IP associato a un hostname", "is_correct": false}, {"text": "Un alias o nome aggiuntivo per un record di indirizzo", "is_correct": false}, {"text": "Il DNS server responsabile delle informazioni del dominio", "is_correct": false}]', '{"right_answer": {"selected_answer": "Il mail server responsabile per il dominio", "rationale": "Il materiale specifica a pagina 12 che il record MX (Mail Exchange) ''Identifies the mail server for the domain''.", "original_reference": {"path": "materiale/FORONI/Lezioni - Implementazione e verifica della sicurezza delle reti e dei sistemi/7.Gathering Target Information.pdf", "page": 12}}, "wrong_answers": [{"answer_text": "L''indirizzo IP associato a un hostname", "interpretation": "Confonde il record MX con il record A", "why_wrong": "La mappatura hostname-IP e'' funzione del record A (Address). Il record MX identifica specificamente il server di posta del dominio."}, {"answer_text": "Un alias o nome aggiuntivo per un record di indirizzo", "interpretation": "Confonde il record MX con il record CNAME", "why_wrong": "Gli alias sono gestiti dal record CNAME (Canonical Name). Il record MX e'' dedicato esclusivamente all''identificazione dei mail server."}, {"answer_text": "Il DNS server responsabile delle informazioni del dominio", "interpretation": "Confonde il record MX con il record SOA", "why_wrong": "L''identificazione del DNS server responsabile e'' funzione del record SOA (Start of Authority). Il record MX riguarda il server di posta, non il DNS."}]}', '[]');
INSERT INTO questions (batch_id, id, curriculum_area, lecturer, topic, clusters, cognitive_level, question_type, distractor_strategy, difficulty, source_file, source_file_page_number, topics, question_prompt, choices, explanation, related_questions) VALUES (7, 'BONONI_2b_netmask_medium_003', 'MOD. 2 - Sicurezza delle reti e dei sistemi', 'BONONI', 'Effetto del cambio di netmask', '["Netmask e subnetting"]', 'analyze', 'scenario', 'opposite_mechanism', 'medium', 'materiale/BONONI - Sicurezza delle reti e dei sistemi/LBononi_IFTS_FAV_2b_esempio_netmask.pdf', 4, '["netmask diversa", "stessa sottorete", "255.255.248.0"]', 'Se per gli stessi host 130.136.169.4 e 130.136.160.11 si cambia la netmask da 255.255.224.0 a 255.255.248.0, cosa cambia?', '[{"text": "Con 255.255.248.0 la rete ha meno sottoreti, quindi i due host si trovano ora nella stessa sottorete", "is_correct": false}, {"text": "Con 255.255.248.0 i due host appartengono a sottoreti diverse (sottorete 21 e sottorete 20), quindi devono comunicare tramite il router", "is_correct": true}, {"text": "Non cambia nulla: i due host restano sempre sulla stessa sottorete indipendentemente dalla netmask", "is_correct": false}]', '{"right_answer": {"selected_answer": "Con 255.255.248.0 i due host appartengono a sottoreti diverse (sottorete 21 e sottorete 20), quindi devono comunicare tramite il router", "rationale": "Con netmask 255.255.248.0 (5 bit per le sottoreti, 32 sottoreti): 169 AND 248 = 168 (sottorete 21) e 160 AND 248 = 160 (sottorete 20). Le sottoreti sono diverse, quindi i pacchetti devono passare per il default router.", "original_reference": {"path": "materiale/BONONI - Sicurezza delle reti e dei sistemi/LBononi_IFTS_FAV_2b_esempio_netmask.pdf", "page": 4}}, "wrong_answers": [{"answer_text": "Non cambia nulla: i due host restano sempre sulla stessa sottorete indipendentemente dalla netmask", "interpretation": "Nega l''effetto della netmask", "why_wrong": "La netmask determina la suddivisione in sottoreti. Cambiando netmask, la stessa coppia di IP puo'' risultare sulla stessa o su sottoreti diverse."}, {"answer_text": "Con 255.255.248.0 la rete ha meno sottoreti, quindi i due host si trovano ora nella stessa sottorete", "interpretation": "Inverte la relazione: piu'' bit = piu'' sottoreti, non meno", "why_wrong": "255.255.248.0 usa 5 bit per le sottoreti (32 sottoreti), piu'' di 255.255.224.0 (3 bit, 8 sottoreti). Piu'' sottoreti e piu'' piccole significano piu'' probabilita'' che due host finiscano in sottoreti diverse."}]}', '[]');
