INSERT INTO questions (batch_id, id, curriculum_area, lecturer, topic, clusters, cognitive_level, question_type, distractor_strategy, difficulty, source_file, source_file_page_number, topics, question_prompt, choices, explanation, related_questions) VALUES (11, 'BERNABEI_01_IntrodAD_easy_003', 'MOD. 3 - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft', 'BERNABEI', 'Workgroup vs Domain', '["Active Directory Domain Services", "Modelli di autenticazione"]', 'understand', 'comparison', 'common_misconception', 'easy', 'materiale/BERNABEI - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft/Slide/01.Introduzione ad Active Directory DS.pptx', 16, '["workgroup", "dominio", "SAM", "identity store"]', 'In un Workgroup, dove viene memorizzato l''identity store rispetto a un dominio Active Directory?', '[{"text": "In un unico database LDAP condiviso tra tutte le macchine del workgroup", "is_correct": false}, {"text": "In un cloud storage accessibile via Internet da tutti i PC del workgroup", "is_correct": false}, {"text": "Nel SAM (Security Accounts Manager) locale di ciascuna macchina, senza condivisione centralizzata", "is_correct": true}, {"text": "Nel file ntds.dit presente su ogni workstation del gruppo", "is_correct": false}]', '{"right_answer": {"selected_answer": "Nel SAM (Security Accounts Manager) locale di ciascuna macchina, senza condivisione centralizzata", "rationale": "La slide 16 spiega che in un Workgroup l''identity store e il Security Accounts Manager (SAM) della macchina Windows locale, senza alcun identity store condiviso.", "original_reference": {"path": "materiale/BERNABEI - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft/Slide/01.Introduzione ad Active Directory DS.pptx", "page": 16}}, "wrong_answers": [{"answer_text": "In un unico database LDAP condiviso tra tutte le macchine del workgroup", "interpretation": "Attribuzione al workgroup delle caratteristiche del dominio", "why_wrong": "Il database LDAP condiviso e una caratteristica del dominio Active Directory, non del workgroup."}, {"answer_text": "Nel file ntds.dit presente su ogni workstation del gruppo", "interpretation": "Confusione tra SAM e ntds.dit", "why_wrong": "Il file ntds.dit e il database di Active Directory presente solo sui Domain Controller, non sulle workstation in workgroup."}, {"answer_text": "In un cloud storage accessibile via Internet da tutti i PC del workgroup", "interpretation": "Modernizzazione errata del concetto di workgroup", "why_wrong": "Il workgroup tradizionale utilizza il SAM locale; non prevede alcun identity store in cloud."}]}', '["BERNABEI_01_IntrodAD_easy_004", "BERNABEI_01_IntrodAD_very_easy_001"]');
INSERT INTO questions (batch_id, id, curriculum_area, lecturer, topic, clusters, cognitive_level, question_type, distractor_strategy, difficulty, source_file, source_file_page_number, topics, question_prompt, choices, explanation, related_questions) VALUES (11, 'BERNABEI_08_DNS_medium_005', 'MOD. 3 - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft', 'BERNABEI', 'AD DS e DNS - Record SRV', '["DNS", "Active Directory", "SRV records", "DC Locator"]', 'understand', 'conceptual', 'related_concept', 'medium', 'materiale/BERNABEI - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft/Slide/08.Configurazione di un server Domain Name System (DNS).pptx', 22, '["AD DS", "DNS", "SRV records", "Domain Controller", "DC Locator"]', 'In un dominio Active Directory, quale ruolo svolgono i record SRV registrati dai Domain Controller nel DNS?', '[{"text": "Definiscono le Group Policy da applicare ai client durante il logon", "is_correct": false}, {"text": "Garantiscono la crittografia delle comunicazioni LDAP fra client e Domain Controller", "is_correct": false}, {"text": "Permettono ai client di individuare i Domain Controller disponibili nel dominio", "is_correct": true}]', '{"right_answer": {"selected_answer": "Permettono ai client di individuare i Domain Controller disponibili nel dominio", "rationale": "La slide 22 spiega che i DCs registrano i propri record Service Locator (SRV) direttamente sul DNS e i clients utilizzano tali record per individuare i DCs disponibili.", "original_reference": {"path": "materiale/BERNABEI - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft/Slide/08.Configurazione di un server Domain Name System (DNS).pptx", "page": 22}}, "wrong_answers": [{"answer_text": "Garantiscono la crittografia delle comunicazioni LDAP fra client e Domain Controller", "interpretation": "Record SRV come meccanismo di crittografia", "why_wrong": "I record SRV non hanno alcuna funzione di crittografia. Servono esclusivamente per la localizzazione dei servizi (Service Locator), permettendo ai client di trovare i DC."}, {"answer_text": "Definiscono le Group Policy da applicare ai client durante il logon", "interpretation": "Record SRV come veicolo per le GPO", "why_wrong": "I record SRV non contengono informazioni sulle Group Policy. Le GPO sono gestite tramite Active Directory e la cartella SYSVOL, non tramite record DNS."}]}', '["BERNABEI_08_DNS_hard_002", "BERNABEI_08_DNS_hard_004"]');
INSERT INTO questions (batch_id, id, curriculum_area, lecturer, topic, clusters, cognitive_level, question_type, distractor_strategy, difficulty, source_file, source_file_page_number, topics, question_prompt, choices, explanation, related_questions) VALUES (11, 'BERNABEI_09_AdminDC_very_easy_002', 'MOD. 3 - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft', 'BERNABEI', 'Promozione DC tramite GUI', '["Domain Controller", "Installazione DC", "Server Manager"]', 'remember', 'definition', 'obviously_wrong', 'very_easy', 'materiale/BERNABEI - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft/Slide/09.Amministrazione dei Domain Controller.pptx', 3, '["DC installation", "Server Manager", "AD DS"]', 'Qual e il primo passo per installare un Domain Controller tramite interfaccia grafica in Server Manager?', '[{"text": "Eseguire immediatamente il comando Install-ADDSForest da PowerShell", "is_correct": false}, {"text": "Formattare il disco e reinstallare il sistema operativo Windows Server", "is_correct": false}, {"text": "Aggiungere il ruolo Active Directory Domain Services tramite Add Roles and Features", "is_correct": true}]', '{"right_answer": {"selected_answer": "Aggiungere il ruolo Active Directory Domain Services tramite Add Roles and Features", "rationale": "La slide 3 indica chiaramente la sequenza: aprire Server Manager, fare clic su Add Roles and Features e selezionare Active Directory Domain Services nella sezione Server Roles.", "original_reference": {"path": "materiale/BERNABEI - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft/Slide/09.Amministrazione dei Domain Controller.pptx", "page": 3}}, "wrong_answers": [{"answer_text": "Eseguire immediatamente il comando Install-ADDSForest da PowerShell", "interpretation": "Confusione tra il metodo GUI e il metodo PowerShell", "why_wrong": "Install-ADDSForest e il cmdlet PowerShell per configurare una nuova foresta, non il primo passo del metodo tramite interfaccia grafica. Da GUI si utilizza il wizard Add Roles and Features."}, {"answer_text": "Formattare il disco e reinstallare il sistema operativo Windows Server", "interpretation": "Reinstallazione completa del sistema", "why_wrong": "Non e necessario reinstallare il sistema operativo. L''installazione del ruolo AD DS avviene su un Windows Server gia funzionante tramite Server Manager."}]}', '["BERNABEI_09_AdminDC_easy_002", "BERNABEI_09_AdminDC_easy_003"]');
INSERT INTO questions (batch_id, id, curriculum_area, lecturer, topic, clusters, cognitive_level, question_type, distractor_strategy, difficulty, source_file, source_file_page_number, topics, question_prompt, choices, explanation, related_questions) VALUES (11, 'BERNABEI_12_DominiForeste_medium_004', 'MOD. 3 - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft', 'BERNABEI', 'Selective Authentication', '["Trust", "Selective Authentication", "Sicurezza"]', 'understand', 'conceptual', 'opposite_meaning', 'medium', 'materiale/BERNABEI - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft/Slide/12.Gestire domini e foreste.pptx', 22, '["selective authentication", "Allowed To Authenticate", "sicurezza", "trust"]', 'Cosa comporta l''attivazione della selective authentication su una relazione di trust?', '[{"text": "Riduce i rischi per la sicurezza eliminando l''accesso automatico tramite Authenticated Users; gli utenti devono ricevere la permission ''Allowed To Authenticate'' sugli oggetti computer", "is_correct": true}, {"text": "Disabilita completamente la trust e richiede che ogni utente crei un account locale nel dominio di destinazione", "is_correct": false}, {"text": "Permette a tutti gli utenti del dominio trusted di accedere automaticamente a tutte le risorse del dominio trusting", "is_correct": false}]', '{"right_answer": {"selected_answer": "Riduce i rischi per la sicurezza eliminando l''accesso automatico tramite Authenticated Users; gli utenti devono ricevere la permission ''Allowed To Authenticate'' sugli oggetti computer", "rationale": "La slide 22 spiega che la selective authentication riduce i rischi per la sicurezza (no Authenticated Users), richiede di specificare quali utenti possono autenticarsi e di assegnare la permission ''Allowed To Authenticate'' sull''oggetto computer di Active Directory.", "original_reference": {"path": "materiale/BERNABEI - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft/Slide/12.Gestire domini e foreste.pptx", "page": 22}}, "wrong_answers": [{"answer_text": "Permette a tutti gli utenti del dominio trusted di accedere automaticamente a tutte le risorse del dominio trusting", "interpretation": "Opposto della selective authentication", "why_wrong": "Questa è la descrizione dell''accesso predefinito SENZA selective authentication. La selective authentication fa esattamente l''opposto: limita l''accesso."}, {"answer_text": "Disabilita completamente la trust e richiede che ogni utente crei un account locale nel dominio di destinazione", "interpretation": "Interpretazione estrema della selettività", "why_wrong": "La selective authentication non disabilita la trust. La mantiene attiva ma richiede una configurazione esplicita di chi può autenticarsi, tramite la permission ''Allowed To Authenticate''."}]}', '["BERNABEI_12_DominiForeste_medium_003", "BERNABEI_12_DominiForeste_hard_001"]');
INSERT INTO questions (batch_id, id, curriculum_area, lecturer, topic, clusters, cognitive_level, question_type, distractor_strategy, difficulty, source_file, source_file_page_number, topics, question_prompt, choices, explanation, related_questions) VALUES (11, 'FORONI_1_CIA_medium_001', 'MOD. 4 - Implementazione e verifica della sicurezza delle reti e dei sistemi', 'FORONI', 'Integrita'' - soggetti autorizzati', '["integrity", "integrita''", "autorizzazione", "modifica dati"]', 'apply', 'scenario', 'scenario_misattribution', 'medium', 'materiale/FORONI/Lezioni - Implementazione e verifica della sicurezza delle reti e dei sistemi/1.CIA.pdf', 5, '["integrita''", "integrity", "modifica autorizzata", "digital assets"]', 'Un dipendente modifica un file contabile aziendale utilizzando le proprie credenziali regolari, ma inserisce dati falsi per coprire un ammanco. Quale aspetto della triade CIA viene violato?', '[{"text": "Riservatezza (Confidentiality), perche'' il dipendente ha acceduto a dati che non doveva vedere", "is_correct": false}, {"text": "Integrita'' (Integrity), perche'' i dati sono stati alterati in maniera non autorizzata nel contenuto", "is_correct": true}, {"text": "Disponibilita'' (Availability), perche'' il file non e'' piu'' accessibile agli altri utenti", "is_correct": false}, {"text": "Nessuno, perche'' il dipendente ha utilizzato credenziali valide", "is_correct": false}]', '{"right_answer": {"selected_answer": "Integrita'' (Integrity), perche'' i dati sono stati alterati in maniera non autorizzata nel contenuto", "rationale": "Il materiale specifica che le informazioni e i beni digitali possono essere modificati soltanto da soggetti autorizzati e in maniera autorizzata. Anche se il dipendente aveva credenziali valide, l''inserimento di dati falsi non e'' una modifica in maniera autorizzata, violando l''integrita''.", "original_reference": {"path": "materiale/FORONI/Lezioni - Implementazione e verifica della sicurezza delle reti e dei sistemi/1.CIA.pdf", "page": 5}}, "wrong_answers": [{"answer_text": "Riservatezza (Confidentiality), perche'' il dipendente ha acceduto a dati che non doveva vedere", "interpretation": "Confonde una modifica fraudolenta con una violazione di accesso alla lettura", "why_wrong": "Lo scenario non descrive un accesso non autorizzato alle informazioni, ma una modifica fraudolenta. Il dipendente poteva accedere ai dati; il problema e'' che li ha alterati con dati falsi."}, {"answer_text": "Disponibilita'' (Availability), perche'' il file non e'' piu'' accessibile agli altri utenti", "interpretation": "Assume erroneamente che il file sia diventato inaccessibile", "why_wrong": "Lo scenario non indica che il file sia diventato inaccessibile. La disponibilita'' riguarda la capacita'' di accedere ai dati nei tempi e modi previsti."}, {"answer_text": "Nessuno, perche'' il dipendente ha utilizzato credenziali valide", "interpretation": "Confonde l''autenticazione valida con l''autorizzazione della modifica", "why_wrong": "L''integrita'' richiede che le modifiche avvengano sia da soggetti autorizzati sia in maniera autorizzata. Inserire dati falsi non e'' una modifica autorizzata, anche con credenziali valide."}]}', '["FORONI_1_CIA_easy_002"]');
INSERT INTO questions (batch_id, id, curriculum_area, lecturer, topic, clusters, cognitive_level, question_type, distractor_strategy, difficulty, source_file, source_file_page_number, topics, question_prompt, choices, explanation, related_questions) VALUES (11, 'FORONI_09_DeepWeb_medium_001', 'MOD. 4 - Implementazione e verifica della sicurezza delle reti e dei sistemi', 'FORONI', 'Funzionamento di TOR: onion routing', '["TOR", "onion routing", "relay", "crittografia", "percorso"]', 'understand', 'conceptual', 'common_confusion', 'medium', 'materiale/FORONI/Lezioni - Implementazione e verifica della sicurezza delle reti e dei sistemi/9. il_deep_web.pdf', 6, '["TOR", "onion routing", "nodi relay", "percorso casuale", "crittografia"]', 'Come funziona il routing dei dati nella rete TOR?', '[{"text": "I dati vengono inviati direttamente dal client al server di destinazione tramite un tunnel VPN dedicato", "is_correct": false}, {"text": "I dati seguono sempre lo stesso percorso predefinito attraverso una serie fissa di server certificati", "is_correct": false}, {"text": "I dati vengono frammentati e distribuiti su tutti i nodi della rete TOR contemporaneamente", "is_correct": false}, {"text": "I dati passano attraverso nodi relay in modo casuale, con percorso che cambia ogni 10 minuti, e sono crittografati tra i server", "is_correct": true}]', '{"right_answer": {"selected_answer": "I dati passano attraverso nodi relay in modo casuale, con percorso che cambia ogni 10 minuti, e sono crittografati tra i server", "rationale": "Il materiale spiega a pagina 6 che i dati ''passano in questi nodi casualmente utilizzando sempre un percorso differente'', la rete ''modifica il percorso dei dati ogni dieci minuti e automaticamente'' e ''tutti i dati scambiati tra i server sono crittografati''.", "original_reference": {"path": "materiale/FORONI/Lezioni - Implementazione e verifica della sicurezza delle reti e dei sistemi/9. il_deep_web.pdf", "page": 6}}, "wrong_answers": [{"answer_text": "I dati vengono inviati direttamente dal client al server di destinazione tramite un tunnel VPN dedicato", "interpretation": "Confonde TOR con una VPN tradizionale", "why_wrong": "TOR non usa un tunnel diretto come una VPN. I dati passano attraverso multipli nodi relay con percorso casuale, offrendo un livello di anonimato diverso dalla VPN."}, {"answer_text": "I dati vengono frammentati e distribuiti su tutti i nodi della rete TOR contemporaneamente", "interpretation": "Confonde il routing di TOR con la distribuzione di Freenet", "why_wrong": "TOR non frammenta i dati su tutti i nodi. I dati seguono un percorso specifico attraverso pochi nodi relay selezionati casualmente. La frammentazione e'' caratteristica di Freenet."}, {"answer_text": "I dati seguono sempre lo stesso percorso predefinito attraverso una serie fissa di server certificati", "interpretation": "Nega la casualita'' e la variabilita'' del percorso", "why_wrong": "Il percorso in TOR e'' casuale e cambia automaticamente ogni 10 minuti. Non ci sono percorsi fissi o server predefiniti."}]}', '[]');
INSERT INTO questions (batch_id, id, curriculum_area, lecturer, topic, clusters, cognitive_level, question_type, distractor_strategy, difficulty, source_file, source_file_page_number, topics, question_prompt, choices, explanation, related_questions) VALUES (11, 'BERNABEI_04_GestireGruppi_medium_005', 'MOD. 3 - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft', 'BERNABEI', 'Eliminazione gruppi e impatto SID', '["Gestione Gruppi", "Best Practices", "Sicurezza"]', 'understand', 'best_practice', 'plausible_alternative', 'medium', 'materiale/BERNABEI - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft/Slide/04.Gestire i gruppi.pptx', 31, '["eliminazione gruppi", "SID", "ACL", "best practice"]', 'Perche eliminare un Security group puo avere un impatto importante e qual e la best practice consigliata?', '[{"text": "Il SID viene perso e non puo essere recuperato ricreando il gruppo; si consiglia di svuotare i membri e attendere prima di eliminare", "is_correct": true}, {"text": "Le Group Policy collegate smettono di funzionare; si consiglia di scollegare le GPO prima dell''eliminazione", "is_correct": false}, {"text": "Il gruppo viene spostato nel Recycle Bin per 60 giorni; non servono precauzioni particolari", "is_correct": false}, {"text": "Tutti i membri perdono automaticamente le proprie password; si consiglia di disabilitare prima gli account", "is_correct": false}]', '{"right_answer": {"selected_answer": "Il SID viene perso e non puo essere recuperato ricreando il gruppo; si consiglia di svuotare i membri e attendere prima di eliminare", "rationale": "La slide 31 spiega che eliminando un Security group il SID viene perso e non puo essere ricreato. Il suggerimento e prendere nota dei membri, svuotare il gruppo e attendere un periodo prima di eliminarlo.", "original_reference": {"path": "materiale/BERNABEI - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft/Slide/04.Gestire i gruppi.pptx", "page": 31}}, "wrong_answers": [{"answer_text": "Tutti i membri perdono automaticamente le proprie password; si consiglia di disabilitare prima gli account", "interpretation": "Perdita password come conseguenza", "why_wrong": "L''eliminazione di un gruppo non influisce sulle password dei membri. L''impatto e la perdita del SID del gruppo, che potrebbe essere usato nelle ACL."}, {"answer_text": "Il gruppo viene spostato nel Recycle Bin per 60 giorni; non servono precauzioni particolari", "interpretation": "Recupero automatico dal Recycle Bin", "why_wrong": "Il Recycle Bin di AD (se abilitato) puo aiutare, ma il materiale sottolinea l''importanza della cautela perche il SID perso potrebbe essere nelle ACL. La best practice resta svuotare e attendere."}, {"answer_text": "Le Group Policy collegate smettono di funzionare; si consiglia di scollegare le GPO prima dell''eliminazione", "interpretation": "Impatto sulle Group Policy", "why_wrong": "Le GPO non sono collegate direttamente ai Security groups in modo che la loro eliminazione le renda non funzionanti. Il problema reale e la perdita del SID usato nelle ACL."}]}', '["BERNABEI_04_GestireGruppi_easy_001", "BERNABEI_04_GestireGruppi_hard_004"]');
INSERT INTO questions (batch_id, id, curriculum_area, lecturer, topic, clusters, cognitive_level, question_type, distractor_strategy, difficulty, source_file, source_file_page_number, topics, question_prompt, choices, explanation, related_questions) VALUES (11, 'FORONI_1_CIA_very_easy_002', 'MOD. 4 - Implementazione e verifica della sicurezza delle reti e dei sistemi', 'FORONI', 'Autenticazione - definizione base', '["autenticazione", "identificazione", "authentication"]', 'remember', 'definition', 'common_confusion', 'very_easy', 'materiale/FORONI/Lezioni - Implementazione e verifica della sicurezza delle reti e dei sistemi/1.CIA.pdf', 7, '["identificazione", "autenticazione", "authentication"]', 'In cosa consiste l''autenticazione (authentication) nel contesto della sicurezza informatica?', '[{"text": "Nel crittografare le comunicazioni tra client e server", "is_correct": false}, {"text": "Nell''assegnare permessi di lettura e scrittura ai file di sistema", "is_correct": false}, {"text": "Nel verificare che utenti o macchine siano veramente chi dichiarano di essere", "is_correct": true}]', '{"right_answer": {"selected_answer": "Nel verificare che utenti o macchine siano veramente chi dichiarano di essere", "rationale": "Il materiale definisce esplicitamente che l''autenticazione di utenti e macchine consiste nel verificare che essi siano veramente chi dichiarano di essere.", "original_reference": {"path": "materiale/FORONI/Lezioni - Implementazione e verifica della sicurezza delle reti e dei sistemi/1.CIA.pdf", "page": 7}}, "wrong_answers": [{"answer_text": "Nel crittografare le comunicazioni tra client e server", "interpretation": "Confonde l''autenticazione con la cifratura dei dati in transito", "why_wrong": "La cifratura e'' una misura di protezione della riservatezza, non un processo di autenticazione. L''autenticazione verifica l''identita'', non protegge i dati."}, {"answer_text": "Nell''assegnare permessi di lettura e scrittura ai file di sistema", "interpretation": "Confonde l''autenticazione con l''autorizzazione (controllo degli accessi)", "why_wrong": "L''assegnazione dei permessi e'' compito dell''autorizzazione (authorization), che avviene dopo l''autenticazione."}]}', '["FORONI_1_CIA_easy_004"]');
INSERT INTO questions (batch_id, id, curriculum_area, lecturer, topic, clusters, cognitive_level, question_type, distractor_strategy, difficulty, source_file, source_file_page_number, topics, question_prompt, choices, explanation, related_questions) VALUES (11, 'BERNABEI_03_GestioneUtenti_easy_005', 'MOD. 3 - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft', 'BERNABEI', 'Attribute Editor - Advanced Features', '["Active Directory", "Gestione Utenti"]', 'remember', 'concept', 'plausible_alternative', 'easy', 'materiale/BERNABEI - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft/Slide/03. Gestione utenti.pptx', 17, '["Attribute Editor", "Advanced Features", "AD Users and Computers"]', 'Come si abilita il tab Attribute Editor in Active Directory Users and Computers?', '[{"text": "Installando un aggiornamento aggiuntivo di Windows Server", "is_correct": false}, {"text": "Selezionando Advanced Features dal menu View", "is_correct": true}, {"text": "Modificando manualmente il registro di sistema (regedit)", "is_correct": false}, {"text": "Riavviando il servizio Active Directory Domain Services", "is_correct": false}]', '{"right_answer": {"selected_answer": "Selezionando Advanced Features dal menu View", "rationale": "Il materiale specifica che per visualizzare il tab Attribute Editor bisogna andare in Active Directory Users and Computers, selezionare il menu View e quindi spuntare Advanced Features.", "original_reference": {"path": "materiale/BERNABEI - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft/Slide/03. Gestione utenti.pptx", "page": 17}}, "wrong_answers": [{"answer_text": "Installando un aggiornamento aggiuntivo di Windows Server", "interpretation": "Aggiornamento di sistema", "why_wrong": "Non è necessario installare alcun aggiornamento. L''Attribute Editor è già disponibile attivando Advanced Features dal menu View."}, {"answer_text": "Modificando manualmente il registro di sistema (regedit)", "interpretation": "Modifica del registry", "why_wrong": "Non è necessario modificare il registro di sistema. Basta attivare l''opzione Advanced Features dal menu View nella console ADUC."}, {"answer_text": "Riavviando il servizio Active Directory Domain Services", "interpretation": "Riavvio servizio AD DS", "why_wrong": "Il riavvio del servizio AD DS non è correlato alla visualizzazione dell''Attribute Editor. Si tratta di un''opzione di visualizzazione della console."}]}', '["BERNABEI_03_GestioneUtenti_medium_004", "BERNABEI_03_GestioneUtenti_easy_004"]');
INSERT INTO questions (batch_id, id, curriculum_area, lecturer, topic, clusters, cognitive_level, question_type, distractor_strategy, difficulty, source_file, source_file_page_number, topics, question_prompt, choices, explanation, related_questions) VALUES (11, 'ANZALONE_2a_introVmware_medium_002', 'MOD. 5 - Cloud privato e cloud pubblico al servizio della business continuity e disaster recovery', 'ANZALONE', 'Storage architecture - shared storage', '["Infrastruttura VMware vSphere", "Storage"]', 'understand', 'concept', 'common_misconception', 'medium', 'materiale/ANZALONE - Cloud privato e cloud pubblico al servizio della business continuity e disaster recovery/2a_introVmware.pdf', 19, '["shared storage", "vMotion", "DRS", "HA", "FT", "Fibre Channel", "iSCSI", "NFS"]', 'Perché l''architettura di shared storage è considerata critica per un deployment vSphere avanzato?', '[{"text": "Perché funzionalità come vMotion, DRS, HA e FT dipendono dalla presenza di storage condiviso", "is_correct": true}, {"text": "Perché lo shared storage è necessario solo per archiviare i log di vCenter Server", "is_correct": false}, {"text": "Perché senza shared storage non è possibile installare ESXi sugli host", "is_correct": false}, {"text": "Perché lo shared storage rende inutile la ridondanza delle NIC sugli host", "is_correct": false}]', '{"right_answer": {"selected_answer": "Perché funzionalità come vMotion, DRS, HA e FT dipendono dalla presenza di storage condiviso", "rationale": "Il materiale indica che la maggior parte delle funzionalità avanzate di vSphere come vMotion, DRS, HA e FT dipendono dalla presenza di un''architettura di shared storage, rendendola una decisione critica quanto la scelta dell''hardware server.", "original_reference": {"path": "materiale/ANZALONE - Cloud privato e cloud pubblico al servizio della business continuity e disaster recovery/2a_introVmware.pdf", "page": 19}}, "wrong_answers": [{"answer_text": "Perché senza shared storage non è possibile installare ESXi sugli host", "interpretation": "Prerequisito per l''installazione ESXi", "why_wrong": "ESXi può essere installato su storage locale del server o anche su dispositivi USB/SD card interni. Lo shared storage non è un requisito per l''installazione di ESXi."}, {"answer_text": "Perché lo shared storage è necessario solo per archiviare i log di vCenter Server", "interpretation": "Utilizzo limitato ai log", "why_wrong": "Lo shared storage non serve solo per i log. È essenziale per ospitare i file delle VM in modo che siano accessibili da più host, abilitando vMotion, HA, DRS e FT."}, {"answer_text": "Perché lo shared storage rende inutile la ridondanza delle NIC sugli host", "interpretation": "Eliminazione necessità NIC ridondanti", "why_wrong": "Lo shared storage non elimina la necessità di NIC ridondanti. Anzi, se si usa iSCSI o NFS, servono NIC aggiuntive per il traffico storage oltre a quelle per management e vMotion."}]}', '["ANZALONE_2a_introVmware_medium_003"]');
INSERT INTO questions (batch_id, id, curriculum_area, lecturer, topic, clusters, cognitive_level, question_type, distractor_strategy, difficulty, source_file, source_file_page_number, topics, question_prompt, choices, explanation, related_questions) VALUES (11, 'BERNABEI_06_GroupPolicy_very_easy_003', 'MOD. 3 - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft', 'BERNABEI', 'GPO di dominio predefinite', '["Group Policy", "Active Directory"]', 'remember', 'definition', 'partial_knowledge', 'very_easy', 'materiale/BERNABEI - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft/Slide/06.Implementare le Group Policy.pptx', 14, '["Default Domain Policy", "Default Domain Controllers Policy", "GPO predefinite"]', 'Quale GPO predefinita definisce le policy per password, account lockout e Kerberos in un dominio AD?', '[{"text": "Default Domain Policy", "is_correct": true}, {"text": "Default Domain Controllers Policy", "is_correct": false}, {"text": "Local Group Policy", "is_correct": false}]', '{"right_answer": {"selected_answer": "Default Domain Policy", "rationale": "La slide 14 specifica che la Default Domain Policy definisce le policies per gli account di AD: Password, account lockout, e policy di Kerberos.", "original_reference": {"path": "materiale/BERNABEI - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft/Slide/06.Implementare le Group Policy.pptx", "page": 14}}, "wrong_answers": [{"answer_text": "Default Domain Controllers Policy", "interpretation": "Confusione tra le due GPO predefinite", "why_wrong": "La Default Domain Controllers Policy definisce policy di auditing per i domain controllers e per Active Directory, non le policy di password e lockout."}, {"answer_text": "Local Group Policy", "interpretation": "La GPO locale come policy di dominio", "why_wrong": "Le Local Group Policy sono configurate sul singolo computer tramite gpedit.msc e non gestiscono centralmente le policy di password del dominio."}]}', '["BERNABEI_06_GroupPolicy_easy_004", "BERNABEI_06_GroupPolicy_medium_001"]');
INSERT INTO questions (batch_id, id, curriculum_area, lecturer, topic, clusters, cognitive_level, question_type, distractor_strategy, difficulty, source_file, source_file_page_number, topics, question_prompt, choices, explanation, related_questions) VALUES (11, 'BERNABEI_03_GestioneUtenti_medium_002', 'MOD. 3 - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft', 'BERNABEI', 'Eliminazione account e SID', '["Active Directory", "Gestione Utenti", "Sicurezza Account"]', 'understand', 'concept', 'plausible_alternative', 'medium', 'materiale/BERNABEI - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft/Slide/03. Gestione utenti.pptx', 15, '["eliminazione account", "SID", "group membership", "dsrm", "Remove-ADUser"]', 'Cosa si perde in modo irreversibile quando viene eliminato un account utente da Active Directory?', '[{"text": "Il SID e le group membership dell''account", "is_correct": true}, {"text": "Nulla, perché è possibile ripristinare completamente l''account con lo stesso SID", "is_correct": false}, {"text": "Solo la password dell''account, recuperabile dal backup", "is_correct": false}, {"text": "Unicamente il display name, mentre il SID viene mantenuto", "is_correct": false}]', '{"right_answer": {"selected_answer": "Il SID e le group membership dell''account", "rationale": "Il materiale indica esplicitamente che eliminando un utente verranno perse le memberships ai gruppi e il security identifier (SID). Il SID è univoco e non viene riassegnato, quindi un nuovo account con lo stesso nome non avrà lo stesso SID né gli stessi permessi.", "original_reference": {"path": "materiale/BERNABEI - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft/Slide/03. Gestione utenti.pptx", "page": 15}}, "wrong_answers": [{"answer_text": "Solo la password dell''account, recuperabile dal backup", "interpretation": "Perdita solo della password", "why_wrong": "Non si perde solo la password. L''intera identità dell''account, inclusi SID e appartenenze ai gruppi, viene eliminata in modo irreversibile."}, {"answer_text": "Unicamente il display name, mentre il SID viene mantenuto", "interpretation": "Conservazione del SID", "why_wrong": "Il SID non viene mantenuto: viene perso definitivamente insieme all''account. Anche ricreando un account con lo stesso nome, avrà un SID diverso."}, {"answer_text": "Nulla, perché è possibile ripristinare completamente l''account con lo stesso SID", "interpretation": "Ripristino completo", "why_wrong": "Non è possibile ripristinare un account eliminato con lo stesso SID attraverso una semplice ricreazione. Il SID è univoco e la sua perdita è irreversibile (salvo restore da backup AD)."}]}', '["BERNABEI_03_GestioneUtenti_very_easy_002", "BERNABEI_03_GestioneUtenti_hard_002"]');
INSERT INTO questions (batch_id, id, curriculum_area, lecturer, topic, clusters, cognitive_level, question_type, distractor_strategy, difficulty, source_file, source_file_page_number, topics, question_prompt, choices, explanation, related_questions) VALUES (11, 'FORONI_3_Crittografia_very_easy_001', 'MOD. 4 - Implementazione e verifica della sicurezza delle reti e dei sistemi', 'FORONI', 'Problemi di sicurezza nelle comunicazioni', '["riservatezza", "integrita''", "autenticazione", "non ripudiabilita''"]', 'remember', 'definition', 'related_concept', 'very_easy', 'materiale/FORONI/Lezioni - Implementazione e verifica della sicurezza delle reti e dei sistemi/3.CRITTOGRAFIA.pdf', 3, '["riservatezza", "integrita''", "autenticazione", "non ripudiabilita''"]', 'Quale dei seguenti NON e'' uno dei quattro problemi di sicurezza nelle comunicazioni su Internet citati nel corso?', '[{"text": "Non ripudiabilita'' (non-repudiation)", "is_correct": false}, {"text": "Integrita'' (integrity)", "is_correct": false}, {"text": "Disponibilita'' (availability) del servizio", "is_correct": true}, {"text": "Riservatezza (confidentiality)", "is_correct": false}]', '{"right_answer": {"selected_answer": "Disponibilita'' (availability) del servizio", "rationale": "I quattro problemi citati nella lezione sono: riservatezza, integrita'', autenticazione e non ripudiabilita''. La disponibilita'' (availability) e'' un concetto importante nella sicurezza informatica (CIA triad), ma non e'' tra i quattro problemi elencati in questo contesto.", "original_reference": {"path": "materiale/FORONI/Lezioni - Implementazione e verifica della sicurezza delle reti e dei sistemi/3.CRITTOGRAFIA.pdf", "page": 3}}, "wrong_answers": [{"answer_text": "Riservatezza (confidentiality)", "interpretation": "Lo studente potrebbe pensare che la riservatezza non sia elencata", "why_wrong": "La riservatezza e'' esplicitamente citata come primo problema: ''messaggio inviato non intercettabile o comunque non violabile''."}, {"answer_text": "Integrita'' (integrity)", "interpretation": "Lo studente potrebbe confondere i problemi elencati", "why_wrong": "L''integrita'' e'' il secondo problema citato: ''messaggio inviato e ricevuto coincidono''."}, {"answer_text": "Non ripudiabilita'' (non-repudiation)", "interpretation": "Lo studente potrebbe non ricordare questo termine", "why_wrong": "La non ripudiabilita'' e'' il quarto problema: ''il mittente non puo'' negare di aver inviato il messaggio''."}]}', '["FORONI_3_Crittografia_easy_001"]');
INSERT INTO questions (batch_id, id, curriculum_area, lecturer, topic, clusters, cognitive_level, question_type, distractor_strategy, difficulty, source_file, source_file_page_number, topics, question_prompt, choices, explanation, related_questions) VALUES (11, 'BERNABEI_01_IntrodAD_medium_001', 'MOD. 3 - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft', 'BERNABEI', 'Processo di autorizzazione', '["Sicurezza e controllo accessi", "Autenticazione e autorizzazione"]', 'apply', 'scenario', 'common_misconception', 'medium', 'materiale/BERNABEI - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft/Slide/01.Introduzione ad Active Directory DS.pptx', 13, '["autorizzazione", "Access Token", "ACL", "ACE", "Security Descriptor"]', 'Un utente tenta di accedere a una cartella condivisa. Quali tre componenti servono al processo di autorizzazione?', '[{"text": "Username, password e indirizzo IP del client", "is_correct": false}, {"text": "Nome del dominio, SID del Domain Controller e SYSVOL path", "is_correct": false}, {"text": "Certificato digitale, chiave privata e timestamp Kerberos", "is_correct": false}, {"text": "Risorsa, Access Token dell''utente e richiesta di accesso", "is_correct": true}]', '{"right_answer": {"selected_answer": "Risorsa, Access Token dell''utente e richiesta di accesso", "rationale": "La slide 13 elenca esplicitamente i tre componenti necessari al processo di autorizzazione: la risorsa, l''Access Token e la richiesta di accesso. Il sistema confronta i SID nell''Access Token con le ACE nella DACL della risorsa.", "original_reference": {"path": "materiale/BERNABEI - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft/Slide/01.Introduzione ad Active Directory DS.pptx", "page": 13}}, "wrong_answers": [{"answer_text": "Username, password e indirizzo IP del client", "interpretation": "Confusione tra autenticazione e autorizzazione", "why_wrong": "Username e password fanno parte del processo di autenticazione, non di autorizzazione. L''autorizzazione avviene dopo, con l''Access Token gia rilasciato."}, {"answer_text": "Certificato digitale, chiave privata e timestamp Kerberos", "interpretation": "Confusione con elementi del protocollo Kerberos/PKI", "why_wrong": "Questi sono elementi di autenticazione e crittografia, non i tre componenti dell''autorizzazione descritti nel materiale."}, {"answer_text": "Nome del dominio, SID del Domain Controller e SYSVOL path", "interpretation": "Confusione tra componenti infrastrutturali e di autorizzazione", "why_wrong": "Questi sono elementi infrastrutturali del dominio, non i componenti del processo di autorizzazione sull''accesso alle risorse."}]}', '["BERNABEI_01_IntrodAD_easy_005", "BERNABEI_01_IntrodAD_easy_002"]');
INSERT INTO questions (batch_id, id, curriculum_area, lecturer, topic, clusters, cognitive_level, question_type, distractor_strategy, difficulty, source_file, source_file_page_number, topics, question_prompt, choices, explanation, related_questions) VALUES (11, 'ANZALONE_4a_storage_medium_004', 'MOD. 5 - Cloud privato e cloud pubblico al servizio della business continuity e disaster recovery', 'ANZALONE', 'Snapshot VMware - limitazioni', '["Storage", "VMware vSphere", "Protezione dati"]', 'apply', 'best_practice', 'common_misconception', 'medium', 'materiale/ANZALONE - Cloud privato e cloud pubblico al servizio della business continuity e disaster recovery/4a_storage.pdf', 74, '["snapshot", "VMware", "backup", "delta disk", "best practice"]', 'Perche'' gli snapshot VMware non dovrebbero essere utilizzati come sostituti dei backup?', '[{"text": "Perche'' possono essere creati solo quando la VM e'' spenta e non in produzione", "is_correct": false}, {"text": "Perche'' ogni snapshot occupa sempre il doppio dello spazio del disco originale", "is_correct": false}, {"text": "Perche'' non sono copie complete: registrano solo le modifiche incrementali su delta disk", "is_correct": true}, {"text": "Perche'' sono compatibili esclusivamente con datastore in formato NFS", "is_correct": false}]', '{"right_answer": {"selected_answer": "Perche'' non sono copie complete: registrano solo le modifiche incrementali su delta disk", "rationale": "Uno snapshot VMware non e'' una copia completa della VM. Crea un delta disk che registra solo le modifiche rispetto allo stato al momento dello snapshot, consentendo un consumo minimo di spazio. Non offre la protezione di un backup completo e non dovrebbe mai sostituirlo.", "original_reference": {"path": "materiale/ANZALONE - Cloud privato e cloud pubblico al servizio della business continuity e disaster recovery/4a_storage.pdf", "page": 74}}, "wrong_answers": [{"answer_text": "Perche'' possono essere creati solo quando la VM e'' spenta e non in produzione", "interpretation": "Suggerisce che gli snapshot richiedano la VM spenta", "why_wrong": "Gli snapshot possono essere creati quando la VM e'' accesa, spenta o sospesa. Non richiedono lo spegnimento."}, {"answer_text": "Perche'' sono compatibili esclusivamente con datastore in formato NFS", "interpretation": "Limita gli snapshot a un solo tipo di datastore", "why_wrong": "Gli snapshot funzionano su tutti i tipi di datastore supportati da VMware (VMFS, NFS), non solo su NFS."}, {"answer_text": "Perche'' ogni snapshot occupa sempre il doppio dello spazio del disco originale", "interpretation": "Esagera il consumo di spazio degli snapshot", "why_wrong": "Gli snapshot NON duplicano tutto lo spazio. Allocano spazio solo per registrare le modifiche (delta), consumando molto meno del doppio del disco originale."}]}', '["ANZALONE_4a_storage_easy_002"]');
INSERT INTO questions (batch_id, id, curriculum_area, lecturer, topic, clusters, cognitive_level, question_type, distractor_strategy, difficulty, source_file, source_file_page_number, topics, question_prompt, choices, explanation, related_questions) VALUES (11, 'ANZALONE_2a_introVmware_very_easy_003', 'MOD. 5 - Cloud privato e cloud pubblico al servizio della business continuity e disaster recovery', 'ANZALONE', 'vCenter Server - ruolo base', '["Infrastruttura VMware vSphere"]', 'remember', 'definition', 'obviously_wrong', 'very_easy', 'materiale/ANZALONE - Cloud privato e cloud pubblico al servizio della business continuity e disaster recovery/2a_introVmware.pdf', 29, '["vCenter Server", "gestione centralizzata"]', 'Qual è il ruolo principale di VMware vCenter Server nell''infrastruttura vSphere?', '[{"text": "È un sistema di videosorveglianza per il datacenter", "is_correct": false}, {"text": "È uno strumento di gestione centralizzata per host ESXi e macchine virtuali", "is_correct": true}, {"text": "È un firewall hardware dedicato alla protezione perimetrale", "is_correct": false}, {"text": "È un editor di testo per scrivere codice sorgente", "is_correct": false}]', '{"right_answer": {"selected_answer": "È uno strumento di gestione centralizzata per host ESXi e macchine virtuali", "rationale": "VMware vCenter Server è lo strumento di gestione centralizzata della suite vSphere che consente l''amministrazione di più server ESXi e macchine virtuali da un''unica console.", "original_reference": {"path": "materiale/ANZALONE - Cloud privato e cloud pubblico al servizio della business continuity e disaster recovery/2a_introVmware.pdf", "page": 29}}, "wrong_answers": [{"answer_text": "È un editor di testo per scrivere codice sorgente", "interpretation": "Ambiente di sviluppo", "why_wrong": "vCenter Server non è un editor di testo. È un tool di amministrazione centralizzata dell''infrastruttura di virtualizzazione."}, {"answer_text": "È un firewall hardware dedicato alla protezione perimetrale", "interpretation": "Dispositivo di sicurezza di rete", "why_wrong": "vCenter Server non è un firewall. È il componente di gestione centralizzata dell''infrastruttura VMware vSphere."}, {"answer_text": "È un sistema di videosorveglianza per il datacenter", "interpretation": "Sistema di sorveglianza fisica", "why_wrong": "vCenter Server non ha alcuna funzione di videosorveglianza. È un software di gestione per l''infrastruttura virtuale."}]}', '["ANZALONE_2a_introVmware_easy_005"]');
INSERT INTO questions (batch_id, id, curriculum_area, lecturer, topic, clusters, cognitive_level, question_type, distractor_strategy, difficulty, source_file, source_file_page_number, topics, question_prompt, choices, explanation, related_questions) VALUES (11, 'BERNABEI_05_AccountComputer_medium_002', 'MOD. 3 - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft', 'BERNABEI', 'Secure Channel tra computer e DC', '["Account Computer", "Secure Channel", "Sicurezza"]', 'understand', 'concept', 'plausible_alternative', 'medium', 'materiale/BERNABEI - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft/Slide/05.Gestire gli account computer.pptx', 17, '["secure channel", "sAMAccountName", "password", "SID", "reinstallazione"]', 'Quale evento causa la corruzione del Secure Channel tra un computer e il Domain Controller?', '[{"text": "La reinstallazione del sistema operativo del computer, che genera un nuovo SID", "is_correct": true}, {"text": "La modifica del nome utente dell''amministratore locale del computer", "is_correct": false}, {"text": "L''aggiornamento del sistema operativo tramite Windows Update", "is_correct": false}, {"text": "Lo spostamento dell''account computer in una OU differente", "is_correct": false}]', '{"right_answer": {"selected_answer": "La reinstallazione del sistema operativo del computer, che genera un nuovo SID", "rationale": "La slide 17 indica che la reinstallazione del computer genera inevitabilmente un nuovo SID, causando la corruzione del Secure Channel poiche le credenziali (sAMAccountName e password) non corrispondono piu tra computer e DC.", "original_reference": {"path": "materiale/BERNABEI - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft/Slide/05.Gestire gli account computer.pptx", "page": 17}}, "wrong_answers": [{"answer_text": "La modifica del nome utente dell''amministratore locale del computer", "interpretation": "Rinomina dell''admin locale come causa di corruzione", "why_wrong": "La modifica del nome dell''amministratore locale non influisce sul Secure Channel, che e basato su sAMAccountName e password dell''account computer in AD."}, {"answer_text": "L''aggiornamento del sistema operativo tramite Windows Update", "interpretation": "Windows Update come causa di corruzione", "why_wrong": "Gli aggiornamenti tramite Windows Update non modificano il SID ne la password dell''account computer e non corrompono il Secure Channel."}, {"answer_text": "Lo spostamento dell''account computer in una OU differente", "interpretation": "Spostamento OU come causa di corruzione", "why_wrong": "Lo spostamento di un account computer tra OU non modifica il SID ne le credenziali dell''account, quindi non corrompe il Secure Channel."}]}', '["BERNABEI_05_AccountComputer_medium_003", "BERNABEI_05_AccountComputer_hard_002"]');
INSERT INTO questions (batch_id, id, curriculum_area, lecturer, topic, clusters, cognitive_level, question_type, distractor_strategy, difficulty, source_file, source_file_page_number, topics, question_prompt, choices, explanation, related_questions) VALUES (11, 'BERNABEI_03_GestioneUtenti_easy_003', 'MOD. 3 - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft', 'BERNABEI', 'Reset password', '["Active Directory", "Gestione Utenti", "Sicurezza Account"]', 'remember', 'concept', 'plausible_alternative', 'easy', 'materiale/BERNABEI - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft/Slide/03. Gestione utenti.pptx', 12, '["reset password", "Set-ADAccountPassword", "dsmod"]', 'Quando si esegue un reset password in AD, quale opzione è consigliato selezionare insieme alla nuova password temporanea?', '[{"text": "Store password using reversible encryption", "is_correct": false}, {"text": "User must change password at next logon", "is_correct": true}, {"text": "Account is disabled", "is_correct": false}, {"text": "Password never expires", "is_correct": false}]', '{"right_answer": {"selected_answer": "User must change password at next logon", "rationale": "Il materiale indica di assegnare una password complessa temporanea, comunicarla all''utente e selezionare ''User must change password at next logon'' affinché l''utente la cambi al primo accesso successivo.", "original_reference": {"path": "materiale/BERNABEI - Sicurezza dei sistemi e delle infrastrutture basati su tecnologie Microsoft/Slide/03. Gestione utenti.pptx", "page": 12}}, "wrong_answers": [{"answer_text": "Password never expires", "interpretation": "Password senza scadenza", "why_wrong": "L''opzione ''Password never expires'' non forza il cambio password ed è una configurazione di policy, non una procedura di reset password sicuro."}, {"answer_text": "Account is disabled", "interpretation": "Disabilitazione account", "why_wrong": "Disabilitare l''account durante un reset password impedirebbe all''utente di accedere, vanificando lo scopo del reset."}, {"answer_text": "Store password using reversible encryption", "interpretation": "Crittografia reversibile", "why_wrong": "Questa opzione è una configurazione di sicurezza (generalmente sconsigliata) e non è correlata alla procedura di reset password."}]}', '["BERNABEI_03_GestioneUtenti_easy_004", "BERNABEI_03_GestioneUtenti_medium_003"]');
INSERT INTO questions (batch_id, id, curriculum_area, lecturer, topic, clusters, cognitive_level, question_type, distractor_strategy, difficulty, source_file, source_file_page_number, topics, question_prompt, choices, explanation, related_questions) VALUES (11, 'FORONI_4_CertificatiPKI_medium_005', 'MOD. 4 - Implementazione e verifica della sicurezza delle reti e dei sistemi', 'FORONI', 'Firma digitale nella legislazione italiana', '["firma digitale", "DPR 513/97", "legislazione italiana", "chiavi asimmetriche"]', 'remember', 'factual', 'common_confusion', 'medium', 'materiale/FORONI/Lezioni - Implementazione e verifica della sicurezza delle reti e dei sistemi/4Certificati e PKI_2017.pdf', 48, '["firma digitale", "DPR 513/97", "validazione", "chiavi asimmetriche"]', 'Secondo il DPR 513/97, come viene definita la ''firma digitale''?', '[{"text": "Una scansione della firma autografa dell''utente allegata al documento informatico", "is_correct": false}, {"text": "Il risultato della procedura informatica (validazione) basata su un sistema di chiavi asimmetriche a coppia, una pubblica e una privata", "is_correct": true}, {"text": "Un codice numerico generato da un token hardware e valido per 30 secondi", "is_correct": false}, {"text": "Un certificato rilasciato dalla pubblica amministrazione che sostituisce la carta di identita''", "is_correct": false}]', '{"right_answer": {"selected_answer": "Il risultato della procedura informatica (validazione) basata su un sistema di chiavi asimmetriche a coppia, una pubblica e una privata", "rationale": "La slide 48 riporta la definizione esatta dal DPR 513/97, Art. 1: ''firma digitale: risultato della procedura informatica (validazione) basata su un sistema di chiavi asimmetriche a coppia, una pubblica e una privata, che consente al sottoscrittore tramite la chiave privata e al destinatario tramite la chiave pubblica, rispettivamente, di rendere manifesta e di verificare la provenienza e l''integrita'' di un documento informatico.''", "original_reference": {"path": "materiale/FORONI/Lezioni - Implementazione e verifica della sicurezza delle reti e dei sistemi/4Certificati e PKI_2017.pdf", "page": 48}}, "wrong_answers": [{"answer_text": "Una scansione della firma autografa dell''utente allegata al documento informatico", "interpretation": "Confonde la firma digitale con la digitalizzazione della firma autografa", "why_wrong": "La firma digitale NON e'' una scansione della firma. E'' una procedura crittografica basata su chiavi asimmetriche, completamente diversa dalla digitalizzazione di una firma scritta."}, {"answer_text": "Un codice numerico generato da un token hardware e valido per 30 secondi", "interpretation": "Confonde la firma digitale con un OTP (One-Time Password)", "why_wrong": "Questa descrizione corrisponde a un OTP (autenticazione a due fattori), non alla firma digitale che e'' basata su crittografia a chiave pubblica."}, {"answer_text": "Un certificato rilasciato dalla pubblica amministrazione che sostituisce la carta di identita''", "interpretation": "Confonde la firma digitale con un certificato o con l''identita'' digitale", "why_wrong": "La firma digitale non e'' un certificato. Il certificato lega un''identita'' a una chiave pubblica; la firma digitale e'' il risultato dell''applicazione della chiave privata a un documento."}]}', '["FORONI_4_CertificatiPKI_hard_004"]');
